/* ==UserStyle==
@name         WhatsApp by Mew
@description  A customizable theme for WhatsApp.
@namespace    github.com/vednoc/onyx
@homepageURL  https://github.com/vednoc/onyx
@supportURL   https://github.com/vednoc/onyx/issues
@updateURL    https://rawgit.com/vednoc/onyx/master/WhatsApp.user.css
@version      1.3.0
@author       vednoc
@license      MIT
@preprocessor uso

@var color dark "Dark" #3c3836
@var color darker "Darker" #32302f
@var color light "Light" #ebdbb2
@var color lighter "Lighter" #fbf1c7
@var color accent "Accent" #d65d0e
@var color icon "Icons" #ebdbb2
@var color shadow "Shadow" rgba(0, 0, 0, .25)
@var color mred "Red-ish" #fb4934
@var color mgreen "Green-ish" #b8bb26
@var color mblue "Blue-ish" #83a598
@var text emojiOpacity "Emoji opacity" 0.75
@var text customFont "Custom font" "CaviarDreams"
@var text customImage "Custom image" url(https://bing.biturl.top/?format=image&mkt=en-US)
@var text customRules "Custom CSS rules" #test::after { content: "paste all your custom css here!" !important; }
@var text customCompact "Compact mode breakpoint" 720px

@advanced dropdown background "App background" {
  bg1 "Default theme image" <<<EOT
  ._1FKgS::after {
    top: 0px; right: 0px;
    bottom: 0px; left: 0px;
    height: auto; width: auto;
    background-color: var(--dark) !important;
    background-image: var(--image) !important;
    background-repeat: no-repeat;
    background-size: cover;
    background-position: center; } EOT;
  bg2 "Your custom image" <<<EOT
  ._1FKgS::after {
    top: 0px; right: 0px;
    bottom: 0px; left: 0px;
    height: auto; width: auto;
    background-color: var(--dark) !important;
    background-image: /*[[customImage]]*\/ !important;
    background-repeat: no-repeat;
    background-size: cover;
    background-position: center; } EOT;
  bg3 "Default WA image" <<<EOT
  ._1FKgS::after {
    opacity: var(--emojiOpacity) !important;
    background-color: var(--accent) !important; } EOT;
  bg4 "No image" <<<EOT
  ._1FKgS::after {
    display: none !important; } EOT;
}

@advanced dropdown doodles "Chat doodles" {
  doodle1 "Light doodles" <<<EOT
  .app .YUoyu {
    filter: invert(100%) opacity(0.45) !important; } EOT;
  doodle2 "Dark doodles" <<<EOT
  .app .YUoyu {
    filter: opacity(0.5) !important; } EOT;
  doodle3 "Disable doodles" <<<EOT
  .app .YUoyu {
    display: none !important; } EOT;
}

@advanced dropdown blurContacts "Blur contacts" {
  disable "Disable" <<<EOT
  /* Disabled. *\/
  EOT;
  enable "Enable" <<<EOT
  /* Blurry mode for contacts. *\/
  ._1NrpZ ._2EXPL {
    filter: blur(10px); }
  /* On event. *\/
  ._1NrpZ ._2EXPL._1f1zm,
  ._1NrpZ ._2EXPL:hover {
    filter: blur(0px); } EOT;
}

@advanced dropdown blurImages "Blur images" {
  disable "Disable" <<<EOT
  /* Disabled. *\/
  EOT;
  enable "Enable" <<<EOT
  /* Blurry mode for images. *\/
  #main ._1JVSX,
  ._2Ry6_ ._1iCCh {
    transition: 500ms ease-in-out;
    transition-delay: 0ms;
    filter: blur(10px) grayscale(100%); }
  /* On event. *\/
  #main ._1JVSX:hover,
  ._2Ry6_ ._1iCCh:hover {
    transition-delay: 500ms;
    filter: blur(0px) grayscale(0%); } EOT;
}

@advanced dropdown fullscreen "Fullscreen" {
  disable "Disable" <<<EOT
  /* Disabled. *\/
  EOT;
  enable "Enable" <<<EOT
  /* Fullscreen. *\/
  .app-wrapper-web ._3dqpi, body ._1abwg {
    height: calc(100% - 0px);
    top: 0px;
    width: 100%; }
  /* Outline and round edges. *\/
  #app .app {
    box-shadow: none !important;
    border-radius: 0px; }
  /* Disable background image. *\/
  body > div > div::after {
    display: none !important; } EOT;
}

@advanced dropdown tails "Message tails" {
  enable "Enable" <<<EOT
  /* Enabled. *\/
  EOT;
  disable "Disable" <<<EOT
  .tail-container {
    display: none !important; }
  ._3_7SH.tail {
    border-radius: 7.5px !important; } EOT;
}

@advanced dropdown compactMode "Compact mode for small resolutions" {
  enable "Enable" <<<EOT
  @media screen and (max-height: 500px) {
    ._3dqpi {
      min-height: auto; }
  }
  @media screen and (max-width:/*[[customCompact]]*\/) {
    /* .app > div { box-shadow: inset 0 0 0 4px var(--accent); } *\/

    /* Header fix. *\/
    ._3auIg {
      min-width: 220px; }

    /* Contacts flexbox. *\/
    .app.two > .k1feT,
    .three .k1feT {
      transition: 250ms ease-in-out;
      flex: 0 0 075px !important; }

    /* Chat section. *\/
    ._1Iexl {
      flex: 0 0 65% !important; }

    /* On hover or active sub-menus. *\/
    .app.two > .k1feT:hover,
    .MZIyP .k1feT {
      transition-delay: 1s !important;
      /* TODO: Adjust panel width. *\/
      flex: 0 0 30% !important; }

    /* Info flexbox. *\/
    .three ._2yeJ5 {
      width: calc(100% - 75px) !important;
      left: 75px !important; }

    /* Chat flexbox. *\/
    .two > ._1Iexl {
      margin: 0 !important;
      flex: 0 0 calc(100% - 75px) !important; }

    /* Search contacts. *\/
    .two .k1feT:not(:hover) ._2MSJr {
      padding: 0; }

    /* On hover. *\/
    .two .k1feT:hover ._2MSJr {
      transition-delay: 1s !important; }

    /** Hack for positioning unread counter. *\/
    #side:not(:hover) ._3j7s9 ._1AwDx ._3Bxar span > div {
      z-index: 101;
      position: absolute; }
    /* ? Unread counter. *\/
    #side:not(:hover) div > .OUeyt {
      height: 18px;
      padding: 0 5px 0 5px;
      margin: -5px 0 0 -35px;
      background-color: var(--dark) !important;
      box-shadow: 0 0 0 3px var(--darker) !important; }
    /* On event. *\/
    /*#side:hover div > .OUeyt {
      background-color: var(--dark) !important;
      box-shadow: 0 0 0 3px var(--darker) !important; }*\/

    /* ? Message status. *\/
    .two .k1feT ._3j7s9 ._1VfKB {
      z-index: 101;
      position: absolute;
      margin-left: -68px;
      margin-top: 5px;
      border-radius: 50%;
      background-color: var(--dark);
      box-shadow: 0 0 0 3px var(--darker); }
    /* Background on event. *\/
    .two .k1feT ._2EXPL:hover ._1VfKB,
    .two .k1feT ._2EXPL._1f1zm ._1VfKB {
      background-color: var(--darker);
      box-shadow: 0 0 0 3px var(--dark) !important; }

    /** Icon size. *\/
    .two .k1feT ._3j7s9 ._1VfKB span > svg {
      padding: 2px;
      height: 14px !important;
      width: 14px !important; }

    /* Hide all icons except new messages. *\/
    .two .k1feT ._15G96 span:not(.OUeyt),
    .two .k1feT .ZR5SB {
      display: none !important; }
  }
  @media screen and (max-width: 720px) {
    /* Width overflow fix. *\/
    ._3dqpi {
      min-width: 100%; }

    /* On hover or active sub-menus. *\/
    .app.two > .k1feT:hover,
    .MZIyP .k1feT {
      transition-delay: 1s !important;
      flex: 0 0 40% !important; }
  } EOT;
  disable "Disable" <<<EOT
  /* Disabled. *\/
  EOT;
}
==/UserStyle== */


@-moz-document domain("web.whatsapp.com") {
  :root {
    --dark: /*[[dark]]*/;
    --darker: /*[[darker]]*/;
    --light: /*[[light]]*/;
    --lighter: /*[[lighter]]*/;
    --accent: /*[[accent]]*/;
    --icon: /*[[icon]]*/;
    --shadow: /*[[shadow]]*/;
    --mred: /*[[mred]]*/;
    --mgreen: /*[[mgreen]]*/;
    --mblue: /*[[mblue]]*/;
    --image: url(https://media.discordapp.net/attachments/310799529161129984/417408747812552715/zilesanamin.jpg);
    --emojiOpacity: /*[[emojiOpacity]]*/;
    --version: "WhatsApp by Mew v1.3.0";
    --message: "Updated on 20th of February. ";
    --changes: "See the changelog at: https://github.com/vednoc/onyx/releases";
  }

  /* Body reset. */
  body {
    background: none;
    background-color: var(--dark); }

  /* Scrollbar track. */
  *::-webkit-scrollbar-track {
    background-color: var(--shadow); }
  /* Scrollbar thumb. */
  *::-webkit-scrollbar-thumb {
    background-color: var(--accent); }
  * {
    scrollbar-width: thin;
    scrollbar-color: var(--accent) var(--shadow); }

  /* Highlight selection. */
  ::selection {
    color: var(--light) !important;
    background-color: var(--accent) !important; }
  ::-moz-selection {
    color: var(--light) !important;
    background-color: var(--accent) !important; }
  /* Input placeholders. */
  ::-webkit-input-placeholder {
    color: var(--lighter) !important; }

  /* Progress. */
  progress[value]::-webkit-progress-bar {
    background: var(--darker); }
  /* Value. */
  progress[value]::-webkit-progress-value {
    background: var(--accent); }
  progress {
    background: var(--darker); }
  ::-moz-progress-bar {
    background: var(--accent) !important; }

  /* App wrapper. */
  #app .app {
    background: none;
    background-color: var(--dark); }

    /* Accent background. */
    .app-wrapper::after {
      background: none !important; }


  /* Log-in page. */
  ._2NbD3 {
    border: 1px solid var(--shadow);
    box-shadow: 0 8px 32px 0 var(--shadow) !important;
    background-color: var(--darker) !important; }
    /* Text color. */
    ._2NbD3 * {
      color: var(--light); }
    /* Reload QR code. */
    .HnNfm {
      background-color: var(--darker) !important; }
    /* Fix QR code. */
    ._2NbD3 .XSdna {
      border: 3px solid white !important; }
    /* Tutorial. */
    ._2NbD3 > div:last-child {
      background-color: var(--darker); }
    /* Loader. */
    #startup svg > circle {
      stroke: var(--lighter) !important; }

  /* Disable background. */
  ._2U_Zc:before {
    display: none !important; }

  /* Icon color. */
  span[class="_3CSsZ"] > svg > rect {
    fill: var(--dark) !important; }


  /* Landing page. */
  .app ._1Iexl ._3qlW9 {
    border: none;
    background-color: var(--dark); }
    /* Image. */
    .app ._1Iexl ._3qlW9 ._3BqNZ ._2Uo0Z {
      max-width: 200px;
      max-height: 200px;
      border-radius: 50%;
      filter: opacity(/*[[emojiOpacity]]*/);
      transition: opacity 1s ease !important; }
    /* Text color. */
    .app ._1Iexl ._3qlW9 ._2MnNk * {
      color: var(--light); }
    /* Accent bar. */
    .app ._1Iexl ._3qlW9:after {
      border-top-color: var(--accent); }
    /* Hide the text. */
    ._1ClcF { font-size: 0px; }
    /** Theme note. */
    .app ._1Iexl ._1ClcF::before {
      display: block;
      margin-bottom: -20px;
      font-size: 16px;
      content: var(--message) var(--changes) !important;
    }
    /** Theme info. */
    .app ._1Iexl ._1ClcF::after {
      display: block;
      margin-top: 10px;
      padding-top: 10px;
      font-size: 16px;
      content: var(--version) !important;
      border-top: 1px solid var(--darker);
    }

  /* Left drawer. */
  #app .app #side {
    background-color: var(--darker); }

    /* Chat list. */
    #side > header {
      background-color: var(--darker) !important; }

    /* Notifications && more!! */
    #side ._3YewW {
      box-shadow: inset 0 0 0 2px var(--accent);
      background-color: var(--darker) !important; }
      /* Text color. */
      #side ._3YewW .l2BEH * {
        color: var(--lighter); }

    /* Search or start new chat. */
    #side ._3CPl4 {
      border-bottom: 0px solid var(--dark);
      background-color: var(--darker) !important;
      background-color: transparent !important; }
    /* Label background. */
    #side ._3CPl4 .gQzdc label,
    .gQzdc._3sdhb,
    ._2dA13 ._15JHr .gQzdc {
      border: 0px solid var(--dark);
      background-color: var(--dark) !important; }
      /* Input element. */
      #side ._3CPl4 .gQzdc label input {
        color: var(--light);
        background-color: transparent; }
      /* Active. */
      #side ._3CPl4 .gQzdc._3sdhb {
        background-color: var(--dark) !important; }

  /* New group and group info buttons. */
  ._2EXPL .dIyEr ._1jJLh,
  ._2EXPL .dIyEr ._1WliW {
    box-shadow: 0 0 8px -1px var(--shadow) !important;
    background-color: var(--dark) !important; }

  /* Search results. */
  #side ._1AKfk {
    color: var(--accent);
    background-color: var(--dark); }
    /* Matching results. */
    #side .matched-text {
      color: var(--accent) !important; }

    /* Contacts wrapper. */
    #side ._1NrpZ, ._2fq0t ._1lIXT {
      background-color: transparent !important; }
      /* Contacts. */
      #side ._1NrpZ ._2EXPL, ._2fq0t ._66JgU {
        transition: 200ms ease-in-out;
        background-color: transparent !important; }
        /* On event. */
        #side ._1NrpZ ._2EXPL._1f1zm,
        #side ._1NrpZ ._2EXPL:hover {
          background-color: var(--dark) !important; }
        /* Text color. */
        #side ._1NrpZ [class*="_3j7s9"] * {
          color: var(--light); }
        /* Avatar wrapper background. */
        ._1WliW {
          box-shadow: 0 0 8px -1px var(--shadow) !important;
          background-color: transparent !important; }

  /* New message indicator. */
  #side .OUeyt {
    color: var(--lighter) !important;
    background-color: var(--darker) !important;
    box-shadow: inset 0 0 0 2px var(--accent); }


  /* Settings. */
  ._2fq0t {
    background-color: var(--darker) !important; }
    /* Header color. */
    ._2fq0t header {
      min-height: 50px;
      height: auto;
      background-color: var(--darker) !important; }
      /* Reset header height. */
      ._2fq0t header > div:first-child {
        padding: 12px 0 !important;
        height: auto; }
      /* Reset header title in left/right drawers. */
      ._2fq0t ._1xGbt {
        margin-top: 2px !important;
        font-size: 16px; }
    /* Transparent sections. */
    ._2fq0t ._1CRb5 {
      box-shadow: none !important;
      background-color: transparent !important; }
      /* Highlighted sections. */
      ._2fq0t ._3hhmj ._1CkkN,
      ._3UUn3 {
        background-color: var(--dark) !important; }
      /* Change profile picture. */
      ._2fq0t .WX_XW,
      ._2fq0t ._2Lbqu {
        background-color: rgba(0,0,0,0.5) !important; }
      /* Text color. */
      ._2fq0t * {
        color: var(--light); }
      /* Zoom in/out. */
      ._2LALH {
        background-color: var(--dark) !important; }

    /* Option/select elements. */
    .uyEaY select {
      border-color: var(--accent); }
      .uyEaY select option {
        background-color: var(--dark); }

    /* Archived chat image. */
    ._2fq0t ._2Lev2 {
      background-color: var(--dark) !important; }

    /* Body. */
    ._2fq0t ._2sNbV ._1CkkN,
    ._2fq0t ._2sNbV ._2EXPL {
      background-color: transparent; }
      /* On event. TODO: Fixed, but it might still overlap somewhere. */
      .k1feT ._2sNbV > ._1CkkN:hover,
      ._2fq0t ._2sNbV ._1CkkN:hover {
        background-color: var(--dark); }

    /* New chat section. */
    ._2fq0t .gQzdc label {
      border: none;
      background-color: var(--dark); }
    /* Input element. */
    ._2fq0t .gQzdc label input {
        background-color: transparent; }

    /* New group. */
    ._2fq0t ._39pS- {
      background-color: transparent !important; }
      /* Header. */
      ._2fq0t ._1AKfk {
        background-color: var(--darker) !important; }
        /* On event. */
        .k1feT ._2fq0t ._2EXPL._1f1zm,
        .k1feT ._2fq0t ._2EXPL:hover {
          background-color: var(--dark) !important; }
      /* Search element */
      ._66JgU ._3_3Rs {
        border-bottom: none !important; }
      ._66JgU ._3_3Rs input, ._66JgU ._3_3Rs ::placeholder {
        color: var(--lighter) !important; }

  /** Context/dropdown menu. */
  ._1RQfk {
    background-color: var(--darker) !important;
    box-shadow: 0 4px 16px 0 var(--shadow) !important; }
  ._1RQfk li:hover, ._1RQfk li:hover div {
    color: var(--accent) !important;
    background-color: var(--dark) !important; }
  ._1RQfk div, ._1ArIP div {
    color: var(--light); }

  /* Middle drawer. */
  .app ._1Iexl ._1GX8_ {
    background-color: var(--dark); }
    /* Doodle background. TODO: Plans for the future. */
    /*[[doodles]]*/

    /* Jump to present button. */
    ._298R6 {
      box-shadow: 0 4px 8px 2px var(--shadow) !important;
      background-color: var(--darker) !important; }
    /* Number of messages. */
    ._298R6 .OUeyt {
      background-color: var(--accent) !important; }

    /* Header. */
    ._1Iexl #main header {
      border: none;
      background-color: var(--dark); }
      /* Text color. */
      ._1Iexl #main header ._1WBXd * {
        color: var(--light); }

    /* Header text color. #22 */
    #main > header > div span[title] {
      color: var(--light); }
    /* Header separator. */
    #main > header::after {
      border-bottom-color: var(--darker);
      background-color: transparent; }

  /* Footer. */
  ._1Iexl #main footer {
    border-top: 1px solid var(--darker);
    background-color: var(--darker); }
    /* Input wrapper. */
    #main footer > div {
      color: var(--light);
      border: none;
      /* border-bottom: 2px solid var(--dark); */
      background-color: var(--dark); }
      /* Input element. */
      #main footer > div:nth-child(1) > div:nth-child(2) {
        border: none !important;
        color: var(--light);
        background-color: var(--darker) !important; }
    /* Voice message duration. */
    ._30ie- {
      color: var(--light) !important; }

  /* Unknown contact. */
  .TYhk9 {
    border-color: var(--darker) !important;
    background-color: var(--dark) !important;
    box-shadow: 0 0px 8px 2px var(--shadow) !important; }
  /* Buttons. */
  .TYhk9 ._1sB6s {
    color: var(--light) !important;
    background-color: var(--darker) !important; }

    /* Shared contact button colors. */
    .b332w {
      color: var(--accent) !important; }
    /* Shared contact text. */
    .AVd_p {
      color: var(--lighter) !important; }
    /* View contact. */
    ._36ApL {
      background-color: transparent !important; }

  /* Hide 'Type a message' once focused. */
  ._3F6QL._2WovP.focused ._39LWd {
    color: transparent; }
    /* Placeholder text color. */
    ._39LWd {
      color: var(--lighter) !important; }
    /* On event smoothness. */
    .pluggable-input.pluggable-input-compose > div:first-child {
      transition: 150ms ease-in-out !important; }


  /* Right drawer. */
  .app ._2yeJ5 ._2fq0t {
    background-color: var(--darker); }
    /* Border-color. */
    .app ._2yeJ5 {
      border-left-color: transparent !important; }
    /* Header. */
    .app ._2yeJ5 ._2fq0t header {
      background-color: var(--darker); }
      /* Text color. */
      .app ._2yeJ5 ._2fq0t header div {
        color: var(--light); }

    /* Group info sections. */
    .app ._2yeJ5 ._2fq0t ._1CRb5 {
      /*box-shadow: 0 1px 3px shadow /**/
      box-shadow: none;
      background-color: transparent; }
      /* Group name. */
      .app ._2yeJ5 ._1CRb5 ._1DTd4, .media-body .CzI8E {
        margin-bottom: 10px;
        color: var(--light);
        border-bottom: 2px solid var(--accent) !important; }
      /* User's name. */
      .app ._2yeJ5 .copyable-text {
        color: var(--light); }
      /* Accent color text. */
      .app ._2yeJ5 ._1CRb5 ._1sYdX {
        font-weight: 400;
        color: var(--accent) !important; }
      /* Mute/Starred Messages. */
      .app ._2yeJ5 ._1CRb5 ._3LL06 {
        color: var(--light); }

      /* Group options. */
      .app ._2yeJ5 ._1CRb5 ._2EXPL,
      .app ._2yeJ5 ._1CRb5 ._1CkkN {
        transition: 200ms ease-in-out;
        background-color: transparent; }
        /* On event. */
        .app ._2yeJ5 ._1CRb5 ._2EXPL._3xj48:hover,
        .app ._2yeJ5 ._1CRb5 ._1CkkN:hover {
          background-color: var(--dark); }
        /* Text color. */
        .app ._2yeJ5 ._1CRb5 ._2EXPL * {
          color: var(--light); }
        /* Admin color. */
        .app ._2yeJ5 ._1CRb5 ._2e8Mu {
          border: 2px solid var(--accent); }


  /* Search messages. TODO: Apply this to all search elements. */
  ._2yeJ5 ._1iMEz, ._1iopp /*, ._2yeJ5 ._1iMEz._3sdhb*/ {
    background-color: var(--darker) !important; }
    /* Input wrapper. */
    ._1iMEz label {
      border: 0px solid var(--dark) !important;
      background-color: var(--dark) !important; }
      ._1iMEz ._3sdhb label {
        background-color: transparent !important; }
      /* Input element. */
      ._1iMEz label input {
        background-color: transparent !important; }

  /* Search messages body. */
  .YAPQk {
    background-color: var(--darker) !important; }
    /* Matching results. */
    ._2yeJ5 .matched-text {
      color: var(--accent) !important; }
    /* On event. */
