:root {
    --dark: rgb(60, 56, 54);
    --darker: rgb(50, 48, 47);
    --light: rgb(235, 219, 178);
    --lighter: rgb(251, 241, 199);
    --accent: rgb(214, 93, 14);
    --icon: rgb(235, 219, 178);
    --shadow: rgba(0, 0, 0, .25);
    --mred: rgb(251, 73, 52);
    --mgreen: rgb(184, 187, 38);
    --mblue: rgb(131, 165, 152);
    --image: url(https://media.discordapp.net/attachments/310799529161129984/417408747812552715/zilesanamin.jpg);
    --emojiOpacity: 0.75;
    --version: "WhatsApp by Mew v1.3.0";
    --message: "Updated on 20th of February. ";
    --changes: "See the changelog at: https://github.com/vednoc/onyx/releases";
  }

  /* Body reset. */
  body {
    background: none;
    background-color: var(--dark); }

  /* Scrollbar track. */
  *::-webkit-scrollbar-track {
    background-color: var(--shadow); }
  /* Scrollbar thumb. */
  *::-webkit-scrollbar-thumb {
