/* ==UserStyle==
@name         GitLab Dark by Mew
@namespace    gitlab.com/vednoc/gitlab-dark
@version      0.34.1
@description  Dark theme for GitLab.
@author       vednoc
@homepageURL  https://gitlab.com/vednoc/dark-gitlab
@supportURL   https://gitlab.com/vednoc/dark-gitlab/issues
@updateURL    https://gitlab.com/vednoc/dark-gitlab/raw/master/gitlab.user.styl
@preprocessor stylus

@var text     b_radius  "Custom border radius" 4px
@var text     a_radius  "Custom avatar radius" 50%
@var text     max_width "Custom width for fluid layout" 100%
@var text     code_font "Custom code font" "'custom_font', "Menlo", "DejaVu Sans Mono", "Liberation Mono", "Consolas", "Ubuntu Mono", "Courier New", "andale mono", "lucida console", monospace"
@var text     emoji_font "Custom emoji font" "'custom_font', 'Apple Color Emoji', 'Segoe UI Emoji', 'Segoe UI Symbol', 'Noto Color Emoji'"
@var text     faded_img "Fade images in md/wiki/blog" "1"
@var checkbox gl_icons  "GitLab Icons: Fix icon alignment" 0
@var checkbox p_header  "Center project header elements" 0
@var checkbox dark_logo "Switch to our darker logo" 0
@var checkbox navbar_c  "Navbar: Theme navbar color" 1
@var checkbox navbar_n  "Navbar: Show 'next' badge" 1
@var checkbox checkbox  "Checkbox inversion" 1
@var checkbox bg_invert "Background inversion" 1
@var text     bg_image  "Background url/base64" "url("data:image/png;base64,iVBORw0KGgoAAAANSUhEUgAAAA8AAAAPCAYAAAA71pVKAAAABHNCSVQICAgIfAhkiAAAAAlwSFlzAAAOxAAADsQBlSsOGwAAABl0RVh0U29mdHdhcmUAd3d3Lmlua3NjYXBlLm9yZ5vuPBoAAAA7SURBVCiRY2BgYHBgQABbBgaGlwwMDE4ExBwYGBheMRBQRJQY/TUyQN3uQIZGBzhBhsbRACNR44gMMADUtz9VRtuY9AAAAABJRU5ErkJggg==")"
@var text     bg_filter "Background filters" opacity(0.05)
@var text     bg_size   "Background size" 15px
@var text     bg_repeat "Background repeat" repeat
@var text     bg_pos    "Background position" center

@var color dark    "Dark color."    #2e2e2e
@var color darker  "Darker color."  #212121
@var color light   "Light color."   #d1d1d1
@var color lighter "Lighter color." #e9e9e9
@var color icon    "Icon color."    #e1e1e1
@var color accent  "Accent color."  #7289da
@var color yellow  "Yellow color."  #f0c674
@var color orange  "Orange color."  #de935f
@var color red     "Red color."     #a54242
@var color magenta "Magenta color." #b294bb
@var color violet  "Violet color."  #85678f
@var color blue    "Blue color."    #5f819d
@var color cyan    "Cyan color."    #5e8d87
@var color green   "Green color."   #8c9440
@var color v_white "White color."   #ffffff
@var color shadow  "Shadow"         rgba(0,0,0,0.07)

==/UserStyle== */

@-moz-document regexp("^https?://(www\.)?([A-Za-z0-9]+\.)?(gitlab\.(?!(io|custom.domain)).*|your.custom.domain|gitgud.io|salsa.debian.org|framagit.org|source.joinmastodon.org|repo.getmonero.org|git.gnu.io|git.synz.io)(/.*)?$") {
  :root {
    --note      : "Dark-GitLab v0.34.1";
    --dark      : dark;
    --darker    : darker;
    --light     : light;
    --lighter   : lighter;
    --accent    : accent;
    --icon      : icon;
    --shadow    : shadow;
    --box-shadow: 0 2px 3px 0 var(--shadow);
    --yellow    : yellow;
    --orange    : orange;
    --red       : red;
    --magenta   : magenta;
    --violet    : violet;
    --blue      : blue;
    --cyan      : cyan;
    --green     : green;
    --white     : v_white;
    --t         : transparent !important;
    --width     : max_width;
    --radius    : b_radius;
    --avatar    : a_radius;
    --code-font : code_font;
    --emoji-font: emoji_font;
    --bg-invert : bg_invert;
    --bg-filter : bg_filter;
    --bg-size   : bg_size;
    --bg-repeat : bg_repeat;
    --bg-pos    : bg_pos;
    --background: bg_image;
    scrollbar-color: var(--darker) var(--dark);
  }

  // * Use stylus-lang variables with CSS variables.
  // TODO try to make this more compact.
  $dark    = var(--dark);
  $darker  = var(--darker);
  $light   = var(--light);
  $lighter = var(--lighter);
  $accent  = var(--accent);
  $icon    = var(--icon);
  $shadow  = var(--shadow);
  $yellow  = var(--yellow);
  $orange  = var(--orange);
  $red     = var(--red);
  $magenta = var(--magenta);
  $violet  = var(--violet);
  $blue    = var(--blue);
  $cyan    = var(--cyan);
  $green   = var(--green);
  $white   = var(--white);
  $radius  = var(--radius);
  $t       = var(--t);
  $box     = var(--box-shadow);

  // * Color mixin.
  // ? All colors -> colors: $light, $darker, $dark;
  // ? Only background-color -> colors: 0, 0, $dark;
  colors(x, y, z) {
    if x is a 'call' {
      color: x !important;
    }
    if y is a 'call' {
      border-color: y !important;
    }
    if z is a 'call' {
      background-color: z !important;
    }
  }

  // * Inlined SVGs.
  isvg(x, y) {
    if x is a 'call' { fill: x !important; }
    if y is a 'call' { stroke: y !important; }
  }

  // * Border radius mixin.
  radius() {
    -webkit-border-radius: arguments;
    -moz-border-radius: arguments;
    border-radius: arguments;
  }

  // Scrollbars.
  ::-webkit-scrollbar {
    width: 12px;
    &-track { background: $dark; }
    &-thumb { background: $darker; }
  }
  // Input placeholder.
  ::placeholder { color: $lighter !important; }
  ::-webkit-input-placeholder { color: $lighter !important; }

  body {
    colors: $light 0 $t;
    // Fixes content shift when modal is open.
    &.modal-open { padding-right: 0px !important; }
    // Empty navbar.
    &.navless {
      colors: $light 0 $t;
      height: calc(100% - 41px);
      .navbar-empty { colors: 0 $darker $dark }
    }
    // Footer.
    .footer-fixed {
      colors: 0 $darker $dark;
      + .footer-container {
        max-width: 100%;
        colors: 0 0 $dark;
      }
    }
  }

  h1, h2, h3, h4, h5, h6, p {
    color: var(--light) !important;
  }
  ul, ol { color: $light; }
  a {
    transition: 200ms ease-in-out;
    color: $accent;
    &:hover, &:focus { color: $accent; }
  }
  hr {
    margin: 16px 0;
    colors: 0, $darker, $t;
  }
  img.transparent,
  img.lazy {
    border: 1px solid var(--dark) !important;
    background: transparent !important;
  }
  code {
    color: $blue;
    border: 1px solid var(--darker);
    background-color: var(--dark);
  }
  // * Table -> Base styles for all `table` elements.
  table:not([class]) {
    radius: $radius;
    border-style: hidden;
    border-collapse: collapse;
    colors: 0 0 $dark;
    box-shadow: 0 0 0 1px inset $darker;
    thead {
      colors: 0 0 $t;
      th { colors: $light $darker $darker }
      tr:first-child {
        colors: 0 0 $t;
        > th:first-child { border-top-left-radius: $radius; }
        > th:last-child { border-top-right-radius: $radius; }
      }
    }
    tbody {
      colors: 0 $darker $t; // Fixes large top border in docs.
      td { colors: $light $darker $t }
      tr:last-child {
        // Bottom corners look a bit off on Firefox.
        td:first-child { border-bottom-left-radius: $radius; }
        td:last-child { border-bottom-right-radius: $radius; }
      }
    }
  }
  // * Table -> Found on: https://gitlab.com/help
  *:not(.use-csslab) table[data-sourcepos][dir] {
    > thead {
      display: flex;
      > tr {
        width: 100%;
        display: flex;
        > th {
          flex: 1 1 100%;
          colors: $light 0 0;
        }
      }
    }
    > tbody {
      display: flex;
      flex-direction: column;
      colors: 0, 0, $dark;
      > tr {
        display: flex;
        // Remove default border and set cell background color.
        > td {
          flex: 1 1 100%;
          border: none !important;
          a[href] { &, > code { colors: $accent, 0, 0; }}
          &:first-child {
            border-left: 1px solid $darker !important;
            border-bottom: 1px solid $darker !important;
          }
          &:last-child {
            border: 1px solid $darker !important;
            border-top: none !important;
          }
          &:not(:last-child):not(:first-child) {
            border-left: 1px solid $darker !important;
            border-bottom: 1px solid $darker !important;
          }
        }
      }
    }
  }
  // * Table -> Reported in: https://gitlab.com/vednoc/dark-gitlab/issues/41
  table[frame="void"] {
    colors: $lighter, 0, 0;
    box-shadow: none;
    > tbody {
      colors: 0, 0, $dark;
      tr th {
        border-bottom-width: 1px !important;
        colors: $light, $darker, $darker;
      }
    }
  }
  // * Table -> Project settings page.
  .border-bottom {
    border-bottom: none !important;
    > .table {
      border-collapse: collapse;
      box-shadow: 0 0 0 1px $darker;
      thead th {
        padding: 8px !important;
        colors: $light, $darker, $darker;
        &:first-child { border-top-left-radius: $radius; }
        &:last-child { border-top-right-radius: $radius; }
      }
      tbody {
        td {
          padding: 8px !important;
          colors: $light, $darker, $dark;
        }
        tr:last-child > td:first-child { border-bottom-left-radius: $radius; }
        tr:last-child > td:last-child { border-bottom-right-radius: $radius; }
      }
    }
  }

  // * Emojis -> Fix emojis being cropped.
  gl-emoji {
    font-size: 1.25em;
    font-family: var(--emoji-font) !important;
    // ? An attempt to fix emoji positioning on *fox-based browsers.
    @supports (-moz-user-select: none) {
      vertical-align: text-top !important;
    }
  }
  .user-status-emoji { display: inline-flex; }
  // Time element.
  time { color: $light; }
  // ? Remove underline from `label-link` selectors.
  .label-link { text-decoration: none !important; }

  // * Icons!
  .fa, svg {
    color: $icon !important;
    fill: $icon !important;
  }
  // * Icons -> Global icons that don't work without `path`.
  // ? Calendar, add reaction, commit icon.
  .nav-controls .has-tooltip svg, .award-control-icon svg, .commit-icon {
    path { fill: $icon !important; }
  }

  // * Bootstrap reset.
  .bg {
    &-white { colors: 0, 0, $dark; }
    &-info { colors: 0, 0, $darker; }
  }
  .text {
    &-white { colors: $light, 0, 0; }
    &-tertiary { colors: $lighter, 0, 0; }
    &-secondary { colors: $light, 0, 0; }
    &-danger { colors: $red, 0, 0; }
  }
  .badge {
    &.unverified { colors: $lighter, $lighter, $dark; }
    &.verified { colors: $green, $green, $dark; }
    &-success { colors: $light, 0, $green; }
    &-primary { colors: $light, 0, $blue; }
    &-info { colors: $light, 0, $blue; }
    &-pill { colors: $light, 0, 0; }
  }
  // TODO move to dashboard or somewhere else.
  .badge-pill {
    &.js-canary-badge.green-badge { colors: $light 0 0 }
    &.badge { border: 1px solid; colors: $lighter, $darker, $dark; }
    &.issues-count { colors: var(--white), 0, $green; }
    &.merge-requests-count { colors: var(--white), 0, $orange; }
    &.todos-count { colors: var(--white), 0, $blue; }
  }
  // Text colors.
  .warning-title { colors: $orange, 0, 0; }
  .danger-title { colors: $red, 0, 0; }
  .cgray,
  .committed_ago,
  .hint {
    color: var(--lighter) !important;
  }
  .warning_message {
    radius: 0 $radius $radius 0;
    border-left-color: $accent;
    colors: $light, 0, $darker;
  }
  // Alerts.
  .bs-callout {
    radius: $radius;
    colors: $light, $darker, $dark;
    &.gcp-signup-offer { border-left-color: $blue !important; }
  }
  // .../settings/ci_cd
  .card-footer > pre {
    radius: $radius;
    colors: $light, $darker, $dark;
  }

  // * Button reset.
  .btn {
    transition: transform 0.2s ease-in-out !important;
    color: var(--light);
    border-color: var(--darker);
    background-color: var(--dark);
    &:hover, &:focus, &.focus, &.btn-default {
      color: var(--light);
      border-color: var(--darker);
      background-color: var(--dark);
    }
    &:active, &.active {
      colors: $accent, $darker, $dark;
      // Active icons!
      // TODO Remove?
      > i, > .fa { color: inherit !important; }
    }
    // Remove blue outline.
    &:focus, &.focus { box-shadow: 0 0 2px 0px $accent; }
    &-link { colors: $accent, 0, 0; }
    // Expand button.
    &.click-to-expand { padding: 3px; }
  }
  // Colored buttons.
  .btn {
    // Green buttons.
    &-success,
    &-success.dropdown-toggle,
    &-register,
    &.comment-btn,
    &-new,
    &-create,
    &-save,
    .comment-btn,
    .commit-btn {
      colors: $white, $green, $green;
      box-shadow: var(--box-shadow) !important;
    }
    &-create.comment-btn {
      colors: $green, $green, $dark;
    }
    // Blue buttons.
    &-info,
    &-primary,
    &-register {
      colors: $white, $blue, $blue;
      box-shadow: var(--box-shadow) !important;
    }
    // Orange buttons.
    &-warning {
      colors: $white, $orange, $orange;
      box-shadow: var(--box-shadow) !important;
    }
    // Close buttons.
    &-close, &-close-color { colors: $red, $red, $dark; }
    &-close-color.dropdown-toggle {
      colors: $white, $red, $red;
      box-shadow: var(--box-shadow) !important;
    }
    &-cancel, &-retry {
      colors: $light, $darker, $dark;
    }
    // Remove buttons.
    &-danger, &-remove, &-red {
      colors: $white, $red, $red;
      box-shadow: var(--box-shadow) !important;
    }
    // Inverted buttons.
    &-inverted.btn-remove {
      colors: $white, $red, $red;
      box-shadow: var(--box-shadow) !important;
    }
    // Blank buttons.
    &-blank { colors: $lighter, 0, $t; }
    &-clipboard { colors: $lighter, $darker, $dark; }
    // Comment on a note.
    &-text-field {
      colors: $lighter, $darker, $darker;
    }
    // Reset for "add x" buttons.
    &-missing { colors: $light, $darker, $dark; }
    // Disabled buttons.
    &.disabled, &.disabled:hover,
    &[disabled], &[disabled]:hover {
      opacity: 0.6 !important;
      colors: $lighter, $darker, $darker;
      box-shadow: var(--box-shadow) !important;
    }
    // On event.
    &:hover:not([disabled="disabled"]),
    &:focus:not([disabled="disabled"]) {
      transform: translateY(-2px);
    }
    // Disable hover state for disabled elements.
    &.disabled:hover {
      transform: translateY(0px);
    }
  }
  // ? Button groups.
  .input-group-btn, .input-group-prepend, .input-group-append {
    colors: 0, 0, $t;
    .btn:focus { colors: 0, 0, $darker; }
  }
  // ? Reaction buttons.
  .award-control {
    &.is-active, &.user-authored { colors: $light, $darker, $dark; }
    // Reacted to.
    &.active { colors: $light, $blue, $dark; }
  }

  // * Project notifications.
  // TODO move to appropriate section.
  .project-feature-toggle {
    colors: 0, 0, $darker;
    &.is-checked { colors: 0, 0, $accent; }
    .toggle-icon { colors: 0, 0, $dark; }
  }

  // Issue/Merge status.
  .status-box {
    &-mr-merged { colors: $white, 0, $blue; }
    &-mr-closed { colors: $white, 0, $red; }
    &-open { colors: $white, 0, $green; }
    &-issue-closed { colors: $white, 0, $blue; }
  }

  // * Global styles.
  [class*="committer-link"],
  [class*="author-link"],
  .author_name a {
    color: $accent !important;
  }
  .nav-links:not(.quick-link) { colors: 0, $darker, 0; }
  .label-bold { color: $light; }
  .cred { color: $red; fill: $red !important; }
  .cblue { color: $blue; fill: $blue !important; }
  .cgreen { color: $green; fill: $green !important; }
  // ? Inverts checkboxes since we can't style them as they are currently.
  if checkbox {
    input[type="checkbox"] { filter: invert(var(--bg-invert)) !important; }
  }
  // ? Move to a better place.
  .option-title { colors: $light, 0, 0; }
  .option-description, .option-disabled-reason {
    colors: $lighter, 0, 0;
  }
  // ? Check #13 for more info.
  .deployment-info, .js-deploy-time { colors: $light, 0, 0; }
  // * Grid styles.
  .flex-grid {
    .grid-row { colors: 0, $darker, 0; }
    .grid-cell {
      colors: 0, $darker, 0;
      .property-label { colors: $lighter, 0, 0; }
    }
  }
  // ? Promotion background on i.e. project/audit_events page.
  .promotion-backdrop { colors: 0, 0, $t; }
  // ? Custom GitLab tables on i.e. project/settings/badges page.
  .gl-responsive-table-row-layout {
    colors: 0, $darker, 0;
    // Items border.
    .table-section:not(:first-child) { colors: 0, $darker, 0; }
    // Text badges.
    .badge.badge-pill { colors: $light, 0, $darker; }
    // Footer.
    .table-button-footer {
      colors: 0, $darker, $t;
      @media screen and (max-width: 767px) { colors: 0, 0, $darker; }
    }
  }
  // ? Pipelines: Fix table corners and item borders.
  .gl-responsive-table-row {
    &:not(:last-child):not(:first-child) {
      border-bottom: none !important;
      border-top: 1px solid $darker;
    }
    &:first-child { radius: $radius $radius 0 0; }
    &:last-child { radius: 0 0 $radius $radius; }
  }
  // ? 404 page.
  img[alt="404"] + .container {
    margin-bottom: 1em;
    colors: 0, $darker, 0;
  }

  // * Sign in/register page.
  .login-page {
    // Header.
    .new-session-tabs {
      border-color: $darker;
      box-shadow: 0 0 0 1px $darker;
      li {
        colors: $lighter, $darker, $darker;
        a.active, a:hover { colors: $light, $accent, $darker; }
      }
    }
    // Credentials.
    .login-box {
      radius: 0 0 $radius $radius;
      box-shadow: 0 0 0 1px $darker;
      background-color: $dark;
      .form-control { colors: 0, 0, $darker; }
    }
    // Sign in with.
    .omniauth-container {
      radius: $radius;
      colors: 0, 0, $dark;
      box-shadow: 0 0 0 1px $darker;
      .omniauth-btn { colors: 0, 0, $darker; }
    }
  }

  /* Misc text color. */
  .light,
  .description-block,
  .task-list-item {
    color: var(--light) !important;
  }
  .help-block {
    colors: $lighter, 0, 0;
  }
  // Epics start/due dates.
  .block .value {
    span:not(.color-label) { colors: $light, 0, 0; }
    abbr { colors: $lighter, 0, 0; }
  }


  // * Cookies.
  #CybotCookiebotDialog {
    colors: $light, 0, $darker;
    box-shadow: 0 0 4px 0 $shadow;
    // Buttons.
    [id*="ButtonsSelectPane"] { colors: 0, $dark, $dark; }
    // Show/hide details button.
    [id*="DetailsWrapper"] {
      colors: $accent, $dark, $accent;
      a { color: $light; }
    }
    // Show details.
    [id*="DialogDetail"] {
      border-width: 1px;
      colors: $light, $dark, $darker;
      [id*="BodyContentTabs"] { border-color: $red; }
      a {
        colors: $accent, $dark, 0;
        &:hover, &[class*="Selected"] { colors: 0, 0, $dark; }
      }
      thead th { colors: $light, $dark, $dark; }
      tbody td { colors: $light, $dark, $t; }
    }
  }

  // * Navbar.
  .navbar {
    colors: 0, $darker, 0;
    box-shadow: 0 2px 8px -2px $shadow !important;
    // ? Feature -> Theme navbar color.
    if ( navbar_c == 1 ) { colors: 0 0 $darker; }
    // ? Feature -> Show 'next' badge.
    if ( navbar_n != 1 ) { .js-canary-badge { display: none; }}
    // Logo text color.
    .logo-text svg, svg.nav-logo .wordmark { fill: $light !important; }
    // Search.
    .search form {
      colors: $lighter, $dark, $dark;
      #search { colors: $lighter, 0, 0; }
    }
    // Navbar links and buttons.
    a, button {
      transition: all 0.2s ease-in-out !important;
      colors: $lighter, 0, 0;
      &:hover, &:focus { colors: $light, 0, $dark; }
    }
    // Active tab/button/menu.
    &-sub-nav, &-nav {
      > li.active, .dropdown.show {
        > a, > button { colors: $light, 0, $dark; }
      }
      // ? Reset button hover state in dropdowns.
      button.btn:hover, .btn:focus { transform: none !important; }
    }
    // Navbar separator on mobile devices.
    &-toggler { border-left-color: $dark !important; }
    // ? Avatars -> Reset.
    .header-user-avatar {
      radius: var(--avatar) !important;
      border: none !important;
      colors: $lighter, 0, $t;
    }
    // ? Shows theme version in the user dropdown.
    .header-user li:first-child::before {
      content: var(--note);
      display: block;
      padding: 8px 12px;
      margin: -16px -12px 8px -12px;
      border-bottom: 1px solid $darker;
      color: $light;
    }
  }

  // * Navbar -> Projects/Groups.
  .frequent-items-dropdown-menu {
    radius: $radius;
    .section-header { colors: $light, 0, 0; }
    .section-empty, [class*="namespace"] { colors: $lighter, 0, 0;}
    [class*="dropdown-sidebar"] { colors: 0, $darker, 0; }
  }

  // * Dropdown.
  .dropdown {
    // ? Toggle.
    &-toggle {
      colors: $lighter, $darker, $dark;
      // ? Add missing top margin to the dropdown on group page. @upstream
      + ul.show { margin-top: 10px !important; }
    }
    // On event.
    &-item {
      transition: background 0.15s ease-in-out;
      &:hover, &:focus {
        color: var(--light);
        background-color: var(--dark);
      }
    }
    // Shadow.
    &-content-faded-mask::after {
      background: none;
    }
    // Button groups.
    .btn-group {
      > button, > a {
        colors: 0, $darker, $dark;
        transition: 0.2s ease !important;
        &:hover { transform: none !important; }
        &:hover, &.active { colors: 0, 0, $darker; }
        &:first-child { border-radius: $radius 0 0 $radius; }
        &:last-child { border-radius: 0 $radius $radius 0; }
      }
    }
    a:active { colors: $light, 0, $darker; }
    .bold { colors: $lighter, 0, 0; }
    label { colors: $light, 0, 0; }
    // ? Navbar -> Search menu.
    &-select {
      li {
        &.separator { colors: 0, 0, $darker; }
        &.dropdown-header { colors: $lighter, 0, 0; }
        a {
          span b { colors: $accent, 0, 0; }
          &:hover { colors: $light, 0, $darker; }
          // ! Weird behavior in searching from projects. @upstream
          &.is-active { colors: $light, 0, 0; }
          &.is-focused { colors: $accent, 0, $darker; }
        }
      }
    }
    // ? Issue boards -> Switch board.
    &-extended-height {
      a {
        transition: 0.1s ease;
        colors: $lighter, 0, 0;
        &:hover, &:active, &:focus { colors: $light, 0, $darker; }
        &.is-active { colors: $accent, 0, 0; }
      }
      // ? Boards -> Create new board.
      .dropdown-divider ~ .dropdown-item {
        colors: $lighter, 0, 0;
        transition: 0.15s ease;
        &:hover, &:active, &:focus { colors: $light, 0, $darker; }
      }
    }
  }
  .dropdown-menu {
    radius: $radius;
    colors: 0, $darker, $dark;
    box-shadow: 0 4px 8px 0 $shadow;
    // ? Height reset.
    &-selectable.show { max-height: unset !important; }
    // ? Toggle.
    &-toggle:not(#search) {
      colors: $lighter, $darker, $dark;
      .dropdown-toggle-text { colors: $lighter, 0, 0; }
      &[class*="filtered-search-history"] { radius: 0 !important; }
      // ? Add missing top margin to the dropdown on group page. @upstream
      + ul.show { margin-top: 10px !important; }
    }
    // ? Header/Title.
    .dropdown-header, .dropdown-title { colors: $light, $darker, 0; }
    // ? Input / Search.
    .dropdown-input-field, .default-dropdown-input {
      radius: $radius;
      colors: $light, $darker, $dark;
      &:focus { box-shadow: 0 0 2px 0px $accent; }
    }
    // ? Body/Content.
    .dropdown-info-note { colors: $light, 0, 0; }
    [class*="divider"] { colors: 0, $darker, $t; }
    // ? Search projects on `/-/operations` page.
    .dropdown-item:not(.disable-hover):hover { colors: 0, 0, $darker; }
    li {
      // ? Reset avatar padding.
      .dropdown-menu-user-link:not(.is-active) { padding-left: 8px; }
      // ? Base styles.
      &:hover a, &:hover button:not(.btn-success) {
        transform: none;
        colors: $light, 0, $darker;
      }
      a, button:not(.btn) {
        colors: $lighter, 0, 0;
        transition: 200ms ease-in-out;
      }
    }
    // ? Reset animation for filter-dropdown items.
    .filter-dropdown-item > .btn { transform: none; }
    // ? MR -> "Created date" dropdown.
    &-sort {
      li {
        // ? Reset due to markup being single `li` with a ton of `a` elements.
        &:hover a { colors: $lighter, 0, $t; }
        a {
          transition: 0.1s ease;
          &:hover { colors: $light, 0, $darker; }
          &.is-active { colors: $accent, 0, 0; }
        }
      }
    }
    // ? MR -> Download button.
    &-right:not(&-sort) {
      li {
        // ? Reset due to markup being single `li` with a ton of `a` elements.
        &:hover a { colors: $lighter, 0, $t; }
        a, button:not(.btn), .menu-item {
          transition: 0.1s ease;
          colors: $lighter, 0, 0;
          &:hover { colors: $light, 0, $darker; }
        }
        .form-group label { colors: $light, 0, 0; }
      }
    }
    // ? Project -> Notifications.
    &-large {
      li a {
        transition: 0.1s ease;
        .dropdown-menu-inner-title { colors: $light, 0, 0; }
        .dropdown-menu-inner-content { colors: $lighter, 0, 0; }
      }
    }
    // ? Snippets -> Embed menu.
    &-selectable {
      li {
        button { colors: $lighter, 0, 0; }
        .is-active { colors: $light, 0, 0; }
      }
    }
    // ? Issue boards -> Add list.
    &-tabs {
      // max-height: 400px !important;
      .dropdown-tabs-list {
        colors: 0, 0, $dark;
        box-shadow: 0 1px 0 0 $darker !important;
        .dropdown-tab-item {
          colors: 0, $darker, 0;
          a { colors: $light, 0, $dark; }
          a.active { colors: $lighter, $accent, $dark; }
        }
      }
    }
    // ? Diff -> File finder.
    &.diff-file-changes {
      min-width: 500px !important;
      .diff-changed-file {
        [class*="path"] { colors: $lighter, 0, 0; }
        &.is-focused, &:focus {
          colors: 0, 0, $darker;
          [class*="file-name"] { colors: $light, 0, 0; }
        }
      }
    }
    // ? Footer.
    .dropdown-footer {
      colors: 0, $darker, 0;
      li:hover a { colors: $lighter, 0, $t; }
      li a {
        transition: 0.1s ease;
        &:hover { colors: $light, 0, $darker; }
      }
    }
    // ? Loader.
    [class*="-loading"] {
      colors: 0, 0, $dark;
      .fa-spinner:before { color: $lighter; }
    }
  }

  // * Dropdown -> The select2 component.
  .select2 {
    // Menu.
    &-drop {
      radius: $radius;
      colors: 0, $darker, $dark;
    }
    // Menu arrow.
    &-arrow {
      padding-left: 12px;
      colors: 0, 0, $t;
      &::after { colors: $lighter, 0, 0; }
    }
    // Input/select.
    &-drop input, &-choice, &-search-choices, &-default {
      radius: $radius !important;
      colors: $lighter, $darker, $dark;
    }
    &-container-multi &-choices {
      radius: $radius;
      colors: $lighter, $darker, $dark;
      input:focus { colors: $light, 0, 0; }
    }
    // ? Fix for flashing input.
    &-container-multi &-choices &-search-field input&-active {
      background: none !important;
    }
    // Results.
    &-results &-result-label {
      transition: 0.1s ease;
      colors: $lighter, 0, 0;
      .group-name { color: $light; }
      .group-path { color: $lighter; }
    }
    &-results &-highlighted &-result-label {
      colors: $light, 0, $darker;
    }
    // Loader.
    &-more-results { colors: 0, 0, $t; }
  }

  // * Dashboard -> Search page hacks.
  [data-project=""] {
    // Remove bottom border from navigation.
    .scrolling-tabs-container .nav-links { border-bottom: none; }
    // Remove content block below the sub-nav.
    .scrolling-tabs-container + .row-content-block {
      padding: 0 !important;
      border: none !important;
      box-shadow: inset 0 0 0 1px $darker;
    }
    // Issues/MR/Milestones.
    .search-results {
      radius: $radius;
      colors: 0, 0, $dark;
      // box-shadow: inset 0 0 0 1px $red;
      .search-result-row {
        padding: 8px;
        margin-bottom: 0;
        colors: 0, $darker, 0;
        &:first-child { radius: $radius $radius 0 0; }
        &:last-child { radius: 0 0 $radius $radius; }
        h4 { margin-top: 0px; }
        code { colors: $lighter, 0, 0; }
        pre.code { colors: 0, $darker, 0; }
      }
    }
  }

  // * Dashboard -> Welcome page.
  .section-welcome {
    padding: 0 !important;
    .container {
      max-width: var(--width) !important;
      // Fix odd spacing.
      .blank-state-row { justify-content: space-between; }
      // Cards.
      .blank-state-link {
        radius: $radius;
        colors: 0, 0, $dark;
        &:hover { colors: $light, 0, $darker; }
        .blank-state {
          radius: $radius;
          colors: 0, $darker, 0;
        }
      }
    }
  }

  // * Dashboard :: New project.
  .gitlab-tabs {
    radius: $radius $radius 0 0;
    colors: 0, $darker, $darker;
    li.nav-item {
      &:not(:last-child) { border-right: none; }
      &.active a.nav-link { colors: $light $accent $dark; }
      &:hover a.nav-link { colors: $accent $darker $darker; }
    }
  }
  // Tab content.
  .gitlab-tab-content {
    radius: 0 0 $radius $radius;
    colors: 0, $darker, $dark;
    // Inputs.
    [class*="input-group"] {
      radius: $radius;
      colors: $lighter, $darker, $darker;
    }
    .template-option { colors: 0, $darker, 0; }
  }
  .account-btn-link,
  .profile-settings-sidebar a,
  .settings-sidebar a {
    color: var(--accent);
  }
  /* Form control. */
  .form-control {
    colors: $lighter, $darker, $dark;
    // transition: border-color 200ms ease-in-out !important;
    &:focus {
      colors: 0, $blue, 0;
      box-shadow: 0 0 2px 1px $shadow;
    }
  }

  // * Table -> Old-ish table components.
  .table-responsive, table.table {
    radius: $radius;
    colors: 0, 0, $dark;
    box-shadow: 0 0 0 1px inset $darker;
    thead tr {
      th {
        border-top: none;
        colors: $light, 0, $darker;
      }
      > th:first-child { radius: $radius 0 0 0; }
      > th:last-child { radius: 0 $radius 0 0; }
    }
    tbody tr {
      td {
        transition: 0.15s ease;
        colors: $lighter, 0, 0;
      }
      &:last-child > td:first-child { radius: 0 0 0 $radius; }
      &:last-child > td:last-child { radius: 0 0 $radius 0; }
      &:hover > td { colors: $light, 0, $darker; }
    }
    // Highlighted odd rows.
    &.table-striped tbody tr:nth-of-type(2n + 1) { colors: 0 0 $shadow; }
    // Remove extra margin.
    > .table { margin-bottom: 0px; }
    // Imported projects.
    .table-success,
    .table-success > td {
      colors: 0, 0, $darker;
    }
  }


  // * User Settings :: Everything.
  .settings-message {
    colors: $light, $darker, $dark;
  }
  // Social icons.
  .provider-btn {
    colors: $lighter, 0, $dark;
    &-group { colors: 0, $darker, $dark; }
    &-image { colors: 0, $darker, 0; }
  }
  // Path.
  .input-group-text {
    colors: $light, $darker, 0;
    // Fix #29.
    &:not(.label-color-preview) { colors: 0, 0, $darker; }
  }
  /* Linked emails. */
  .account-well { colors: $light, $darker, $dark; }
  /* List items. */
  ul.bordered-list > li { border-bottom-color: $darker; }
  /* Active sessions. */
  .card .list-group-item {
    border: 1px solid !important;
    colors: $light, $darker, $dark;
  }
  /* Pipeline quota table. */
  .pipeline-quota,
  .table tr td {
    border-color: var(--darker) !important;
  }
  .pipeline-quota .progress {
    box-shadow: 0 0 0 1px var(--shadow);
    background-color: var(--darker);
  }
  /* Text color. */
  .text-muted,
  .description {
    color: var(--light) !important;
  }
  .profile-settings-content a {
    color: var(--accent);
  }

  // * Alerts.
  .alert-wrapper {
    display: flex;
    flex-direction: column;
    // Move breadcrumbs to the last spot.
    > nav { order: 999; }
    // Alert main reset.
    [class*="flash-"] {
      margin-bottom: 0px !important;
      // ? Fixes unnecessary limited container issue.
      > .container-limited {
        padding: 0;
        max-width: 100%;
      }
      a {
        color: $white;
        text-decoration: underline;
      }
    }
    // Alert colors.
    .flash-notice { colors: $white, $blue, $blue; }
    .flash-alert { colors: $white, $red, $red; }
    .flash-success { colors: $white, $green, $green; }
    .alert-warning { colors: $white, $orange, $orange; }
    .alert-link { colors: $white, 0, 0; }
    // ? Broadcasts found on `git.synz.io`.
    .broadcast-message {
      p, .fa { color: inherit !important; }
      &[style*="background-color: #004275"] { colors: $white, 0, $blue; }
      &[style*="background-color: #00bd7f"] { colors: $white, 0, $green; }
      &[style*="background-color: #e75e40"] { colors: $white, 0, $orange; }
      &[style*="background-color: #f4f4f4"] { colors: $light, 0, $darker; }
    }
  }
  // Green alerts.
  .alert-success {
    colors: $white, $green, $green;
    &.prepend-top-default { radius: $radius; }
  }
  // Customers page, possible refactor.
  .alert-danger { colors: $white, $red, $red; }
  .alert-info { colors: 0, $blue, $dark; }

  // * Bootstrap breadcrumbs.
  .breadcrumbs {
    colors: $light, 0, 0;
    // Wrapper.
    .breadcrumbs-container {
      padding: 0 10px;
      margin-top: 10px;
      radius: $radius;
      border: 1px solid !important;
      colors: 0, $darker, $dark;
    }
    &-list a { colors: $lighter, 0, 0; }
    // Highlighted link.
    &-sub-title a { colors: $light, 0, 0; }
    // Separator on mobile devices.
    @media (max-width: 576px) {
      .toggle-mobile-nav + .breadcrumbs-links { colors: $lighter, $darker, 0; }
    }
    // ? Reset epics-roadmap page.
    &.group-epics-roadmap {
      border-bottom: none !important;
      max-width: 100% !important;
    }
  }
  .breadcrumb {
    .breadcrumb-item a {
      colors: $lighter, 0, 0;
    }
  }

  // * Epics :: Resets!
  [data-page*="groups:roadmap:"]  {
    .row-content-block {
      margin-top: 0px !important;
      padding: 8px !important;
      radius: 0 !important;
    }
    .alert-wrapper .breadcrumbs.container-fluid.group-epics-roadmap {
      padding: 0px !important;
      max-width: 100% !important;
      .breadcrumbs-container {
        margin-top: 0px !important;
        radius: 0px !important;
        border: none !important;
      }
    }
  }

  // * Epics :: Roadmap.
  .roadmap-container {
    radius: $radius;
    colors: 0 0 $dark;
    box-shadow: 0 0 0 1px inset $darker;
    // Timeline header.
    .timeline-header-blank {
      colors: $lighter, $darker, $dark;
      box-shadow: -1px 0 0 0 inset $darker;
      &::before { colors: 0, $darker, 0; }
      &::after { background: linear-gradient(to right, $shadow 0%, $t 100%); }
    }
    // Timeline items.
    .timeline-header-item {
      colors: 0, $darker, $dark;
      .item-label { colors: $light, $darker, 0; }
      .item-sublabel .sublabel-value { colors: $lighter, 0, 0; }
      .item-sublabel .label-bold { colors: $light, 0, 0; }
    }
    // Background cells.
    .epics-list-section {
      colors: 0, 0, $darker;
      // Left sidebar cells.
      .epic-details-cell {
        colors: 0, $darker, $dark;
        box-shadow: -1px 0 0 0 inset $darker;
        &::after { background: linear-gradient(to right, $shadow 0%, $t 100%); }
        .epic-url { colors: $light, 0, 0; }
        .epic-timeframe { colors: $lighter, 0, 0; }
      }
      .epics-list-item {
        &:hover .epic-timeline-cell { colors: 0, $darker, $dark; }
        .epic-timeline-cell { colors: $light, $dark, 0; }
        // ? Bars.
        .timeline-bar {
          colors: 0, 0, $blue;
          &.start-date-undefined { background: linear-gradient(
            to right, #55555510 0%, #55555530 50%, $blue 100%
          ) !important; }
          &.end-date-undefined { background: linear-gradient(
            to right, $blue 0%, #55555530 50%, #55555510 100%
          ) !important; }
        }
      }
    }
  }

  // * Dashboard :: Page title.
  .page-title-holder {
    border: none;
    margin-top: 10px;
    .page-title { margin: 0; }
    // Found on: .../dashboard/milestones
    .project-item-select-holder { overflow: unset; }
  }

  // * Dashboard :: Project navigation.
  .nav-block .nav-links {
    padding: 0 8px;
    radius: $radius;
    border: 1px solid;
    colors: 0, $darker, $dark;
  }

  // * Navigation.
  .nav-links {
    border-bottom: none;
    // Links.
    li {
      a { colors: $lighter, 0, 0; }
      // On focus.
      > a:focus { colors: $light, $darker, 0; }
      // Active tabs.
      &.active > a, &:hover > a:not(.btn-missing) { colors: $light, $accent, 0; }
      // Badges.
      a .badge.badge-pill { colors: $lighter, 0, $darker; }
      // Make sure the button stays green.
      a.btn.btn-success { colors: var(--white), $green, $green; }
    }
  }

  /* Nav section. TODO: Fix the fixed layout version. */
  .container-fluid > .top-area {
    margin-top: 10px;
    padding: 0 10px;
    radius: $radius;
    border: 1px solid var(--darker);
    background-color: var(--dark);
    // * Groups :: Search form.
    &.group-nav-container .group-search {
      padding: 8px 0;
      margin-bottom: -10px;
      border: none;
      .group-filter-form { margin-right: 0px; }
      + .scrolling-tabs-container {
        padding: 0px !important;
        margin: 0px !important;
        border: none !important;
      }
    }
  }
  /* Wrapper. */
  .top-area,
  .wiki-page-header {
    border-bottom-color: var(--darker);
    // Remove unnecessary height.
    > .nav-text { line-height: unset; }
  }


  // * Settings -> Billings.
  .billing-plans {
    [class*="card"] {
      colors: 0 0 $dark;
      .card-header {
        radius: $radius $radius 0 0;
        colors: $white 0 $accent;
      }
      .card-body {
        radius: 0 0 $radius $radius;
        // Items.
        .feature-list li { colors: 0 0 $t }
        // Pricing.
        .price-per-month { colors: $accent 0 0 }
        .price-per-year { colors: $green 0 0 }
        // Borders.
        ../.current .card-body { colors: 0 $accent 0 }
        ../:not(.current) .card-body { colors: 0 $darker 0 }
      }
    }
  }

  // * Purchases.
  // ? https://customers.gitlab.com/customers/sign_up
  .sign-in .white-card {
    color: $light;
    border-color: $darker;
    background-color: $dark;
    .title { colors: 0, $darker, $darker; }
    label { color: $lighter; }
    select, option, input {
      colors: $lighter, 0, $darker;
      box-shadow: none;
    }
    // Select one.
    .chosen-container {
      // Results.
      .chosen-drop {
        colors: 0, $darker, $darker;
        box-shadow: none !important;
        .chosen-results {
          colors: $light, 0, 0;
          .highlighted {
            colors: $light, 0, $accent;
            background-image: none;
          }
        }
      }
      // Input element.
      .chosen-single {
        min-height: 34px;
        line-height: 34px;
        colors: $light, $darker, $darker;
        background-image: none !important;
        box-shadow: none !important;
        // Caret position.
        b { background-position: 0px 6px !important; }
      }
    }
    // Register button.
    .btn-gitlab {
      colors: 0, $darker, 0;
      transition: all 0.2s ease-in-out !important;
      &:hover, &:focus, &:active { colors: $light, $accent, $accent; }
    }
  }

  /* SSH Keys. */
  // pre,
  code.key-fingerprint { colors: $lighter, $darker, $darker; }


  // * Projects list.
  // TODO move groups to appropriate place. Possible refactor.
  .projects-list, #groups .content-list {
    margin: 10px 0;
    radius: $radius;
    box-shadow: inset 0 0 0 1px $darker;
    background-color: $dark;
    // Items.
    > li {
      padding: 8px;
      transition: all 0.15s ease-in-out;
      border-bottom-color: $darker;
      // On event.
      &:hover { background-color: $darker; }
      // ? Round corners on first and last child.
      &:first-child { radius: $radius $radius 0 0; }
      &:last-child { radius: 0 0 $radius $radius; }
    }
    // ? List items from 11.8 update?
    .project-row {
      padding: 8px;
      // TODO Turn this into a global and/or custom setting?
      .avatar-container.s64 {
        min-width: 48px !important;
        min-height: 48px !important;
        height: 48px !important;
        width: 48px !important;
      }
      // Project metadata.
      .metadata-info, .updated-note {
        colors: $lighter, 0, 0;
        time { color: $lighter; }
      }
      // Project stats and icons.
      .icon-container > .icon-wrapper {
        colors: $lighter, 0, 0;
      }
    }
  }

  // * Avatars.
  .avatar {
    &, &-circle, &-container, &-tile {
      radius: var(--avatar) !important;
      border: none !important;
      colors: $lighter, 0, $t;
    }
  }
  // TODO Prolly not needed anymore.
  .identicon { color: $lighter; }
  // ? Drop some shadow; use transparent avatars for best results.
  img.avatar { filter: drop-shadow(1px 1px 2px $shadow); }
  // ? Replace default background for project avatars and fix the height.
  .avatar:not(img) {
    colors: 0, 0, $darker;
    transition: 0.15s ease-in-out !important;
  }
  // ? Hover event for content list and group list.
  li:not(.group-row), .group-row-contents {
    &:hover .avatar:not(img) {
      colors: 0, 0, $dark;
    }
  }

  // * Project details.
  .project-details {
    .text-plain {
      color: $light;
      &:hover { color: $lighter; }
    }
  }
  .project-stats .nav .stat-link {
    color: $light;
    &:hover { color: $lighter; }
  }
  .user-access-role { colors: $light, $dark, $darker; }

  // * Project :: Empty project.
  .project-empty-note-panel {
    border-color: $darker;
    // Remove unnecessary margins.
    hr { margin: 0px; }
    .prepend-top-20:last-child { margin-top: 0px; }
  }
  .git-empty pre.bg-light { colors: $light, $darker, $dark; }
  // Empty wiki homepage.
  .project-show-wiki .landing {
    radius: $radius;
    colors: $red, $darker, $dark;
  }
  // ? Remove margin-top from buttons nav buttons.
  .project-empty-note-panel + .project-buttons {
    .btn { margin-top: 0px; }
    // Prevent buttons clipping on hover.
    .scrolling-tabs {
      margin-top: 0;
      padding-top: 8px;
    }
  }

  // * Project :: New fork.
  .fork-thumbnail-container {
    .fork-thumbnail {
      colors: 0, $darker, $dark;
      transition: 0.2s ease;
      &:hover { colors: 0, $dark, $darker; }
    }
  }

  // * Project :: Commits.
  #commits-list {
    .commit-list {
      .commit {
        padding: 8px;
        a { colors: $light, 0, 0; }
      }
    }
    // Header.
    .commit-header {
      display: inline-flex;
      radius: $radius;
      border: 1px solid;
      colors: 0, $darker, $dark;
      .day { padding-right: 1ch; }
      // Have some whitespace between days.
      &:not(:first-child) { margin-top: 10px; }
      // Separate every first commit from the header.
      + .commits-row { margin-top: 10px; }
    }
    // List.
    .commits-row {
      position: relative;
      margin-bottom: 8px;
      radius: $radius;
      colors: 0, 0, $dark;
      box-shadow: inset 0 0 0 1px $darker;
      &::before {
        content: "";
        height: 10px;
        position: absolute;
        top: -10px; left: 28px;
        border-left: 1px solid $darker;
      }
    }
  }
  .commit {
    colors: 0, $darker, 0;
    a {
      colors: $light, 0, 0;
      // Center avatar.
      img {
        margin-top: 3px;
        margin-left: 3px;
      }
    }
    // Link.
    .gfm { colors: $accent, 0, 0; }
    // Commit sha.
    > .commit-detail { align-items: center; }
    // Commit description.
    [class*="-description"] {
      padding-left: 16px;
      border-width: 4px;
      colors: $lighter, $darker, 0;
    }
  }

  // Show commit message.
  .text-expander {
    colors: $light, $darker, $dark;
    transition: 0.2s ease-in-out;
    &.open, &:hover { colors: 0, $accent, $darker; }
    &:focus { outline: none; }
  }

  // * Project :: Commit details page.
  // https://gitlab.com/vednoc/dark-gitlab/commit/9caacd40
  .commit-box {
    margin-bottom: 10px;
    padding: 8px;
    radius: $radius;
    colors: 0, $t, $dark;
    box-shadow: inset 0 0 0 1px $darker;
    [class*="-description"] {
      padding-left: 16px;
      border-width: 4px;
      colors: $lighter, $darker, 0;
    }
  }

  // * Project :: Branches.
  .all-branches {
    > .branch-item {
      padding: 8px;
      colors: 0, $darker, 0;
      .branch-title a { colors: $light, 0, 0; }
      .branch-commit {
        colors: $lighter, 0, 0;
        .commit-sha {
          font-size: 100%; // ! Why smaller font? @upstream
          colors: $accent, 0, 0;
        }
        time, .commit-row-message { colors: $lighter, 0, 0; }
      }
    }
    .btn.btn-default { colors: $light, 0, 0; }
    .btn.btn-remove { colors: var(--white), $red, $red; }
    .branch-commit .commit-row-message { colors: $light, 0, 0; }
    // Select next sibling and reset the colors.
    & + .card-footer.text-center {
      colors: $light, 0, $t;
      a { colors: $lighter, 0, 0; }
    }
  }
  // Graph color.
  .divergence-graph {
    .graph-side [class*="bar"] { colors: 0, 0, $lighter; }
    .graph-separator { colors: 0, 0, $lighter; }
    [class*="count"] { colors: $light, 0, 0; }
  }
  .content-list li {
    colors: 0, $darker, 0;
    // TODO Find a better way to style this selector.
    a:not(.btn-warning):not(.btn-danger):not(.page-link) { color: $accent !important; }
  }
  // ? Active/Stale/All pages.
  [data-page="projects:branches:index"] {
    .row-content-block + .all-branches,
    .top-area.adjust + .all-branches {
      margin-top: 10px !important;
      radius: $radius;
      colors: 0, 0, $dark;
      box-shadow: inset 0 0 0 1px $darker;
      li {
        transition: 0.1s ease;
        &:hover { colors: 0, 0, $darker; }
        &:first-child { radius: $radius $radius 0 0; }
        &:last-child { radius: 0 0 $radius $radius; }
      }
    }
  }

  // * Project :: Tags.
  .tags > ul {
    margin-top: 10px;
    radius: $radius;
    colors: 0, 0, $dark;
    box-shadow: inset 0 0 0 1px $darker;
    li.flex-row {
      padding: 10px;
      transition: 0.2s ease;
      .branch-commit {
        &, .js-timeago, .commit-row-message {
          colors: $lighter, 0, 0;
        }
      }
      .item-title.ref-name { colors: $light, 0, 0; }
    }
  }
  // * Project :: Tags -> Details.
  .multi-line .branch-commit {
    .commit-row-message { colors: $lighter, 0, 0; }
    .commit-sha { colors: $accent, 0, 0; }
  }

  // * Project :: Contributors.
  [id*="contributors"] {
    .person .spark, .tint-box {
      radius: $radius;
      colors: $light, 0, $dark;
      box-shadow: 0 0 0 1px $darker;
      .axis {
        .domain, line { stroke: $lighter; }
        text { fill: $light; }
      }
      // Graph colors.
      .area { fill: $green !important; }
      .area-contributor { fill: $orange !important; }
    }
  }

  // * Project :: Graph.
  .project-network {
    margin-top: 10px;
    overflow: hidden;
    radius: $radius;
    border-color: $darker;
    .controls { colors: 0, $darker, $dark; }
    // Graph background.
    .network-graph {
      fill: var(--light);
      background-color: var(--dark);
      // Hover over commit.
      > svg rect[rx*="4"][ry*="4"] {
        fill-opacity: 1 !important;
        stroke: $t;
        fill: $darker;
      }
      [d^="M185"], [d^="M195"] {
        fill-opacity: 1 !important;
        fill: $darker;
      }
      // Text color.
      > svg text { fill: $light; }
      // Sidebar.
      > svg rect[width*="40"], > svg rect[width*="30"] {
        stroke-width: 0;
        stroke: var(--darker);
        fill: var(--darker);
      }
    }
  }
  // ? Fix conflicting styles in the navigation.
  [data-page="projects:network:show"] .row-content-block {
    padding: 0;
    border: 0;
  }


  // * Global -> Modals.
  .modal, .modal-doorkeepr-auth {
    .modal-content {
      border: none;
      colors: 0, 0, $dark;
      box-shadow: 0 0 0 1px $darker !important;
      .modal-header { colors: 0 $darker $dark }
    }
    .modal-body {
      radius: 0 0 $radius $radius;
      colors: 0, 0, $dark;
      [class*="-subheader"] { colors: 0 $darker 0 }
      // ? Shortcuts modal.
      .shortcut-mappings {
        th { colors: $light 0 0 }
        td { colors: $lighter 0 0 }
        kbd { colors: $light 0 $darker }
      }
      // ? Board scope.
      .title { colors: $lighter, 0, 0; }
      .block + .block { border-top-color: $darker; }
      // Actions in auth.
      .form-actions {
        radius: 0 0 $radius $radius;
        border: none !important;
        border-top: 1px solid $darker !important;
      }
    }
    .modal-footer { colors: 0, $darker, 0; }
  }

  // * Global -> Close buttons.
  .close {
    transition: 0.15s ease;
    text-shadow: 0 1px 0 $shadow;
    &, &:hover, &:focus { colors: $lighter 0 0 }
    // Non-modal.
    &:not(:disabled):not(.disabled) .fa {
      text-shadow: 0 1px 0 $shadow;
      transform: translateY(-5px);
    }
  }

  // * Project -> Blame.
  [data-page*="projects:blame"] {
    .blame table {
      radius: none;
      box-shadow: none;
    }
    .blame-commit {
      position: relative;
      border-left: none !important;
      &::before {
        content: "";
        position: absolute;
        top: 0;
        left: 0;
        bottom: 0;
        width: 4px;
      }
    }
    // ? Custom colors!
    for i in 0..9 {
      .legend-box-{i},
      .blame-commit-age-{i}::before {
        colors: 0 0 $green;
        opacity: s(("0.%s99"), i);
      }
    }
  }

  // * Project -> Various resets.
  .file-holder {
    .file-fork-suggestion { colors: 0, $darker, $dark; }
    .table-responsive { radius: 0 !important; }
    .file-blame-legend { colors: 0, $darker, $dark; }
    .file-content.blame tr { border-bottom-color: $darker; }
    // ? Search file names; official GitLab solution.
    .file-title a:not(.btn) { colors: $lighter, 0, 0; }
  }

  // * Latest commit.
  .info-well {
    > .well-segment {
      padding: 6px 8px;
      // https://gitlab.com/vednoc/dark-gitlab/commit/9caacd40
      &:not(:last-of-type) { colors: 0, $darker, 0; }
    }
    // Deleted branch.
    // ! Might not be necessary anymore.
    .limit-box {
      radius: $radius;
      colors: 0, 0, $t;
      box-shadow: inset 0 0 0 1px $red;
    }
  }

  // * GPG button.
  .gpg-status-box {
    &.valid { colors: $green, $green, $dark; }
    &.invalid, &.invalid:focus { colors: $light, $darker, $dark; }
  }

  // * Popover.
  .popover {
    colors: 0, $darker, $dark;
    box-shadow: 0 4px 8px 0 $shadow;
    &-header { colors: 0, $darker, $darker; }
    &-body { colors: $light, 0, 0; }
    &[class*="-top"] {
      .arrow::before { border-top-color: $darker !important; }
      .arrow::after { border-top-color: $dark !important; }
    }
    &[class*="-bottom"] {
      .arrow::before { border-bottom-color: $darker !important; }
      .arrow::after { border-bottom-color: $dark !important; }
    }
    &[class*="-left"] {
      .arrow::before { border-left-color: $darker !important; }
      .arrow::after { border-left-color: $dark !important; }
    }
    &[class*="-right"] {
      .arrow::before { border-right-color: $darker !important; }
      .arrow::after { border-right-color: $dark !important; }
    }
    .gpg-popover-user-link { colors: $light, 0, 0; }
    // Milestones.
    .milestone-popover-body { colors: 0, 0, $t; }
    .milestone-popover-footer { colors: 0, $darker, 0; }
  }

  // * Commits :: Diff summary.
  .diff-files-changed {
    radius: $radius;
    padding: 2px;
    margin: 10px 0;
    border: 1px solid;
    colors: 0, $darker, $dark;
    // Wrapper.
    > .files-changed-inner {
      display: flex;
      flex-flow: row-reverse wrap;
      // Center buttons vertically.
      .inline-parallel-buttons {
        float: none;
        padding: 3px 5px 5px 5px;
        // Hide buttons on small resolution.
        @media (max-width: 885px) { display: none !important; }
      }
      // ? "Showing x changed..."
      // TODO: Remove if/when they fix it.
      .commit-stat-summary {
        margin: auto auto auto 0;
        padding: 0 8px;
        colors: 0, 0, $t;
      }
    }
  }
  // * Diff file.
  .diff {
    radius: $radius;
    // Collapsed file content.
    &-file .diff-content {
      radius: 0 0 $radius $radius;
      colors: $light, 0, $dark;
      .btn-link { colors: 0, $t, 0; }
      // Gems/Packages/etc.
      .code .line a { color: $accent; }
      // Added lines.
      .new .idiff, .idiff.addition {
        padding: 0 2px;
        background-color: #55ff5520 !important;
      }
      // Removed lines.
      .old .idiff, .idiff.deletion {
        padding: 0 2px;
        background-color: #ff555520 !important;
      }
    }
    // ? Fix borders on expanded sections.
    &-expanded { & > td, & + :not(&) > td { colors: 0, $darker, 0; }}
  }

  // Image background.
  // TODO refactor with other file-holder related elements.
  .diff-content .file-container,
  .file-holder .file-content.image_file,
  .file_holder .file-content.video,
  .file-holder .file-content.blob-no-preview {
    background-color: $dark;
  }

  // * Diff :: Image.
  .diff-file {
    .image { colors: 0, 0, $t; }
    .frame {
      colors: 0, 0, $darker;
      // Added/removed images.
      &.deleted { colors: 0, $red, 0; }
      &.added { colors: 0, $green, 0; }
      // Broken image.
      img {
        background: none !important;
        colors: $light, $t, 0;
      }
    }
    .view-modes {
      colors: 0, 0, $t;
      ul li {
        colors: $light, $darker, 0;
        &.active { colors: $accent, 0, 0; }
      }
      // ? Remove empty element's border.
      + .note-container { border-top: none; }
    }
  }
  // * Add note.
  // TODO move to a more appropriate place.
  .note-container {
    colors: 0, $darker, $dark;
    // Reply background.
    .new-note, .discussion-notes {
      colors: 0, $darker, 0;
    }
  }
  // Show/hide replies.
  .replies-toggle {
    colors: $lighter, $darker, $dark;
    &:hover span { colors: $light, 0, 0; }
  }
  /* Text color. */
  .branch-info > span {
    color: $light !important;
  }
  /* Badge. */
  .info-well .badge.badge-gray {
    color: $light;
    border: 1px solid $darker;
    background-color: $dark;
  }
  // TODO move to the global styles.
  .badge.badge-gray, .label.badge-gray { colors: 0, 0, $darker; }


  // * Timeline content.
  .timeline-entry {
    colors: $light, $darker, $t;
    &.outlined { colors: 0, 0, $dark; }
    // Entry borders.
    .timeline-entry-inner { colors: 0, $darker, 0; }
    // Highlighted entry.
    &:target, &.target {
      radius: $radius $radius 0 0;
      colors: 0 0 $darker;
      box-shadow: 0 0 0 1px $darker;
    }
    // Fix button background.
    .note-action-button { colors: 0, 0, $t; }
  }

  // * Note.
  .note {
    // Profile links.
    .note-headline-light { colors: $lighter, 0, 0; }
  }
  .discussion-header { colors: 0, $darker, 0; }
  // ? Fix for discussion's rounded corners in MR.
  // TODO: Explore merging this with `.card, .card-slim` selectors.
  .discussion-wrapper {
    radius: $radius;
    [class$="notes"] .replies-toggle:last-child { radius: 0 0 $radius $radius; }
  }
  /* Text color. */
  .discussion-headline-light,
  .note-headline-meta .system-note-separator {
    text-decoration: none !important;
    color: var(--light);
    time { color: $lighter; }
  }
  // * Notes.
  // #notes {
  //   radius: $radius;
  //   border: 1px solid;
  //   colors: 0, $darker, 0;
  // }
  .notes {
    colors: 0 0 $t;
    // Text color.
    // ? Be super-specific because they're using !important on upstream. :p
    > li .note-body div.note-text blockquote p,
    > li .note-body div.note-text .blockquote p {
      colors: $light, 0, 0;
    }
    a, a:hover { color: $accent !important; }
    // ? MR -> Notes -> Suggestions.
    .md-suggestion-header {
      radius: $radius $radius 0 0;
      colors: $light, $darker, $dark;
      + table.code { radius: 0 0 $radius $radius; }
    }
    // Added/removed/changed things in discussions.
    .idiff.addition { background-color: #55ff5520 !important; }
    .idiff.deletion { background-color: #ff555520 !important; }
  }

  // * Issues :: Conversation notes.
  [id^="notes"] {
    // ? Note background, avoid system notes and forms.
    .notes.timeline > .timeline-entry:not(.system-note):not(.note-form) {
      border-radius: $radius;
      background-color: $dark !important;
    }
    // Icon.
    .system-note .timeline-icon {
      border: 1px solid $darker;
      background-color: $dark;
    }
    // ? Issue description.
    .detail-page-description > script + div {
      padding: 8px;
      border-radius: $radius;
      border: 1px solid $darker;
      background-color: $dark;
    }
  }
  // The line connector.
  .main-notes-list::before {
    border-left-width: 1px !important;
    colors: 0, $darker, 0;
  }

  // * Issues :: New note.
  .common-note-form .md-area {
    radius: $radius;
    colors: 0, $darker, $dark;
    &.is-focused { box-shadow: 0 0 0px 2px $shadow !important; }
    // Input area.
    .markdown-area { colors: $light, $darker, $dark; }
    // Use code font for input area.
    .note-textarea { font-family: var(--code-font); }
    // When you use commands.
    .referenced-commands { colors: 0, 0, $darker; }
    // Navigation.
    // ? New design; find old styles and bring them here for backwards compatability.
    .nav-links:not(.quick-links) {
      colors: 0, $darker, 0;
      li:not(.md-header-toolbar) {
        button { colors: $lighter, 0, 0; }
        &.active button { colors: $light, $accent, 0; }
      }
    }
  }
  /* Toolbar colors. */
  .edited-text,
  .comment-toolbar {
    color: $light;
    border-color: $darker !important;
  }

  // * Markdown selectors.
  .md {
    colors: $light, $dark, 0;
    a { colors: $accent, 0, 0; }
    h1, h2 { colors: 0, $darker, 0; }
    hr { colors: 0 $darker 0; }
    pre {
      &:not([class]) {
        radius: $radius;
        colors: $light $darker $dark;
        code { colors: 0 $t $t }
      }
      &.code {
        radius: $radius;
        colors: 0 $darker $dark;
      }
    }
    p > code {
      border: 1px solid !important;
      colors: $light, $darker, 0;
    }
    blockquote {
      padding-left: 16px !important;
      border-left: none !important;
      box-shadow: inset 4px 0 0 0 $darker !important;
      &[dir="auto"] p { colors: $lighter 0 0 }
    }
    img:not(.emoji) {
      border: none;
      radius: $radius;
      colors: 0, $darker, $dark;
      transition: 0.15s ease;
      opacity: faded_img;
      &:hover { opacity: 1; }
    }
    ul, ol { ul, ol { margin-bottom: 0 !important }}
  }
  /* Attach a file. */
  .markdown-selector { color: $accent; }
  /* Fullscreen (zen) mode. */
  .zen-backdrop.fullscreen {
    colors: 0, 0, $darker;
    textarea {
      margin-top: 5px;
      radius: $radius;
      colors: $light, 0, $dark;
    }
  }


  // * Left sidebar.
  .nav-sidebar {
    colors: 0, 0, $dark;
    box-shadow: 1px 0 0 0 $darker;
    .divider { colors: 0, $darker, 0; }
    // Hide scrollbar.
    ::-webkit-scrollbar { display: none !important; }
    // Links.
    li a, .context-header > a {
      colors: $light, 0, 0;
      &:hover, &:focus { colors: 0, 0, $darker; }
    }
    // ? Fix overflow and height on Firefox.
    .nav-sidebar-inner-scroll {
      height: calc(100% - 44px);
      overflow-x: hidden !important;
      > ul.sidebar-top-level-items { margin-bottom: 0; }
    }
    // ? Styles for submenus.
    .nav-sidebar-inner-scroll > ul.sidebar-top-level-items {
      // Active submenu.
      > li.active:not(.fly-out-top-item) {
        colors: 0, 0, $t;
        box-shadow: 4px 0 0 0 inset $darker, 1px 0 0 1px inset $darker;
        // Group name.
        > a {
          transition: 0.15s ease;
          colors: $accent, 0, 0;
          svg { fill: $accent !important; }
        }
        // Active list items.
        .sidebar-sub-level-items li:not(.fly-out-top-item) {
          transition: 0.15s ease;
          &.active, &:hover {
            colors: $lighter, 0, $darker;
            box-shadow: 4px 0 0 0 inset $accent;
            // Remove unnecessary background.
            a { colors: $light, 0, $t; }
          }
        }
      }
      // Fly-out menu.
      > li:not(.fly-out-top-item).is-showing-fly-out .sidebar-sub-level-items {
        radius: 0 $radius $radius 0;
        colors: 0, $darker, $dark;
        box-shadow: 2px 2px 2px 0 $shadow;
      }
      > li.is-over > a { colors: 0, 0, $darker; }
      // Badges.
      .badge.badge-pill { colors: $lighter, $darker, $dark; }
      > li:hover, > li.active {
        .badge.badge-pill { colors: $light, $dark, $darker; }
      }
      // Remove unnecessary margin on items.
      a {
        margin-right: 0;
        transition: 0.15s ease;
      }
    }
    // ? Resets for collapsed sidebar.
    &.sidebar-collapsed-desktop {
      ul.sidebar-top-level-items > li.active:not(.fly-out-top-item) {
        colors: 0, 0, $darker;
        box-shadow: 4px 0 0 0 inset $accent !important;
      }
      ul.sidebar-sub-level-items > li:not(.fly-out-top-item) {
        box-shadow: none !important;
      }
    }
    // ? Collapse button.
    .toggle-sidebar-button {
      height: 44px;
      width: 100%;
      transition: 0.15s ease !important;
      colors: 0, $darker, $dark;
      .collapse-text {
        transition: 0.15s ease;
        colors: $lighter, 0, 0;
      }
      &:hover {
        colors: 0, 0, $darker;
        .collapse-text { colors: $light, 0, 0; }
      }
    }
  }

  // * Right sidebar.
  .right-sidebar {
    colors: 0, $darker, $dark;
    // Separators.
    .block, .gutter-toggle { colors: 0, $darker, 0; }
    // Progress in milestones.
    .progress { colors: 0, 0, $darker; }
    // Text color.
    a:not(.btn):hover, .btn-link:not(.btn):hover { colors: $accent, 0, 0; }
    .no-value, .sidebar-collapsed-icon { colors: $light, 0, 0; }
    .title {
      colors: $lighter, 0, 0;
      // Edit buttons.
      > a, > button { colors: $lighter, 0, 0; }
    }
    // Button background.
    .js-move-issue:hover { colors: 0, 0, $darker; }
    // Adjust sidebar width.
    &.right-sidebar-collapsed {
      width: 50px;
      .block {
        width: 50px;
        transition: 0.15s ease;
        &:hover { colors: 0, 0, $darker; }
      }
      .gutter-toggle { height: 55px; }
    }
    // Expanded state should take more available space.
    &.right-sidebar-expanded .issuable-sidebar {
      padding: 0 16px;
      .block {
        width: 100%;
        max-width: 264px;
      }
    }
  }

  // * Project :: Wiki.
  [data-page*="wikis:show"] #content-body {
    // Wiki sidebar.
    .right-sidebar.wiki-sidebar {
      a { color: $light; }
      a:hover { color: $accent; }
      .active > a { color: $accent; }
    }
    // Text colors.
    .wiki-last-edit-by {
      colors: $lighter, 0, 0;
      > strong { colors: $light, 0, 0; }
    }
  }

  // * Project :: Wiki -> Mermaid.
  .mermaid {
    max-width: var(--width) !important;
    padding-bottom: 10px;
    radius: $radius;
    colors: 0, 0, $dark;
    box-shadow: inset 0 0 0 1px $darker;
    // Boxes.
    rect.actor { isvg: $darker, $darker; }
    text.actor { isvg: $light, 0; }
    // Connectors.
    .actor-line { isvg: 0, $darker; }
    // Notes.
    .note { isvg: $darker, $accent; }
    .noteText, .noteText tspan { isvg: $lighter, 0; }
    // Lines.
    .message {
      &Text { isvg: $lighter, 0; }
      &Line0 { isvg: 0, $darker; }
    }
  }

  // * Project :: Project/group home panel.
  .project-home-panel, .group-home-panel {
    margin-top: 8px;
    colors: 0, $t, 0;
    // Shorten description.
    .project-description { max-width: 60em; }
    // Text color.
    .text-secondary, .count, .star-count { colors: $lighter 0 0; }
    // Remove button backgrounds.
    .js-read-more-trigger { colors: $accent, 0, $t; }
    // Metadata text color.
    .project-metadata * {
      colors: $lighter, 0, 0;
      // Reset license button.
      &.project-license .btn {
        transform: translate(0) !important;
        background: 0 !important;
      }
    }
    // Project tags.
    .home-panel-topic-list {
      colors: 0, $darker, 0;
      // Fix line height.
      .text-nowrap { line-height: 22px !important; }
      svg.icon { margin-top: 4px !important; }
      .badge:hover { colors: $light, 0, $darker; }
    }
    // ? New design, December 2018.
    .add-license-link { colors: $lighter, 0, 0; }
    .project-stat-value { colors: $light, 0, 0; }
    .project-stats .stat-text { colors: $lighter, 0, 0; }
    .project-repo-buttons {
      .count-badge, .btn-xs { height: 34px; }
      // Buttons.
      .count-badge-count { colors: 0, $darker, $dark; }
      .count-badge-button { colors: $light, $darker, $darker; }
    }
  }
  // "Auto DevOps enabled" and possibly other buttons.
  .project-buttons .stat-text {
    colors: $light, $darker, $dark;
    transition: transform 0.2s ease-in-out;
    &:hover { transform: translateY(-2px); }
  }

  // * Arrows.
  // ? Old GitLab design.
  .count-with-arrow {
    .count { colors: 0, $darker, $dark; }
    .arrow::before { border-right-color: $darker !important; }
    .arrow::after { border-right-color: $dark !important; }
  }
  // * Remove unnecessary whitespace.
  .project-home-panel {
    padding: 0;
    // Badges.
    .prepend-top-default { margin: 10px 0 10px 0 !important; }
    // Project description.
    .project-home-desc, .project-description > div > p { margin: 0 0 10px 0 !important; }
    // Buttons under badges.
    .project-repo-buttons {
      // Reset margins on all items.
      > div { margin: 0; }
      // Apply margins on the child elements.
      // ? Inline is needed for specificity.
      > div > div:not(:last-child), > div > .inline { margin: 0 8px 8px 0 !important; }
      // Right margin for buttons.
      .project-action-button { margin: 0 !important; }
    }
  }
  // * Separator.
  .project-stats {
    border-bottom: none;
    colors: 0, $darker, 0;
    // ? Reset background-color on project page.
    .progress { colors: 0, 0, $t; }
  }
  // Language bar.
  .progress.repository-languages-bar {
    background-color: $t !important;
    .progress-bar {
      radius: 8px;
      // ? Fixes buggy bar in `.../vednoc/better-comments` repo.
      flex-grow: 1;
      // Apply right margin to all but the last child.
      &:not(:last-child) { margin: 0 4px 0 0 !important; }
      // Crystal language color.
      &[style$="#776791"] { background: #000000 !important; }
    }
  }

  // * Project :: Settings.
  .settings {
    border-bottom-color: $darker;
    // ? Fix border colors.
    + .settings { colors: 0, $darker, 0; }
    // ? Standalone sections i.e. under Advanced.
    .sub-section {
      radius: $radius;
      colors: 0, $darker, $dark;
    }
    .bs-callout-info {
      position: relative;
      border: none;
      colors: $lighter, $dark, $dark;
      box-shadow: 0 0 0 1px inset $darker;
      &::before {
        content: "";
        position: absolute;
        top: 0px; left: 0; bottom: 0px;
        radius: $radius 0 0 $radius;
        border-left: $radius solid $blue;
      }
    }
    .project-visibility-setting {
      radius: $radius $radius 0 0;
      colors: $light, $darker, $dark;
    }
    .project-feature-settings {
      radius: 0 0 $radius $radius;
      colors: 0, $darker, $dark;
    }
    // Protected tag card.
    .protected-tags-list > .card-header { colors: $light, $darker, $darker; }
  }
  // ? Service Desk -> Forward...
  #js-service-desk .card-header {
    radius: $radius;
    colors: $orange, $darker, $darker;
    h3 {
      margin: 0.75rem 0;
      font-size: 1.0rem;
    }
  }

  /* Latest commit message.. */
  .info-well {
    margin-bottom: 0;
    radius: $radius;
    colors: $light, $darker, $dark;
    a { color: $lighter; }
  }

  /* Commit button. */
  .commit-sha-group .label,
  .commit-sha-group .btn {
    color: $light;
    border-color: $darker;
    background-color: $dark;
  }
  /* Fix background in upload files. */
  .commit-message-container { background: none; }
  /* Dropzone! */
  .blob-upload-dropzone-previews { border-color: $darker; }

  // * Project :: Badges.
  .project-badge { transition: 0.2s opacity ease-in-out; }
  [src*="for-the-badge"] { radius: $radius; }
  span.badge, button.badge, div.badge {
    // Remove unnecessary underline.
    &.color-label { text-decoration: none !important; }
    // Apply everywhere except on color-label elements.
    &:not(.color-label) { colors: var(--white), 0, 0; }
    // Hack for darkening background color.
    &:not(.badge-pill):not(.available) {
      // transition: 0.15s ease;
      box-shadow: inset 0 0 0 1rem #33333370;
      &:hover { box-shadow: none; }
      &[style*="color: #333333"] {
        color: #000000 !important;
        box-shadow: inset 0 0 0 1rem #33333325;
      }
    }
  }

   // * Project :: Readme.
  .file-holder {
    radius: $radius;
    colors: 0, $darker, $dark;
    // ? Header.
    .file-title {
      // @upstream https://gitlab.com/gitlab-org/gitlab-ce/merge_requests/25648
      border-top: 1px solid;
      &, &-flex-parent {
        padding: 8px 13px;
        radius: $radius $radius 0 0;
        colors: 0, $darker, $darker;
        // Buttons w/o `button-default` class.
        a { color: $lighter; }
        // Diff -> Renamed file names. #43
        .idiff {
          &.addition { background-color: #55ff5520 !important; }
          &.deletion { background-color: #ff555520 !important; }
        }
      }
    }
    // ? Body.
    .file-content { colors: 0, 0, $t; }
  }

  // * Wiki.
  .wiki:not(.use-csslab) {
    color: $light;
    a { color: $accent !important; }
    h1, h2 { colors: $lighter, $darker, 0; }
    > p > code { border: 1px solid $darker !important; }
    blockquote, .blockquote {
      padding: 8px 16px;
      border-left-width: 4px;
      colors: $light, $darker, 0;
      // Second rule solves blockquote weirdness in issues.
      p, * > p { colors: $lighter, 0, 0; }
    }
    pre { radius: $radius; }
    pre, code { colors: $lighter, $darker, $dark; }
    img:not(.emoji) {
      border: none;
      radius: $radius;
      colors: 0, $darker, $dark;
    }
    kbd {
      colors: $accent, $dark, $shadow;
      box-shadow: none;
    }
  }

  // * Project -> File table.
  .table-holder {
    radius: $radius;
    border: none !important;
    background-color: $dark;
    box-shadow: 0 0 0 1px inset $darker;
    // ? File holder.
    .tree-table tr {
      // ? Remove weird borders on file-holder.
      &, th { border: none !important; }
      // Cell background colors.
      th { colors: $light, 0, $darker; }
      td { transition: 0.15s ease; }
      // ? Table cells.
      &.tree-item {
        // Git submodules.
        span a { color: inherit; }
        // Text colors.
        .tree-time-ago, .tree-commit-link, .tree-item-file-name > *, time {
          transition: 0.15s ease;
          colors: $lighter, 0, 0;
        }
        // On event.
        &:hover, &.selected {
          > td { colors: 0, 0, $darker; }
          .tree-time-ago, .tree-commit-link, .tree-item-file-name > *, time {
            colors: $light, 0, 0;
          }
        }
      }
    }
    // ? Fixes `pre` element on project/pages/domains page.
    tbody pre { colors: $lighter, $darker, $dark; }
  }

  // Groups section.
  [data-page="users:show"] #groups {
    .content-list {
      margin-top: 10px;
      radius: $radius;
      colors: 0, 0, $dark;
      li {
        transitions: all 0.15s ease-in-out;
        &:hover { colors: 0, 0, $darker; }
      }
      .group-row .stats { color: $lighter; }
    }
  }
  // Text dividers.
  .middle-dot-divider::after { color: $lighter; }
  /* Fix master/history/find file/web ide section when low-resolution. */
  @media (max-width: 740px) {
    .nav-block {
      flex-direction: column;
    }
  }
  // ? Scrolling tabs.
  // body:not([data-page$="users:show"])
  :not(.user-profile) .scrolling-tabs-container {
    margin: 10px 0;
    padding: 0 10px;
    /* overflow: hidden; */
    radius: $radius;
    border: 1px solid $darker;
    background-color: $dark;
    .fade-right { background: linear-gradient(to left, $dark 45%, $t); }
    .fade-left { background: linear-gradient(to right, $dark 45%, $t); }
  }

  // Profile -> Shared styles.
  .user-profile {
    // User info cover.
    .cover-block {
      colors: 0, $darker, $dark;
      .cover-title { colors: $light, 0, 0; }
      .cover-desc { colors: $lighter, 0, 0; }
    }
    // ? Calendar in activity. @obsolete
    #activity > .row-content-block { colors: 0, $darker, $dark; }
    // Reset scrolling nav.
    .scrolling-tabs-container {
      margin: 0 !important;
      border: none !important;
    }
    // * Content list items.
    .content_list {
      radius: $radius;
      background-color: $dark;
      box-shadow: inset 0 0 0 1px $darker;
      .event-item { @media (max-width: 575px) {  padding-left: 8px !important; }}
    }
    // ? Overview -> Adjust whitewspace between panes.
    .row {
      @media (min-width: 980px) {
        .col-12 .append-bottom-default { margin-bottom: 0; }
        .col-lg-6:first-child { padding-right: 3px; }
        .col-lg-6:last-child { padding-left: 3px; }
      }
    }
    // ? Fix loader height.
    .overview-content-list > .loading  {
      &, > .loading {
        margin: 0 auto !important;
        height: 50px;
      }
    }
  }

  // * Profile -> Contribution calendar.
  svg.contrib-calendar > g {
    text { fill: $light; }
    rect { fill: $accent; }
    rect[fill="#ededed"] { opacity: 0.05; }
    rect[fill^="rgb(17"] { opacity: 0.20; }
    rect[fill^="rgb(12"] { opacity: 0.40; }
    rect[fill^="rgb(82"] { opacity: 0.65; }
    rect[fill^="rgb(37"] { opacity: 1; }
  }

  // ? Compact projects-list.
  .compact.projects-list {
    margin: 0 !important;
    colors: 0, 0, $t;
    box-shadow: none !important;
    .project-row {
      padding: 8px;
      &:first-child { radius: 0; }
      &:last-child { radius: 0 0 $radius $radius; }
    }
  }

  /* Text color. */
  .nothing-here-block,
  .calendar-hint,
  .content-list li {
    color: var(--light) !important;
  }
  .nothing-here-block { padding: 8px; }

  // * Project -> Activity.
  .project-activity {
    radius: $radius;
    background-color: $dark;
    box-shadow: inset 0 0 0 1px $darker;
    // Item.
    .event-item {
      padding: 8px 8px 8px 60px;
      colors: $light, $darker, 0;
      transition: background 0.15s ease-in-out;
      &:hover { background-color: $darker; }
      // Text color.
      a { color: $accent; }
      .event-title { color: $light; }
      .author_name a, strong a { color: $accent; }
      // Remove left padding on small resolution.
      @media (max-width: 575px) { padding-left: 8px; }
      // Avatar position.
      .user-avatar { margin-left: 8px; }
    }
  }

  // * Profile :: Overview.
  .user-profile {
    // Sections.
    [class*="col"] > [class*="-block"] {
      margin-top: 16px;
      radius: $radius;
      colors: 0, 0, $dark;
      box-shadow: inset 0 0 0 1px $darker;
      // Remove padding for titles.
      > [class*="-block"] {
        padding: 0;
        colors: 0, $red, $t;
        // Hide border in "activity" section. @obsolete
        &:first-child { colors: 0, $t, 0; }
        // Add padding.
        &.user-calendar { padding: 8px !important; }
      }
      .border-bottom {
        padding: 0 8px !important;
        // ? Select next sibling; it'll always be the content list.
        + div {
          padding-bottom: 0;
          box-shadow: 0 -1px 0 0 $darker;
        }
      }
      // ? Add some padding to empty blocks.
      .overview-content-list { padding-bottom: 1px; }
    }
    // ? Content list items.
    .event-item {
      colors: 0, $darker, 0;
      transition: 0.15s ease;
      padding: 8px 8px 8px 48px !important;
      .system-note-image { left: 12px; }
      .event-title {
        color: $light;
        // Branch/repo.
        a { color: $light; }
      }
      .author_name a { colors: $light, 0, 0; }
      .username { colors: $lighter, 0, 0; }
      .event-body {
        .commit-row-title .commit-sha { colors: $light, 0, 0; }
        &, .md p, .commit-row-title { colors: $light, 0, 0; }
      }
      &:first-child { radius: $radius $radius 0 0; }
      &:last-child { radius: 0 0 $radius $radius; }
      &:hover { colors: 0, 0, $darker; }
    }
  }

  // * Project :: Cycle Analytics.
  #cycle-analytics {
    // Introduction.
    .landing { colors: 0, $darker, $dark; }
    // Colors.
    .stage-nav {
      .stage-nav-item {
        colors: $light, $darker, $dark;
        &.active {
          colors: 0, $dark, $dark;
          box-shadow: inset 2px 0 0 0 $accent;
        }
        // Median.
        .stage-empty, .not-available { color: $lighter; }
      }
    }
    // Related issues.
    .stage-event-item {
      colors: $lighter, $darker, 0;
      .issue-link, .issue-date, .build-date { colors: $lighter, 0, 0; }
      .item-title a { colors: $light, 0, 0; }
    }
  }

  // * Dashboard -> Sub-nav block.
  .row-content-block {
    margin-top: 10px !important;
    padding: 8px;
    radius: $radius;
    border: 1px solid $darker;
    colors: $lighter 0 $dark;
    // Recently pushed to...
    &.white {
      border: 1px solid $darker;
      background-color: $dark;
    }
  }
  /* Sub-header found in compare branches. */
  .sub-header-block {
    border-color: $darker;
    background-color: transparent;
    // Commits list.
    + .card {
      border-radius: $radius !important;
      ul > li:last-child { radius: 0 0 $radius $radius; }
    }
  }
  // Sticky file name.
  .diff-file .is-compare { top: 88px !important; }
  /* There isn't anything to compare. */
  .card.bg-light {
    border: 1px solid var(--darker) !important;
    background-color: var(--dark) !important;
  }
  [class*="card"] > ul > li {
    transition: 200ms ease-in-out;
  }
  /* Text color. */
  .issuable-milestone,
  .issuable-info,
  .task-status,
  .issuable-updated-at {
    color: var(--light) !important;
  }
  /* Email button. */
  .issuable-email-modal-btn {
    color: var(--accent);
  }
  /* Merged/closed. */
  .merge-request.merged,
  .issue.merged,
  .merge-request.closed,
  .issue.closed {
    border-color: var(--darker);
    background-color: var(--dark);
  }
  // ? New issues.
  .issue.today {
    colors: 0, 0, $t;
    box-shadow: none;
    &::before {
      content: "";
      border-left: 4px solid $accent;
      position: absolute;
      top: 0; left: 0; bottom: 0;
    }
  }
  // ? Rounded corners for first list item.
  .issuable-list > li:first-child::before { radius: $radius 0 0 0; }
  // Items on hover.
  .card > ul > li {
    &:hover, &.done-reversible {
      colors: 0, 0, $darker;
    }
    .btn {
      colors: 0, 0, $dark;
    }
  }


  // * Dashboard :: Activity.
  .nav-block.activities {
    border-bottom: none;
    // ? Solves the conflicting styles.
    .scrolling-tabs-container {
      padding: 0;
      border: none;
      border-bottom: 1px solid var(--darker);
      .fade-right { right: 1px; }
    }
  }
  // * List.
  .activities {
    // Fix RSS icon.
    // TODO: Remove when fixed on upstream.
    .controls {
      margin: 20px 10px 0 10px;
      z-index: 9;
      position: absolute;
      top: 0; right: 8px;
      @media screen and (max-width: 640px) { display: none !important; }
    }
    // List.
    // ! Might need extra tweaking somewhere.
    .content_list {
      radius: $radius;
      colors: 0, 0, $dark;
      box-shadow: inset 0 0 0 1px $darker;
    }
    // Items.
    .event-item {
      padding: 8px 16px 8px 60px;
      colors: $light, $darker, 0;
      .event-title { color: $light; }
      a { color: $accent; }
      // Adjust avatars.
      .user-avatar { margin-left: 10px; }
    }
  }

  // * Dashboard :: Operations.
  .operations-dashboard {
    // Remove extra padding.
    > nav, > div { padding: 0 !important; }
    // "Search your projects" margin fix.
    .page-title-holder {
      margin-top: 16px;
      .append-bottom-default { margin-bottom: 0px !important; }
    }
    // Add projects.
    .tokenized-input-wrapper {
      radius: $radius;
      &.focus { box-shadow: 0 0 1px 1px $accent !important; }
      .tokenized-input { colors: $light, 0, $t; }
      .input-token, .tokenized-input-token-remove { colors: 0, 0, $darker; }
    }
    // Cards.
    .card {
      radius: $radius;
      .card-header { radius: $radius $radius 0 0; }
    }
  }

  // * Dashboard :: Top navigation.
  .top-area:not(.cover-block) {
    margin-top: 10px;
    padding: 0 8px;
    radius: $radius;
    border: 1px solid;
    colors: 0, $darker, $dark;
    // Remove overflow so the button animation can be seen properly.
    .project-item-select-holder.btn-group { overflow: visible; }
    // Found on: .../gitlab-org/gitlab-ce/tags/any-tag
    pre.wrap {
      radius: $radius;
      font-family: var(--code-font);
      colors: $lighter, $darker, $dark;
    }
    // ? Found in: ...project/pipeline_schedules
    + .card { margin-top: 10px; }
  }

  // * Dashboard :: Issues/MR page.
  [class$="-filters"] + .card {
    colors: 0, $t, $t;
    // List.
    .issuable-list {
      margin-top: 10px !important;
      radius: $radius;
      border: 1px solid;
      colors: 0, $darker, $dark;
      // Round corners.
      li {
        &:first-child { border-radius: $radius $radius 0 0; }
        &:last-child { border-radius: 0 0 $radius $radius; }
      }
      // Remove right padding on issues/merge requests page.
      .issuable-info-container { padding-right: 0; }
    }
  }

  // * Dashboard :: Todos page.
  .js-todos-all .card {
    margin-top: 10px;
    radius: $radius;
    colors: 0, 0, $t;
    // List wrapper.
    .todos-list {
      radius: $radius;
      border: 1px solid;
      colors: 0, $darker, $dark;
      // List items.
      .todo {
        border: none;
        // Reset borders and round corners.
        &:not(:first-child) { border-top: 1px solid $darker; }
        &:first-child { border-radius: $radius $radius 0 0; }
        &:last-child { border-radius: 0 0 $radius $radius; }
      }
    }
  }

  // * Issues :: List.
  .issues-holder {
    margin-top: 10px;
    radius: $radius;
    border: 1px solid $darker;
    background-color: $dark;
    // Email a new issue to this project.
    .issuable-footer {
      padding: 10px;
      border-top: 1px solid $darker;
    }
    li:not(.page-item) {
      transition: background 0.15s ease-in-out;
      &:first-child { radius: $radius $radius 0 0; }
      &:hover { background-color: $darker; }
    }
    // ? Reset pagination.
    .gl-pagination {
      padding: 10px;
      margin-top: 0px !important;
      border-top: 1px solid $darker;
      .pagination { margin: 0px; }
    }
  }

  // * Issues :: Search or filter results.
  .filtered-search-box {
    &.focus { box-shadow: 0 0 4px 0 $darker; }
    colors: 0, $darker, $dark;
    // Search field.
    .form-control { colors: $light, $t, 0; }
    // Search field words.
    .name { color: $light; }
    // Search field button.
    .clear-search { colors: 0, 0, $dark; }
    // Search tokens (labels).
    .filtered-search-token > .name,
    .selectable > div {
      colors: $light, $red, $darker;
    }
  }

  // * Issues :: Details.
  .detail-page-header,
  .issuable-details .content-block {
    color: var(--light);
    border-color: var(--darker);
  }
  // * Epics -> Wrapper.
  .epic-page-container {
    // Remove extra padding.
    .issuable-details {
      padding: 0;
      colors: 0, $t, 0;
      // Remove margin.
      .card-slim { margin-bottom: 0px; }
    }
    // Reactions wrapper.
    + .content-block { colors: 0 $t 0; }
  }
  // * Epics -> Tab holders.
  .epic-tabs-holder {
    colors: 0 $t $t;
    .scrolling-tabs-container { margin: 0 0 14px 0; }
  }
  // Time tracking.
  .time_tracker .time-tracking-help-state {
    border-color: var(--darker);
    background-color: var(--dark);
  }
  // Edited.
  // ? Contains styles for milestone elements; find a better place.
  .detail-page-description {
    .title, &.milestone-detail { colors: $light, $darker, 0; }
    .edited-text, .edited-text * { colors: $lighter, 0, 0; }
  }

  // * Issues :: Related issues.
  .related-issues-block {
    // Number of issues.
    .issue-count-badge { colors: 0, $dark, 0; }
    // Issue metadata.
    .item-body {
      // Link to the issue.
      a.sortable-link { colors: $light, 0, 0; }
      // Milestone/weight/etc.
      .item-weight span, .path-id-text { colors: $lighter, 0, 0; }
    }
  }
  // Related merge requests.
  .mr-count-badge { colors: 0, $dark, 0; }
  .related-items-list {
    .path-id-text, .item-path-id { colors: $light, 0, 0; }
    .open { fill: $green !important; }
    .closed { fill: $red !important; }
  }

  /* Created branch button. */
  .issuable-details p > code,
  a.gfm-project_member {
    border: 1px solid;
    colors: $light, $darker, $dark;
  }
  // Current user (you).
  .gfm.current-user {
    text-decoration: none;
    colors: $white $accent $accent;
    &:hover {
      // TODO: Resolve a conflict with `.notes a` in next refactor.
      colors: $white 0 0;
      opacity: 0.8;
    }
  }
  // Notes.
  .note-text pre {
    radius: $radius !important;
  }
  // ? Fixes missing bottom border for code.
  .notes .note-text > p:last-child { padding-bottom: 1px !important; }

  // * Cards.
  .card, .card-slim {
    radius: $radius;
    // overflow: hidden;
    colors: 0, $darker, $dark;
    .card-header, .card-footer {
      font-weight: 600 !important;
      colors: 0, $darker, $darker;
    }
    .card-body {
      padding: 8px;
      pre {
        font-family: var(--code-font);
        radius: $radius;
        colors: $light, $darker, $dark;
      }
    }
    // ? Project -> Releases.
    &.linked-card {
      .badge.badge-pill { colors: $light, 0, 0; }
      .dropdown > .btn-link { colors: $light, $t, 0; }
      &::after {
        border: none;
        border-left: 1px solid $darker;
      }
    }
  }

  // * Emoji picker.
  .emoji-menu {
    radius: $radius;
    colors: 0 $darker $dark;
    box-shadow: 0 4px 8px 0 $shadow;
  }
  // * Emoji autocomplete.
  .atwho-view {
    radius: $radius;
    colors: $lighter $darker $dark;
    box-shadow: 0 2px 4px 0 $shadow;
    .atwho-view-ul {
      li {
        colors: $lighter 0 0;
        // Matching text.
        strong { colors: $light 0 0 }
        // Selected.
        &:hover, &.cur { colors: $light 0 $darker }
      }
    }
  }

  // * Borders -> Wrapper resets.
  .boards-list {
    padding: 8px !important;
    height: calc(100vh - 134px);
    > .board {
      padding: 0 4px !important;
      // Whitespace for the last card.
      .board-card:last-child { margin-bottom: 8px !important }
    }
  }

  // * Boards -> Base styles.
  .issue-boards-content {
    // Fullscreen.
    &.is-focused {
      colors: 0, 0, $dark;
    }
    // Reset search bar.
    .row-content-block {
      margin: 0 16px;
      padding: 0 !important;
      border: none !important;
      colors: 0, 0, $t;
      // Fix search box.
      .filtered-search-box {
        max-height: 35px;
        radius: 3px;
      }
    }
    // Board colors.
    .board-inner {
      colors: 0, $darker, $dark;
      // Board header.
      .board-title { colors: $light, $darker, 0; }
      // Empty, default boards.
      [class$="-state"] { colors: $light, 0, $dark; }
      // Showing all issues.
      .board-list-count,
      .board-title-sub-text {
        colors: $lighter, 0, 0;
      }
    }
    .issue-count-badge {
      colors: 0, $darker, 0;
    }
    // Weight cards.
    .board-card-weight { colors: $lighter, 0, 0; }
    // Fix sidebar gutter close svg. #46 @upstream
    .gutter-toggle {
      padding-right: 10px;
      svg { width: 11px; }
    }
  }

  // * Boards -> Cards.
  // ? They turn into a tooltip once dragged so we must style them here.
  .board-card {
    transition: background 0.2s ease-in-out;
    border: 1px solid;
    colors: 0, $darker, $dark;
    box-shadow: 0px 1px 2px 0px $shadow;
    &:hover {
      colors: 0, $darker, $darker;
      box-shadow: 0px 2px 3px 2px $shadow;
    }
    // Text color.
    .board-card-title a { color: $light; }
    .board-card-number { color: $lighter; }
    // Assignee background.
    .board-card-assignee:hover a { colors: 0, 0, $t; }
  }
  // ? When you move boards themselves.
  .board > .board-inner {
    colors: 0, $darker, $dark;
    .board-title { border-bottom-color: $darker; }
  }

  // * Boards :: Add issues.
  .add-issues-container {
    radius: $radius;
    colors: 0, 0, $darker;
    // Reset styles.
    .add-issues-header, .add-issues-footer {
      colors: 0, 0, $t;
    }
  }

  // * Issues :: Labels.
  .labels-container {
    padding: 0px;
    colors: 0, 0, $t;
    // List.
    .manage-labels-list > li:not(.empty-message):not(.is-not-draggable) {
      margin-bottom: 10px;
      radius: $radius;
      colors: 0, $darker, $dark;
      transition: background 0.2s ease-in-out;
      &:hover { colors: 0, $darker, $darker; }
    }
    .label-badge-gray {
      border: 1px solid;
      colors: $light, $darker, $dark;
    }
    .label-badge-blue, .badge-info {
      border: 1px solid;
      colors: $light, $blue, $dark;
    }
    // Fix subscribe button.
    .dropdown > button.label-subscribe-button {
      padding: 3px;
      margin-top: -3px;
      font-weight: normal;
    }
  }


  // * Issues :: Milestones.
  .milestones {
    padding: 0;
    colors: 0, 0, $t;
    // Items.
    .milestone, .status-box {
      border: 1px solid !important;
      colors: $light, $darker, $dark;
      // Text color.
      .milestone-range { colors: $light, 0, 0; }
      // Links.
      .milestone-progress {
        .light, a { colors: $lighter, 0, 0; }
      }
      // Title and reopen button.
      strong a, .btn-reopen { colors: $light, 0, 0; }
    }
  }
  .milestone {
    .label-badge { border: 1px solid; colors: $lighter, $darker, $darker; }
    // Color in relatable issues.
    &-title { colors: $lighter, 0, 0; }
    &-banner-link { padding: 2px !important; }
  }

  // Progress bar background.
  .progress {
    colors: 0, 0, $darker;
    // Filled progress.
    .bg-success { colors: $light, 0, $green; }
  }

  // Details.
  .issuable-row {
    colors: 0, 0, $t;
    .issuable-number { colors: $lighter, 0, 0; }
  }

  /* Create milestone footer. */
  .form-actions,
  hr {
    border-top-color: var(--darker);
    background-color: var(--dark);
  }
  .form-actions {
    padding: 8px;
    border-radius: 4px;
    border: 1px solid;
    colors: 0, $darker, 0;
  }

  // * Promotional.
  .user-callout {
    // Reset extra margins on the side.
    margin: 20px 0 10px !important;
    &.promotion-callout {
      .bordered-box {
        padding: 8px !important;
        radius: $radius;
        colors: 0, $darker, $dark;
      }
    }
    // ? Found in: .../repo/pipeline_schedules
    .bordered-box.content-block {
      padding: 8px !important;
      radius: $radius;
      colors: 0, $darker, $dark;
    }
  }
  .banner-non-empty-state {
    padding: 5px 0 0 0;
    border: 1px solid;
    radius: $radius;
    colors: 0, $darker, $dark;
  }
  // GitLab-Gold promotion.
  .container-fluid > #content-body > .container-fluid.pt-1 { padding: 0; }

  // * Project :: Chart.
  // TODO unfinished.
  .burndown-chart {
    background-color: $darker;
    text { fill: $light; }
    .axis {
      &-label text { colors: $red, 0, 0; }
      &-label line, line, path { stroke: $lighter; }
    }
    // ? Legend!
    .legend {
      rect { stroke: $lighter; }
      text { fill: $light; }
    }
    .actual.line {
      stroke: $accent;
      fill: none !important;
    }
  }
  // Repository charts.
  .repo-charts {
    // Brings back the language color.
    .fa { color: inherit !important; }
    canvas:not(#languages-chart) { colors: 0, 0, $dark; }
  }
  // ? Found in: .../repo/pipelines/charts
  [data-page="projects:pipelines:charts"] {
    .legend {
      &-success .fa { color: inherit !important; }
      &-all { colors: $lighter, 0, 0; }
      &-all > i { color: inherit !important; }
    }
    canvas {
      radius: $radius;
      colors: 0, 0, $dark;
      box-shadow: inset 0 0 0 1px $darker;
    }
  }

  // * Calendar.
  .pika-single {
    colors: $light, $darker, $dark;
    // Header.
    .pika-label { colors: $lighter, 0, $t; }
    // Cells.
    td {
      colors: 0, $darker, 0;
      .pika-day { colors: $light, 0, $dark; }
      .pika-day:hover, &[class^="is-"] .pika-day { colors: $accent, 0, $darker; }
    }
  }

  // * Pagination.
  .pagination {
    // Rounded corners.
    > .page-item:first-child a {
      border-top-left-radius: $radius;
      border-bottom-left-radius: $radius;
    }
    > .page-item:last-child a {
      border-top-right-radius: $radius;
      border-bottom-right-radius: $radius;
    }
    // Links.
    .page-item {
      // Defaults.
      .page-link { colors: $lighter, $darker, $dark; }
      // Disabled.
      &.disabled .page-link {
        colors: $lighter, 0, 0;
        filter: opacity(0.75) !important;
      }
      // Selected.
      &.active .page-link { colors: $accent, 0, $darker; }
      &:hover:not(.disabled):not(.active) .page-link { colors: $accent, 0, 0; }
    }
  }

  // * Merge Requests :: List.
  .merge-requests-holder {
    margin-top: 10px;
    radius: $radius;
    border: 1px solid var(--darker);
    background-color: var(--dark);
    li:not(.page-item) {
      transition: background 0.15s ease-in-out;
      &:hover { background-color: var(--darker); }
      // Approvals.
      .text-success {
        colors: $green, 0, 0;
        svg { fill: $green !important; }
      }
    }
    // Fix whitespace.
    .gl-pagination {
      padding: 10px;
      margin-top: 0px !important;
      border-top: 1px solid $darker;
      .pagination { margin: 0px; }
    }
    // Email a new MR.
    .issuable-footer {
      padding: 10px;
      border-top: 1px solid var(--darker);
    }
  }

  // * Merge Requests :: Details.
  .merge-request-tabs-holder {
    margin: 10px 0;
    padding: 0 8px;
    border: 1px solid;
    radius: $radius;
    colors: 0, $darker, $dark;
    .scrolling-tabs-container {
      margin: 0;
      padding: 0;
      radius: 0;
      border: 0;
    }
  }
  // First contribution.
  .fa-certificate {
    colors: $accent, 0, 0;
    + strong { colors: $light, 0, 0; }
  }
  /* Nav background. */
  .limit-container-width .merge-request-tabs-container .inner-page-scroll-tabs {
    background-color: transparent;
  }
  .nav-tabs .nav-link.active {
    background-color: transparent !important;
  }
  // * Navigation :: Links.
  .nav-links {
    li a.active, .active a { colors: $light, $accent, 0; }
  }
  /* Resolved. */
  .line-resolve-all {
    border-color: var(--darker);
    background-color: var(--dark);
  }


  /* Merge Requests :: Notes. */
  .discussion-body .notes .note,
  .diff-file .notes .note {
    border-bottom-color: var(--darker);
  }
  /* Reply. */
  .discussion-form {
    background-color: transparent !important;
  }
  // ? Discussion within diff section.
  .diffs {
    .notes {
      border-top: none !important;
      &::before { border-left: 1px solid $darker !important; }
    }
    .note.timeline-entry {
      colors: 0, 0, $dark;
    }
  }
  // TODO merge with other discussion styles.
  .discussion-body .discussion-reply-holder,
  .diff-file .discussion-reply-holder {
    radius: 0 !important;
    border-top-color: $darker;
    border-bottom: 1px solid $darker;
    background-color: var(--dark);
  }
  // ? If reply is the last child, apply rounded corners and hide bottom color.
  .discussion-notes > .discussion-reply-holder:last-child {
    radius: 0 0 $radius $radius !important;
    colors: 0, $t, 0;
  }
  // Comments disabled.
  .disabled-comment {
    margin-top: 10px;
    padding: 16px 0;
    radius: $radius;
    colors: $light, $darker, $dark;
    a { color: $accent; }
    [class*="warning"] { colors: $orange, $t, $t; }
  }
  /* Text color. */
  .timeline .system-note .note-text,
  .notes .note-body .note-text {
    color: var(--light) !important;
  }
  .notes .system-note .system-note-commit-list-toggler {
    color: var(--accent) !important;
  }
  /* Commit list. */
  .notes .system-note .note-body .note-text:not(.hide-shade)::after {
    background: linear-gradient(transparent -100px, var(--dark) 100%) !important;
  }


  // * Merge Requests :: Discussions in file diff.
  .diff-file .notes_holder {
    // ! Might not need this line anymore.
    .notes_line {
      colors: $light, $dark, $dark;
      border-right-color: $darker !important;
    }
    // Top border at the start of discussion.
    .notes-content { colors: 0 $darker 0 }
    .notes_content {
      colors: $light, $dark, $dark;
      // Discussion.
      .notes {
        border-top: 1px solid;
        colors: 0, $darker, $dark;
      }
    }
  }

  // * Merge Requests :: Files.
  .diff-tree-list {
    margin-right: 8px;
    padding: 8px;
    radius: $radius;
    border: 1px solid;
    colors: 0, $darker, $dark;
    // Remove padding on the right side.
    > div { padding: 0; }
    // File border color.
    .tree-list-scroll { colors: 0, $darker, 0; }
    // Files.
    .file-row:hover, .file-row:active { colors: 0, 0, $darker; }
  }

  // * Merge Requests :: New MR.
  // TODO Refactor needed to simplify styles.
  .mr-state-widget {
    .mr-source-target {
      padding: 12px 8px;
      min-height: auto;
      colors: 0, $darker, $dark;
    }
    // Description container.
    .mr-section-container {
      radius: $radius;
      border: 1px solid;
      colors: 0, $darker, $dark;
      .mr-widget-section {
        padding: 12px 8px;
        radius: 0 !important;
        colors: 0, $darker, 0;
      }
      // Remove extra padding.
      .media-section > .media { padding: 0px !important; }
      .mr-widget-footer .mr-widget-help { padding: 0 4px; }
      // Align the CI icon.
      .media > .space-children { margin-left: 4px !important; }
      // Rounded corners.
      > section:first-child { radius: $radius $radius 0 0; }
      > div:last-child { radius: 0 0 $radius $radius; }
    }
    // Borders.
    .media-section,
    .mr-widget-heading,
    .mr-widget-footer {
      padding: 12px 8px;
      colors: 0, $darker, $dark;
    }
    // Rounded corners.
    .mr-source-target, .mr-widget-heading { radius: $radius; }
    // Circle icon thing.
    .circle-icon-container { colors: $light, $darker, 0; }
    .mr-widget-extension { colors: 0, $darker, $dark; }
    // ? Fixes approved merge request element.
    // TODO Rewrite this entire section at some point.
    .mr-widget-content {
      padding: 0 !important;
      .js-mr-approvals { padding: 0 !important; }
    }
    .mr-widget-workflow {
      // Connector.
      &::before { border-left-color: $darker; }
      // Remove the double-border forming on first child.
      > .mr-widget-border-top:first-child,
      > .mr-widget-section:first-child { border-top: none; }
      // Remove excess padding.
      .mr-widget-body { padding: 0px; }
    }
    // Branch name.
    .label-branch { color: $red, 0, 0; }
    // Found on: https://gitlab.com/gitlab-org/gitlab-ce/merge_requests/3023
    .ci-widget { colors: $lighter, 0, 0; }
  }
  .mr-widget-workflow::before,
  .mr-widget-heading:not(.deploy-heading)::before {
    left: 24px !important;
    border-left: 1px solid var(--darker);
  }
  // Text color.
  .mr-state-widget {
    colors: $light, 0, 0;
    .bold { colors: $light, 0, 0; }
    .font-weight-bold, .code-text, .coverage, .diverged-commits-count {
      colors: $lighter, 0, 0;
      a { font-weight: bold; color: $light; }
    }
  }
  // Icon color.
  .git-merge-icon-container { colors: $light, $darker, $darker; }

  /* Merge Requests :: Diff. */
  .mr-version-controls {
    border: 1px solid;
    radius: $radius;
    colors: $light, $darker, $dark;
    // Reset the width when file tree is open. @upstream
    &.is-fileTreeOpen {
      margin: 0 !important;
      width: 100% !important;
    }
    > div { border-bottom: none; }
  }

  // * File Diff :: Expanded view.
  .line_holder {
    // Show/hide comments.
    .diff-notes-collapse {
      colors: 0,  $darker, $dark;
      &:hover { colors: 0, 0, $darker; }
    }
    // No new line.
    .new-nonewline { colors: $lighter, 0, $t; }
  }
  // Matching lines.
  .line_holder .line_content.match {
    colors: $lighter, 0, $dark;
  }
  // Add a comment to this line.
  .line_holder .add-diff-note {
    padding: 0 0 1px 1px;
    colors: $light, $darker, $dark;
    &:hover { colors: 0, 0, $darker; }
  }
  /* Highlight the entire line. */
  .line_holder [class*="side"]:hover,
  .line_holder [class*="noteable"]:hover,
  .line_holder .diff-line-num.is-over,
  .line_holder .diff-line-num.hll:not(.empty-cell).is-over {
    colors: 0, $shadow, $darker;
  }
  /* Text colors. */
  .diff-stats-summary-toggler { color: $blue !important; }

  // * Snippets
  .project-snippets {
    // Fix file title height.
    .js-file-title  .file-header-content {
      line-height: 30px;
      .btn { padding: 8px 7px 2px 8px; }
    }
  }
  .notes pre.code {
    colors: $light, 0, $dark;
  }
  // Edit button.
  .detail-page-header a,
  .snippet-edited-ago {
    color: var(--light);
  }
  // Toggle discussion.
  .note-action-button {
    color: $light;
    &:focus, &:hover { color: $lighter; }
  }
  // Reactions.
  .row-content-block.content-component-block { background-color: $t; }
  // ? Sub-navigation for different sections.
  .snippet-scope-menu {
    padding: 0 8px !important;
    margin-top: 10px !important;
    radius: $radius;
    border: 1px solid;
    colors: 0, $darker, $dark;
  }
  // Snippets list.
  .snippets-list-holder {
    margin-top: 10px;
    border: 1px solid;
    radius: $radius;
    colors: 0, $darker, $dark;
    li {
      transition: all 0.2s ease-in-out;
      &:first-child { radius: $radius $radius 0 0; }
      &:last-child { radius: 0 0 $radius $radius; }
      &.snippet-row {
        padding: 8px !important;
        &:hover { colors: 0, 0, $darker; }
        // Text colors.
        .snippet-info {
          &, time, .js-user-link { colors: $lighter, 0, 0; }
        }
      }
    }
  }
  // Reset styles in reaction area.
  [data-page*="snippets:show"] .row-content-block {
    padding: 8px 0 0 0 !important;
    margin: 0 !important;
    border: none !important;
  }

  // * Project :: Snippets.
  [data-page*="projects:snippets:index"] {
    // Sub-navigation.
    .top-area {
      padding: 0 !important;
      colors: 0, $t, $t;
    }
    .snippet-scope-menu {
      margin-top: 0 !important;
      border-bottom: 1px solid $darker;
    }
  }

  // * Project :: Members.
  .members-list {
    li.member { padding: 8px; }
    .cgray time { colors: $lighter, 0, 0; }
    .member a.member-group-link { colors: $light, 0, 0; }
  }

  // * Editor -> Wrapper.
  .file-editor {
    // Rounded corners; might remove later.
    .file-holder { radius: $radius; }
    // Fix flashing white background.
    > #editor {
      radius: 0 0 $radius $radius;
      colors: 0, 0, $t;
    }
    // Header.
    .nav-links {
      margin-bottom: 10px;
      padding: 0 8px;
      radius: $radius;
      border: 1px solid;
      colors: 0, $darker, $dark;
    }
    // Branch name.
    .editor-ref { colors: 0, $darker, $t; }
    // Commit or disband.
    .form-actions {
      padding: 8px;
      radius: $radius;
      border: 1px solid $darker;
    }
  }

  // * Editor -> Template selection.
  .template-selectors-menu {
    colors: 0, $darker, 0;
    .text-info { colors: $light, 0, 0; }
  }

  // * Ace editor.
  .ace-tm {
    colors: $light, 0, $dark;
    // Gutter.
    .ace_gutter {
      radius: 0 0 0 $radius;
      colors: $lighter, 0, $dark;
      box-shadow: 1px 0 0 0 $darker;
      .ace_gutter-cell { colors: 0, 0, $t; }
      + .ace_scroller { radius: 0 0 $radius 0; }
    }
    // Highlighted line.
    .ace_gutter-active-line, .ace_active-line { colors: 0, 0, $darker; }
    .ace_selected-word, .ace_selection { colors: 0, $accent, $darker; }
    .ace_cursor { colors: $accent, 0, 0; }
    .ace_indent-guide {
      background: none;
      box-shadow: 1px 0 0 0 $lighter !important;
    }
    // Wrap line.
    .ace_print-margin { colors: 0, 0, $darker; }
    // Search/replace popout.
    .ace_search {
      padding: 0 4px;
      margin: 10px 10px 0 0;
      border: 1px solid;
      radius: $radius;
      colors: 0, $darker, $dark;
      box-shadow: 0 4px 8px 0 $shadow;
      // Inputs.
      &_field { colors: $light, 0, $dark; }
      .ace_search_form, .ace_replace_form { colors: 0, $darker, 0; }
      .ace_searchbtn, .ace_replacebtn { colors: 0, $darker, $darker; }
    }
    // Options.
    .ace_search_options {
      margin: 4px 0 0 0;
      text-align: left;
      .ace_button {
        padding: 5px;
        border-radius: 3px;
        colors: $icon, $t, $dark;
        &:hover, &.checked { colors: $accent, 0, $t; }
      }
    }
    // Scroller.
    .ace_scroller.ace_scroll-left { box-shadow: inset 17px 0 16px -16px $dark; }
  }

  // * Ace editor -> Syntax highlighting.
  .ace {
    &_line { colors: $lighter, 0, 0; }
    &_support&_function { colors: $darker, 0, 0; }
    &_support&_type { colors: $lighter, 0, 0; }
    &_meta&_tag { colors: $blue, 0, 0; }
    &_string, &_comment { colors: $green, 0, 0; }
    &_heading, &_storage, &_keyword { colors: $blue, 0, 0; }
    &_list { colors: $magenta, 0, 0; }
    &_variable { colors: $green, 0, 0; }
    &_constant, &_numeric { colors: $light, 0, 0; }
  }

  // * About page :: Applications.
  // ? https://about.gitlab.com/applications
  .applications {
    // Search area.
    .search-container {
      border: 1px solid;
      colors: $light, $darker, $dark;
      input { colors: $light, 0, $dark; }
      .suggestions {
        colors: 0, 0, $dark;
        li:hover, .hl { radius: $radius; }
        li:hover { colors: 0, 0, $darker; }
        .hl { colors: 0, 0, $accent; }
      }
    }
    // Categories.
    .category-name { colors: $light, 0, 0; }
    .category-applications .tile {
      border: 1px solid;
      colors: 0, $darker, $dark;
    }
    // Scroll.
    .scroll-to-top {
      border: 1px solid;
      colors: $light, $darker, $dark;
    }
    // Source!
    .source-link {
      colors: $light, 0, $darker;
      .external-source-link { colors: $accent, $t, 0; }
    }
  }
  // Footer links.
  footer.footer a {
    colors: $lighter, 0, 0;
    &:hover, &:focus { colors: $accent, 0, 0; }
  }

  // * Docs :: Landing page.
  // TODO: Merge with styles found in `docs` regex.
  .landing {
    .landing-header-top { colors: 0, $darker, $darker; }
    // Search.
    .input-container input {
      colors: $light, 0, $darker;
      &:focus { box-shadow: 0 4px 16px -4px $shadow; }
    }
    // Cards.
    .main-topics li {
      border: 1px solid;
      colors: 0, $darker, $dark;
      box-shadow: 0 4px 16px -4px $shadow !important;
    }
    // Header/footer.
    .landing-header, .footer {
      colors: $light, 0, $dark;
      // Created/cookies/edit.
      .source-link { colors: $light, 0, $darker; }
    }
  }

  // * Docs :: Search results.
  .algolia-autocomplete {
    [class*="dataset"] { colors: $light, $darker, $dark;
      // Transparentize the content.
      [class*="docsearch-s"] { colors: $light, 0, $t; }
    }
    // Remove shadow.
    .ds-dropdown-menu {
      box-shadow: 0 4px 16px -4px $shadow;
      // Arrow.
      &:before { colors: 0, $darker, $dark; }
    }
    // Header.
    [class*="header"] { colors: $lighter, $darker, 0; }
    // Separator.
    .algolia-docsearch-suggestion--subcategory-column::before,
    .algolia-docsearch-suggestion--content::before { colors: 0, 0, $darker; }
    // Selected.
    [class$="--content"]:hover { colors: 0, 0, $darker; }
    // Matching result.
    [class*="highlight"] { box-shadow: inset 0 -2px 0 0 $accent !important; }
  }

  /* Docs :: Page. */
  [itemtype*="http://schema.org/WebPage"] .header {
    colors: 0, $shadow, $darker;
    // Search button.
    .docsearch {
      radius: $radius !important;
      colors: 0, $darker, $dark;
    }
  }

  // * Main content.
  .main {
    background-color: transparent !important;
    box-shadow: none !important;
    // TOC.
    // Remove sidebar.
    *::-webkit-scrollbar { display: none !important; }
    // Alerts.
    blockquote {
      position: relative;
      colors: 0, $t, $dark;
      &::before {
        content: "";
        position: absolute;
        top: 0; left: 0; bottom: 0;
        radius: $radius 0 0 $radius;
        border-left: 4px solid $blue;
      }
    }
    .alert-warning { colors: 0, $red, $dark; }
    .alert.alert-success { colors: 0, $violet, $violet; }
    // Reset *introduced in...* blockquotes.
    blockquote {
      padding: 16px;
      radius: $radius;
      box-shadow: inset 0 0 0 1px $darker;
      > p { margin: 0; }
    }
    // Border colors.
    h2, h3, h4, h5, h6, .edit-on, .comments { colors: 0, $darker, 0; }
    // Text colors.
    a:hover, a:focus { text-decoration: underline; }
    .article-metadata { colors: $light, 0, 0; }
    .highlight.plaintext code { colors: $light, 0, 0; }
    code {
      border: 1px solid;
      colors: $accent, $darker, $dark;
    }
    img {
      transition: 0.15s ease;
      opacity: faded_img;
      &:hover { opacity: 1; }
    }
  }

  // * Custom width for fluid layout.
  // ? Emulates fixed width layout without editing many hard-coded selectors.
  .layout-page .container-fluid {
    &:not(.container-limited):not(.js-focus-mode-board) {
      max-width: var(--width) !important;
    }
  }

  /* Fix bugged left sidebar. */
  @media screen and (max-width: 1200px) {
    /* Cut transitions. */
    .page-with-contextual-sidebar,
    .nav-sidebar,
    .toggle-sidebar-button,
    .close-nav-button,
    .nav-sidebar a {
      transition-duration: 0s !important;
    }
  }


  // * IDE!
  .ide {
    // Text color.
    .ide-view {
      colors: $light, 0, 0;
      // Left sidebar title.
      .context-header > a,
      .ide-review-sub-header { colors: $light, 0, 0; }
    }
    // Status bar.
    .ide-activity-bar, .ide-status-bar { colors: 0, $darker, $dark; }
    // Resize.
    .drag-handle { colors: 0, 0, $dark; }
  }
  // File templates.
  #ide .ide-file-templates { colors: 0, $darker, $dark; }

  // Left sidebar active.
  .ide-sidebar-link {
    transition: 0.2s ease-in-out;
    // On event.
    &:focus, &:hover, &.active {
      colors: $accent, $darker, $darker;
      box-shadow: inset 3px 0 var(--accent) !important;
    }
    &::after { colors: 0, 0, $dark; }
  }

  // * IDE :: File explorer.
  .ide {
    // Tree.
    .multi-file-commit-panel-inner-content {
      colors: 0, $darker, $dark;
      // File in the tree.
      .file-row {
        transition: 0.2s ease-in-out;
        &.is-active, &:hover, &:active { colors: 0, 0, $darker; }
        // File options.
        .ide-entry-dropdown-toggle { colors: $light, 0, $dark; }
      }
    }
    // Commit.
    .multi-file-commit-form {
      colors: 0, $darker, $dark;
      > .commit-form-compact { border-top-color: $darker; }
    }
    // Icons.
    .ide-tree-header button {
      color: var(--icon);
      transition: all 0.2s ease-in-out;
      &[aria-expanded="true"] { colors: 0, 0, $darker; }
    }
  }

  // * IDE :: Merge Requests / Branches.
  .ide-tree-header {
    border-bottom-color: var(--darker);
    // Branch popup.
    .ide-nav-form {
      // Separator.
      .tab-links { colors: 0, $dark, 0; }
      // Inactive tab.
      .nav-links li a:not(.active) { colors: 0, 0, $darker; }
      .border-bottom { border-color: $dark !important; }
      // Search container.
      .filtered-search-wrapper {
        colors: 0, $darker, $dark;
        input { colors: 0, 0, $t; }
      }
      // Project metadata.
      .ide-merge-request-project-path { color: $lighter; }
    }
    // Branch on hover.
    button:hover { colors: $light, 0, $darker; }
  }
  /* Selected. */
  .ide-file-list .file:hover,
  .ide-file-list .file.file-active,
  .multi-file-commit-list-path:hover,
  .multi-file-commit-list-path.is-active {
    background-color: var(--darker);
  }

  // * IDE :: Commit section.
  .ide-view {
    // Borders.
    .ide-commit-list-container.is-first,
    .multi-file-commit-panel-header { colors: 0, $darker, 0; }
    // Remove background on buttons.
    .ide-staged-action-btn { colors: 0, 0, $t; }
    // Commit message.
    .ide-commit-message-field {
      colors: $lighter, 0, $t;
      textarea { colors: $light, 0, 0; }
    }
    // Background after you push a commit.
    .multi-file-commit-panel-success-message { colors: 0, 0, $dark; }
  }

  // * IDE :: Right sidebar.
  .ide-view .multi-file-commit-panel {
    border-color: var(--darker);
    background-color: var(--dark);
  }
  /* Jobs. */
  .ide-view .card-header,
  .ide-view .ide-job-item:not(:last-child) {
    border-color: var(--darker);
  }
  /* Build header. */
  .build-page .top-bar {
    radius: $radius $radius 0 0;
    colors: $lighter, $darker, $dark;
    // ? Fix terminal controls header, bug on upstream.
    &.sidebar-expanded { margin-right: 0 !important; }
  }
  // ? Hotfix for the header.
  .build-page > header > .build-header.top-area { margin-bottom: 10px; }
  /* Build log. */
  .build-page .build-trace {
    radius: 0 0 $radius $radius;
    color: var(--light);
    border: 1px solid var(--darker);
    border-top-color: transparent;
    background-color: var(--dark);
    > code {
      colors: $lighter, 0, 0;
      .term-fg-l-green { colors: $green, 0, 0; }
      .term-fg-red { colors: $red, 0, 0; }
    }
  }
  .build-page .environment-information {
    colors: 0, $darker, $dark;
  }

  // * Build sidebar.
  .build-sidebar {
    .link-commit { colors: $accent, 0, 0; }
  }

  // * Pipelines :: Builds.
  // TODO Buggy, info: https://gitlab.com/vednoc/dark-gitlab/issues/22
  .builds-container {
    colors: $light, $darker, $dark;
    .build-job {
      transition: 0.2s ease;
      a { colors: $light, 0, 0; }
      &:hover { colors: 0, 0, $darker; }
      // ? https://gitlab.com/gitlab-org/gitlab-ce/-/jobs/141718266
      &.retried { colors: 0, 0, $t; }
    }
  }

  // * IDE :: Edit area.
  .multi-file-edit-pane {
   colors: 0, $darker, $dark;
   // Tabs.
   .ide-mode-tabs { colors: 0, $darker, $darker; }
  }
  /* Tabs. */
  .multi-file-tabs {
    background-color: var(--dark);
    box-shadow: inset 0 -1px var(--darker);
    // Items.
    li {
      transition: 0.2s ease-in-out;
      colors: $lighter, $darker, $t;
      &.active, &:hover { colors: $accent, $darker, $darker; }
    }
  }
  // Commit header.
  .ide-commit-editor-header {
    colors: $light, $darker, $dark;
    box-shadow: inset 0 -1px var(--darker);
  }
  // Git additions/deletions.
  .monaco-diff-editor {
    .line-delete {
      border-color: transparent !important;
      background-color: rgba(200, 49, 49, 0.3) !important;
    }
    .char-delete, .char-insert {
      background-color: rgba(0, 0, 0, 0.15) !important;
    }
    .line-insert {
      border-color: transparent !important;
      background-color: rgba(149, 200, 99, 0.3) !important;
    }
  }

  // * IDE :: Monaco editor.
  .monaco-editor {
    colors: $light, 0, $dark;
    // Background.
    &-background { colors: $light, 0, $dark; }
    // TODO figure out what this was. XD
    .scroll-decoration {
      box-shadow: inset 0 6px 6px -6px $dark !important;
    }
    // Gutter.
    .margin {
      colors: 0, $dark, $dark;
      .line-numbers { colors: $lighter, 0, 0; }
    }
    // Current line.
    [class*="current-line"]  { colors: 0, $darker, $darker; }
    // Cursor.
    .cursor { colors: $light, $accent, $accent; }
    // Indent guide.
    .lines-content [class*="cigr"] { box-shadow: inset 1px 0 0 0 $accent !important; }
    // Command palette.
    .monaco-quick-open-widget {
      colors: $light, $darker, $dark;
      box-shadow: $box !important;
      // Focused.
      .monaco-tree-row {
        &:hover, &.focused { colors: $accent, 0, $darker; }
      }
    }
    // Inspect tokens.
    .tokens-inspect-widget { colors: $light, $darker, $dark; }
    // Input.
    .monaco-inputbox {
      colors: $light, $darker, $dark;
      input { colors: $light, 0, $dark; }
    }
    // Resize.
    .monaco-sash { colors: 0, 0, $accent; }
    // Find palette.
    .find-widget {
      colors: 0, 0, $darker;
      box-shadow: $box;
      .button, .label { filter: invert(var(--bg-invert)); }
    }
    // Accessibility.
    .accessibilityHelpWidget {
      colors: $light, $darker, $dark;
      box-shadow: $box;
    }
    // Context menu.
    .context-view {
      colors: 0, 0, $darker;
      box-shadow: 0 2px 8px $shadow;
    }
    // Hover popup.
    .monaco-editor-hover { colors: 0, $shadow, $darker; }
    // Selection.
    .cslr.selected-text { background-color: $darker; }
    // Matching.
    .selectionHighlight { background-color: $darker !important; }
    // Suggestion.
    .tree {
      colors: $light, $darker, $darker;
      box-shadow: $box;
      .focused { colors: $accent, 0, $dark; }
    }
  }
  /* Diff view. */
  .diffOverviewRuler { background-color: var(--dark); }
  /* Git. */
  .dirty-diff-added { background-color: var(--green); }
  .dirty-diff-modified { background-color: var(--blue); }
  // ? Dropdown for file finder.
  .ide-view .dropdown-menu {
    max-height: 500px;
    li button.is-focused, a.active { colors: 0, 0, $dark; }
  }

  // * IDE :: Monaco syntax highlighting.
  .monaco-editor {
    .mtk1, .mtk3 { color: $light; }
    .mtk10 { color: $lighter; }
    .mtk4, .mtk20 { color: $red; }
    .mtk8 { color: $green; }
    .mtk5, .mtk6, .mtk23 { color: $blue; }
    .mtk14 { color: $magenta; }
    .mtk22, .mtk12 { color: $cyan; }
    .mtk11 { color: $orange; }
  }

  // * Project :: Code view.
  .code {
    colors: $light, 0, $t;
    // Remove weird border around code wrapper.
    code { border: none; }
    // Highlighted line.
    pre .hll { colors: 0, 0, $darker; }
    // Gutter border.
    pre.code, .diff-line-num { colors: 0, $darker, 0; }
    // Gutters in file view/diff.
    .line-numbers, .diff-line-num {
      &, a { colors: $lighter, 0, $t; }
      // ? Add left border to right section in parralel diff view.
      &:nth-child(3) { border-left: 1px solid $red !darker; }
    }
    // ? Code wrapper.
    .line_holder {
      // Lines for added/removed sections.
      .line_content {
        z-index: 1;
        colors: $light, 0, $t;
        &.new::before, &.old::before { colors: $lighter, 0, 0; }
        &.old::after, &.new::after {
          content: "";
          z-index: -1;
          opacity: 0.25;
          top: 0; right: 0; bottom: 0; left: 0;
          position: absolute;
        }
        &.old::after { colors: 0, 0, $red; }
        &.new::after { colors: 0, 0, $green; }
      }
      // Gutters for added/removed sections.
      .diff-line-num.old, .diff-line-num.new {
        z-index: 1;
        colors: 0, $darker, $t;
        &.old::after, &.new::after {
          content: "";
          z-index: -1;
          opacity: 0.25;
          top: 0; right: 0; bottom: 0; left: 0;
          position: absolute;
        }
        &.old::after { colors: 0, 0, $red; }
        &.new::after { colors: 0, 0, $green; }
      }
      // Rounded corners.
      &:last-child > td {
        &:first-child, &:first-child::after { radius: 0 0 0 $radius; }
        &:last-child, &:last-child::after { radius: 0 0 $radius 0; }
      }
    }
    // ? Notes -> Suggested changes.
    .gd, .gi {
      z-index: 0;
      position: relative;
      colors: $light, 0, $t;
      // Fake element.
      &::before {
        content: "";
        position: absolute;
        top: 0; right: 0; bottom: 0; left: 0;
        opacity: 0.25;
        z-index: -1;
      }
    }
    // Removed/added.
    .gd::before { colors: 0, 0, $red; }
    .gi::before { colors: 0, 0, $green; }
  }

  // * Project :: Code syntax highlighting.
  .code.white {
    .n, .gh, .s2 { color: $light; }
    .s, .c, .cp, .c1, .nn { color: $lighter; }
    .m, .nv, .na { color: $cyan; }
    .nb, .nc { color: $blue; }
    .ss, .s1, .se, .gu { color: $magenta; }
    .sx, .sb, .sh, .nf { color: $red; }
    .nt, .si { color: $violet; }
    .err {
      colors: 0, 0, $t;
      box-shadow: inset 0 -2px 0 0 $red;
    }
  }


  // * Project CI/CD :: Pipelines.
  .table-holder {
    margin: 10px 0 0 0;
    .ci-table {
      margin-bottom: 0;
      // Pipelines.
      > [class*="row"] {
        padding: 2px 4px 2px 8px;
        border-color: var(--darker);
      }
      // Header.
      th, .table-row-header {
        font-weight: 700;
        color: var(--light);
        border-color: transparent;
        background-color: var(--darker);
      }
      // On hover.
      .commit, .commit > td {
        transition: background 0.15s ease-in-out;
        &:hover, &:hover > td {
          background-color: var(--darker) !important;
        }
      }
      // Whitespace.
      tr td { padding: 4px 8px }
    }
  }
  /* Pipeline commit. */
  .commit a,
  .generic_cimmit_status a,
  .pipelines .ci-table .pipeline-id,
  .ci-table .branch-commit .commit-sha {
    color: var(--light);
  }
  // Pipeline stage connectors.
  .stage-container::after { border-bottom: 1px solid $lighter !important }

  // * Pipelines -> Mini-pipelines.
  .mini-pipeline-graph-dropdown-toggle {
    colors: 0 0 $darker;
    &:focus { box-shadow: 0 0 4px 1px $blue !important }
    // * Pipelines -> Stages. Old info in #22.
    + .mini-pipeline-graph-dropdown-menu {
      // Indicator.
      &::after { colors: 0 $accent 0 }
      // Reset for items.
      li {
        &:hover a { colors: $lighter 0 $t }
        a:hover, a:focus { colors: $light 0 $darker }
        // Pipelines where you have permissions.
        .ci-job-component {
          transition: 0.15s ease;
          &:hover { colors: 0 0 $darker }
          .mini-pipeline-graph-dropdown-item {
            colors: 0 0 $t;
            .ci-status-text { colors: $lighter 0 0 }
            &:hover .ci-status-text { colors: $light 0 0 }
          }
          // Retry button.
          .ci-action-icon-container {
            colors: 0 $dark $darker;
            &:hover { colors: 0 $accent $darker }
          }
        }
      }
      // ? Fix text weight.
      .ci-build-text, .ci-status-text { font-weight: normal !important }
    }
  }

  // * Project CI/CD :: Pipeline details.
  .pipeline-graph {
    margin-top: 10px;
    radius: $radius;
    colors: $light, 0, $dark;
    box-shadow: inset 0 0 0 1px $darker;
    // Pipeline node.
    .build .build-content {
      border-width: 2px !important;
      colors: $lighter, $darker, $dark;
      transition: 0.2s ease;
      // On event.
      &:hover { colors: $light, $darker, $darker; }
    }
    // Triangle.
    .cross-project-triangle {
      margin-top: 1px;
      border-left-color: $darker;
    }
    // Linked pipeline?
    // ? https://gitlab.com/gitlab-org/gitlab-ce/pipelines/31973329
    .linked-pipeline {
      &-content {
        border-width: 2px !important;
        radius: 10px !important;
        colors: $light, $darker, $dark;
        // ? Reset it because it's a button.
        transform: none !important;
        &:hover { colors: 0, 0, $darker; }
      }
      // Connector line.
      &::before { margin-top: 2px; }
    }
    // Actions.
    .ci-action-icon-container {
      margin: 1px 1px 0 0;
      colors: 0, $darker, $dark;
    }
    // Connectors.
    .build, .stage-column .curve {
      &::before, &::after { colors: 0, $darker, 0; }
    }
    .ci-build-txt, .ci-status-text { font-weight: normal; }
  }

  // * Pipelines :: Failed Jobs.
  .build-failures {
    .table {
      margin-top: 10px;
      border: 1px solid;
      colors: 0, $darker, $darker;
      // Table header.
      > thead th {
        colors: $light, 0, 0;
      }
    }
    // Log output.
    .build-trace {
      colors: 0, $dark, 0;
      > code {
        colors: $light, $darker, $darker;
      }
    }
    .build-name a {
      color: $accent;
    }
  }

  // * Pipelines :: Security.
  .split-report-section {
    margin-top: 10px;
    border: 1px solid;
    radius: $radius;
    colors: $light, $darker, $dark;
    // Wrapper.
    .report-block-container {
      colors: $light, $darker, $dark;
    }
  }

  // * Jobs :: Show output.
  .build {
    // Breadcrumbs.
    &-header .header-content {
      margin: 4px 0;
      color: $lighter;
      a { color: $light;
        &:hover { color: $accent; }
      }
    }
    // Job details.
    &-light-text { color: $lighter; }
    // Job on hover.
    &s-container a:hover { color: $accent !important; }
  }

  // * Jobs :: Browse artifacts.
  [data-page="projects:artifacts:browse"] {
    .tree-table {
      radius: $radius;
      box-shadow: 0 0 0 1px $darker;
      thead {
        tr, tr > th { border-top: none; }
        th:first-child { radius: $radius 0 0 0; }
        th:last-child { radius: 0 $radius 0 0; }
      }
      tbody {
        tr:last-child {
          td:first-child { radius: 0 0 0 $radius; }
          td:last-child { radius: 0 0 $radius 0; }
        }
      }
      // Remove borders.
      tr {
        border: none !important;
        td {
          border: none !important;
          border-top: 1px solid $darker !important;
        }
      }
      // Item text color.
      .tree-item a { colors: $light, 0, 0; }
    }
  }

  // * Jobs :: Navigation.
  [data-page="projects:commit:pipelines"], [data-page^="projects:pipelines"] {
    // ? Only apply to nav-links that have .nav-tabs class.
    .nav-tabs.nav-links:not(.quick-links) {
      margin-top: 10px;
      padding: 0 10px;
      border: 1px solid;
      radius: $radius;
      colors: 0, $darker, $dark;
      a.active { colors: $light, 0, 0; }
    }
  }

  // * CI/CD :: Schedules.
  [data-page^="projects:pipeline_schedules"] {
    .ref-name, .next-run-cell { colors: $light, 0, 0; }
    .pipeline-schedule-table-row a { colors: $light, 0, 0; }
  }

  // * Skeleton.
  // ? Changes color of loading elements.
  .animation-container [class^="skeleton-line-"] {
    background-color: var(--darker);
  }
  .animation-container [class^="skeleton-line-"]::after {
    background-image: linear-gradient(to right, var(--darker) 0%, var(--dark) 100%);
  }

  // * Groups.
  .group-nav-container {
    > .scrolling-tabs-container:first-child {
      margin: 0 !important;
      border: none !important;
      + .nav-controls { padding: 8px 0 !important; }
    }
    // No results.
    + .tab-content {
      .has-no-search-results { colors: $lighter, 0, 0; }
    }
  }
  // * Group header.
  .explore-groups {
    radius: $radius;
    colors: 0, $darker, $dark;
  }
  // * Group body.
  .groups-list-tree-container > ul.group-list-tree {
    margin-top: 10px !important;
    radius: $radius;
    colors: 0, 0, $dark;
    box-shadow: 0 0 0 1px inset $darker;
    // On event.
    > .group-row {
      // Rounded corners.
      &:first-child > div { radius: $radius $radius 0 0; }
      &:last-child > div { radius: 0 0 $radius $radius; }
      // &.has-children .group-list-tree { margin-left: 42px !important; }
    }
    .group-row {
      // Keep fake border even when sub-group is open.
      &.is-open > div { box-shadow: 0 -1px 0 0 inset $darker; }
      // Horizontal line.
      &::before { colors: 0, $darker, 0; }
      // Vertical line.
      .group-list-tree::before {
        top: 0px;
        colors: 0, $darker, 0;
      }
      // Remove top-border from the first item.
      &:first-child { border-top: none !important; }
      // Fix last item background.
      &:last-child::before { colors: 0, 0, $t; }
      // Content.
      &-contents {
        transition: 0.15s ease;
        &:hover { colors: 0, 0, $darker; }
        // Text colors and width.
        .stats {
          colors: $lighter, 0, 0;
          .stat-value {
            min-width: 1.5rem;
            text-align: left;
          }
        }
        &.has-more-items { padding: 10px; }
      }
    }
  }

  // * Groups -> Contribution Analytics.
  .svg-graph-container {
    padding: 16px;
    margin-top: 16px;
    display: flex;
    justify-content: center;
    align-items: center;
    radius: $radius;
    colors: 0 0 $dark;
    box-shadow: 0 0 0 1px inset $darker;
    overflow-y: hidden; // Hide whitespace.
    svg {
      margin-top: -75px;
      text { isvg: $lighter false }
      path, line { svg: false $lighter }
      rect {
        fill: url(none) !important;
        &.bar-rect { isvg: $darker $blue }
        &[fill="#fff" i] { isvg: $dark false }
      }
    }
  }

  // * Invert image icons.
  // TODO adjust if it causes weird things.
  img[src*="/assets/icon"] {
    filter: invert(1);
  }

  // * Invert illustrations.
  .wiki .anchor,
  .banner-graphic > svg,
  .explore-groups > .svg-container > svg,
  .commits-empty > svg,
  .artwork svg,
  .billing-plan-logo > svg,
  img[src*="/assets/illustrations/"],
  #subgroups_and_projects .empty-state svg {
    filter: opacity(0.9) invert(0.9) hue-rotate(180deg) !important;
  }

  // * Invert `shadow-root (closed)` SVG icons.
  // TODO remove if/when fixed on upstream.
  .created-icon, .opened-icon,
  .ci-status-icon > svg,
  [class*="ci-status"] svg.s24,
  [class*="ci-status"]:not(#stageDropdown) svg.s16,
  a[class*="ci-status"] svg, // ? Found on: .../-/jobs
  svg.ic-upload {
    // ? Inverts icons and their colors only if `Background inversion` is
    // ? enabled in the style settings. Does not work with ThemeSwitcher.
    if (bg_invert == 1) {
      filter: invert(calc(var(--bg-invert) - 0.2)) hue-rotate(180deg);
    }
  }
  // * CI icons.
  .ci-status {
    // ? Pipeline coverage.
    &.ci-success, &-icon-success, &-icon-passed {
      colors: $green, $green, $t;
      svg { fill: $green !important; colors: 0, $green, 0; }
    }
    &.ci-running, &-icon-running {
      colors: $blue, $blue, $t;
      svg { fill: $blue !important; colors: 0, $blue, 0; }
    }
    &-icon-pending, &-icon-warning, &-icon-success_with_warnings {
      colors: $orange, $orange, $t;
      > svg { fill: $orange !important; colors: 0, $orange, 0; }
    }
    &.ci-failed, &-icon-failed {
      colors: $red, $red, $t;
      svg { fill: $red !important; colors: 0, $red, 0; }
    }
    &.ci-created, &.ci-skipped,
    &.ci-canceled, &.ci-disabled,
    &.ci-manual {
      colors: $lighter, $darker, $t;
      svg { fill: $icon !important; colors: 0, $icon, 0; }
    }
    &-icon-skipped, &-icon-created {
      colors: 0, $icon, 0;
      svg { fill: $icon !important; colors: 0, $icon, 0; }
    }
  }
  // Icons in epics.
  .issue-token-state-icon-open { fill: $green !important; }
  .issue-token-state-icon-closed { fill: $blue !important; }
  // Icons in activities.
  .commented-on-icon svg { fill: $blue !important; }
  .created-icon svg { fill: $cyan !important; }
  .opened-icon svg { fill: $green !important; }
  .closed-icon svg { fill: $red !important; }
  .accepted-icon svg { fill: $cyan !important; }
  .deleted-icon svg { fill: $yellow !important; }
  .imported-icon svg { fill: $violet !important; }

  // "Weird" behaving success with warnings status.
  a.ci-success_with_warnings { colors: $orange, $orange, $t; }
  // "Weird" behaving warning on pipelines page.
  .ic-status_warning_borderless { fill: $orange !important; }

  // * Centers project header elements; old design lookalike.
  if p_header {
    // Necessary in v11.6 (I think).
    .project-home-panel .project-header {
      > div { max-width: 100%; }
    }
    .project-home-panel .project-badges {
      flex-direction: row !important;
      justify-content: center;
    }
    // Up until v11.5 (I think).
    .project-home-panel > div {
      display: flex;
      flex-direction: column;
      margin-bottom: 0px;
      > div {
        justify-content: center;
        text-align: center;
      }
    }
    .project-stats .nav { justify-content: center; }
    // Center the description.
    .project-description,
    .home-panel-title-row,
    .project-repo-buttons {
      margin: 0 auto;
    }
    // Separate buttons from the header and the description.
    .project-repo-buttons {
      margin-top: 10px;
      justify-content: center !important;
    }
  }

  // Switch to our version of GitLab logo.
  if dark_logo {
    .tanuki-logo {
      filter: brightness(095%) drop-shadow(0px 1px 1px $shadow) !important;
      .tanuki { &-right-eye, &-left-eye { isvg: $accent $t; }}
      .tanuki-right { &-cheek, &-ear { isvg: $dark $t; }}
      .tanuki-left { &-cheek, &-ear { isvg: $dark $t; }}
      .tanuki-nose { isvg: $dark $t; }
    }
  }

  // * Custom app background.
  // ? `:not(.dark) is used to prevent disqus iframe conflicts.
  body:not(.dark)::before {
    position: fixed;
    z-index: -1;
    content: "";
    top: 0;
    left: 0;
    right: 0;
    bottom: 0;
    background-position: var(--bg-pos);
    background-repeat: var(--bg-repeat);
    background-size: var(--bg-size);
    background-image: var(--background);
    filter: invert(var(--bg-invert)) var(--bg-filter);
  }

  // * Code font.
  .gfm-commit, .diff-file table,
  code, .code, pre.code code,
  [class*="monospace"], .ref-name,
  #editor, .monaco-editor * {
    font-family: var(--code-font) !important;
  }

  // * Feature -> Fix alignment of icons from GitLab icons extension.
  if ( gl_icons == 1 ) {
    .tree-item-file-name {
      > span, > .fa {
        width: 18px;
        display: inline-flex;
        justify-content: center;
        margin-right: 2px !important;
      }
    }
  }

  /* Prevent horizontal scrollbar from showing up when the viewport is overflowed. */
  html {
    overflow-x: hidden;
    background-color: var(--dark);
  }

  // ! What.
  // ? Fixes weird pseudoelements.
  .diff-file .file-title {
    &::before, &-flex-parent::before { display: none !important; }
  }
}

@-moz-document regexp("https?://design.gitlab.com(/.*)") {
  // Loading bar.
  .nuxt-progress { colors: 0, 0, $accent; }
  // Contribute section.
  .contribute { colors: 0, $darker, $dark; }
  // Cards.
  .card--header { colors: 0, $darker, $darker; }
  // Navigation.
  .nav-sidebar {
    colors: 0, $darker, 0;
    &__header {
      colors: 0, $darker, 0;
      &-anchor { colors: $light, 0, 0; }
    }
    // Links.
    summary:hover, details > a:hover { colors: 0, 0, $darker; }
    &__section-items-anchor { colors: $lighter, 0, 0; }
    .nuxt-link-exact-active {
      colors: $light, 0, $darker;
      box-shadow: inset 4px 0 0 $accent !important;
    }
  }
  // Fix color details.
  .palette:not(.neutral) > .color { background-color: $darker; }
  .todo { colors: 0, $darker, $dark; }
  table {
    colors: $light, $darker, $dark;
    thead { colors: 0, $darker, $darker; }
    td, th { colors: 0, $darker, 0; }
    td.header, th.header { colors: 0, $darker, $darker; }
  }
  // Typography spacing.
  .baseline--typography:after { background-image: linear-gradient($darker 1px, $t 0); }
}

// * Code for about page on GitLab.
// ? Might be split into a separate userstyle if it gets quite large.
// ! @RaitaroH takes the full responsibility for this section. (=

@-moz-document regexp("https?://about.gitlab.com(/.*)") {
  // * Global image reset.
  img, .video_container {
    opacity: faded_img !important;
    transition: 0.15s ease;
    &:hover { opacity: 1 !important; }
  }

  // * Index page for about sub-domain.
  .sdlc-table {
    radius: $radius !important;
    colors: 0, 0, $dark;
    box-shadow: 0 0 0 1px $darker !important;
    .sdlc-column:not(:last-child) {
      border-right-color: $darker !important;
    }
    .stage-container, .solutions-container {
      border-bottom-color: $darker !important;
    }
    .devops-tool-logo-container {
      radius: 0 0 $radius $radius;
      colors: 0, 0, $dark;
    }
  }
  a.feature-more { colors: $accent, 0, 0; }

  // * Blog resources.
  .tile-resource {
    radius: $radius;
    colors: 0, 0, $dark;
    box-shadow: $box, 0 0 0 1px $darker;
    .tile-info  { colors: 0, 0, $t; }
  }

  // * Articles.
  .blog.article {
    .overlay { background: linear-gradient(0deg, $dark, $t 100%); }
    .body { colors: 0, 0, $dark; }
    // Anchors.
    .content {
      h1, h2, h3, h4, h5, h6, ul li, a {
        // colors: $accent, 0, 0;
        a, code { colors: $accent, 0, 0; }
      }
      p a, p a > a { colors: $accent, 0, 0; }
    }
    .btn.btn-red { colors: $light, $red, $darker; }
    .twitter-block .panel-body { colors: $lighter, 0, $darker; }
    // Availability of the features.
    .badge:not(.available) { colors: $light, $darker, $darker; }
    // Previous/next buttons.
    .pull-left a, .pull-right a { colors: $light, $darker, $dark; }
    // Signup section.
    .signup {
      max-width: 100%;
      colors: 0, 0, $darker;
      > .panel {
        margin: 0 auto;
        max-width: 900px;
      }
    }
    // Disqus section.
    .comments {
      radius: $radius;
      border: 1px solid;
      colors: 0, $darker, $dark;
    }
  }
  // Header anchor.
  .header-link > i.fa-link { colors: $lighter, 0, 0; }
  // Divider between new features.
  [class$="-column"]:last-child { colors: 0, $darker, 0; }
  // Navbar.
  .sticky-banner {
    colors: 0, 0, $darker;
    box-shadow: 0 0 8px 0 $shadow;
  }
  .gray-section {
    colors: $lighter, $darker, $dark;
  }
  .navbar .navbar-right ul li {
    a, .search-icon {
      colors: $light, $shadow, 0;
      &:hover, &:focus {
        colors: $accent, $shadow, $dark;
      }
    }
    .dropdown {
      colors: $light, $darker, $darker;
      a {
        color: $light !important;
        &:hover {
          color: $accent !important;
        }
      }
    }
  }
  .blog-category-nav ul li {
    color: $lighter !important;
    &:after {
      background-color: $accent !important;
    }
    &:hover {
      color: $accent !important;
    }
  }
  .navbar .sign-up, .navbar .search-icon {
    color: $lighter !important;
    &:hover {
      color: $accent !important;
    }
  }
  .navbar .mobile-icons .mobile-icon .icon-bar {
    background: $lighter !important;
    &:hover {
      background: $accent !important;
    }
  }
  .navbar .mobile-menu {
    background: $dark !important;
    li .dropdown a {
      color: $lighter !important;
      &:hover {
        color: $accent !important;
      }
    }
    li .main-nav-link {
      color: $accent !important;
    }
    ul .sign-up-container {
      background: $darker !important;
      li .sign-up {
        background: $dark !important;
      }
    }
  }

  // Announcements.
  .announcement-container {
    colors: $lighter, 0, $dark;
    box-shadow: inset 0 0 0 1px $darker;
    // &:hover { colors: 0, 0, $darker; }
  }

  // ? https://about.gitlab.com/releases
  .simple-header {
    z-index: 1;
    &::after {
      background-image: linear-gradient(60deg, $orange 0%, $violet 100%);
      background-color: $dark;
    }
    h1 { text-shadow: 2.5px 2.5px 0 $shadow; }
  }
  // Main content.
  .md-page {
    radius: $radius;
    colors: 0, 0, $dark;
    box-shadow: inset 0 0 0 1px $darker, 0 3px 3px 0 $shadow;
    li {
      colors: $lighter, 0, 0;
      a {
        colors: $accent, 0, 0;
        code { colors: $accent, 0, 0; }
      }
    }
    p.date { font-weight: bold; }
    .breadcrumb { colors: 0, 0, $darker; }
    .breadcrumb > li + li::before { colors: $lighter, 0, 0; }
  }

  // ? https://about.gitlab.com/press/press-kit
  .tile {
    radius: $radius;
    colors: 0, 0, $dark;
    transition: 0.2s ease;
    box-shadow: inset 0 0 0 1px $darker, 0 2px 4px 0 $shadow;
    &:hover { box-shadow: inset 0 0 0 1px $darker, 0 4px 8px 2px $shadow; }
    .list { colors: 0, $darker, 0; }
    .list > a { colors: 0, $darker, 0; }
  }
  // Article borders.
  .press-block { colors: 0, $darker, 0; }
  // Mini nav.
  .press-toc-links, .toc-links {
    a { colors: $light, 0, 0; }
    a:hover { colors: $accent, $t, 0; }
  }

  // ? https://about.gitlab.com/solutions/public-sector
  .summary-container, .characteristics-container {
    colors: 0, $darker, $dark;
  }
  .form-container {
    margin-bottom: -80px;
    colors: 0, 0, $dark;
  }

  // ? https://about.gitlab.com/pricing/
  .tabs-wrapper {
    colors: 0, $darker, 0;
    .pricing-tabs {
      li { colors: 0, $darker, $dark; }
      li.active { colors: 0, $darker, $darker; }
    }
  }
  // Plans.
  .plan-row .plan {
    colors: $lighter, $orange, $dark;
    box-shadow: inset 0 0 0 1px $darker;
    li > a { color: $accent; }
    .btn-pricing { colors: $accent, $accent, 0; }
    .plan-name, .all-features { colors: $light, $darker, 0; }
  }

  // QnA.
  .faq {
    margin-bottom: -80px;
    colors: 0, $darker, $dark;
    .faq-item {
      .faq-inner-item { colors: $lighter, $darker, 0; }
      &.is-open .faq-inner-item {
        colors: $light, $darker, 0;
        .faq-answer p { colors: 0, 0, $darker; }
      }
    }
  }

  // Sign up
  .mktoForm input[type="text"], .mktoForm input[type="url"], .mktoForm input[type="email"],.mktoForm input[type="tel"],
  .mktoForm input[type="number"], .mktoForm input[type="date"], .mktoForm select.mktoField, .mktoForm textarea.mktoField {
    colors: $light, $shadow, $dark;
  }
  .newsletter-form .mktoButton {
    colors: $light, $shadow, $dark;
    &:hover {
      colors: $accent, $shadow, $dark;
    }
  }
  .newsletter-form .mktoForm .mktoLogicalField label {
    color: $light !important;
  }

  // Front page
  .sdlc-container .sdlc-table {
    td:not(:last-child), td:not(:last-child), tr:not(:last-child) {
      border-color: $darker !important;
    }
    .competitor-logo-container {
      background-color: $darker !important;
    }
  }

  // Card
  .gke-layer .gke-layer-body, .gke-layer .k8s-layer-media,
  .testimonials-container .testimonials-row .testimonial, .forrester-graphic-title,
  .feature {
    colors: $light, $shadow, $darker;
    box-shadow: $box;
  }
  .u-subtle-bg {
    background-color: $dark;
  }
  .flex-tile-list > .tile {
    colors: $light, $darker, $darker;
  }
  .cta-btn.accent, .cta-btn.accent:hover {
    colors: $lighter, $shadow, $accent;
    box-shadow: $box;
  }
  .panel, .panel-info > .panel-heading {
    colors: $light, $darker, $darker;
  }
  .panel-success > .panel-heading {
    colors: $lighter, $green, $green;
    .panel-title {
      color: $lighter !important;
    }
  }
  .panel-warning > .panel-heading {
    colors: $lighter, $orange, $orange;
    .panel-title {
      color: $lighter !important;
    }
  }
  // Featured Posts cards
  .blog.list {
    .article-information {
      colors: $lighter, $shadow, $darker;
      a {
        color: $accent !important;
      }
    }
    .more, .browse {
      colors: $lighter, $dark, $dark;
      &:hover {
        background-color: $accent !important;
      }
    }
  }
  .blog .callout, .blog .gitlab-trial {
    colors: $lighter, $shadow, $darker;
  }

  // Search
  .search-box, .search-box-large input {
    colors: $lighter, $darker, $dark;
  }
  .search-input {
    color: $lighter !important;
  }
  .st-ui-injected-overlay-container {
    colors: $lighter, $darker, $dark;
    .st-ui-header, .st-ui-footer {
      colors: $lighter, $darker, $dark;
      input[type="text"] {
        color: $lighter !important;
      }
    }
  }
  a.st-ui-result {
    border-color: $darker !important;
    &:hover .st-ui-type-detail, &:hover .st-ui-type-detail-bold {
      color: $light !important;
    }
  }
  .st-install-Z4n7msKyctXXfJs66EKx .st-ui-result em {
    colors: $accent, $darker, $darker;
  }
  a.st-ui-close-button {
    filter: invert(1);
  }

  // Code
  .command, .alert-warning {
    colors: $accent, $shadow, $darker;
  }
  .panel-body pre {
    colors: $accent, $shadow, $dark;
  }
  .method p a:not(.btn) {
    color: $accent !important;
  }
  .distro-content .display-extra-links li {
    a {
      colors: $accent, $darler, 0;
      background: $t !important;
      text-shadow: none !important;
      text-decoration: none !important;
    }
    &:not(:last-child)::before {
      background-color: $darker !important;
    }
  }

  // Footer
  .footer {
    background-color: $darker !important;
    a:hover {
      color: $accent !important;
    }
  }
  .source-link {
    colors: $light, 0, $dark;
    .external-source-link {
      colors: $lighter, $lighter, 0;
      &:hover { colors: $accent, $t, 0; }
    }
  }

  // Logo
  if dark_logo {
    .wordmark {
      fill: $light !important;
    }

    path.logo-dark-orange-shape:nth-child(5), path.logo-light-orange-shape:nth-child(4),
    .footer-logo path.logo-dark-orange-shape:nth-child(4), .footer-logo path.logo-light-orange-shape:nth-child(3) {
      dark-logo: $t, 0, 0, 0, 0;
    }
    path.logo-light-orange-shape:nth-child(7),
    .footer-logo path.logo-svg-shape:nth-child(1) {
      dark-logo: 0, $t, 0, 0, 0;
    }
    path.logo-dark-orange-shape:nth-child(2), path.logo-svg-shape:nth-child(8),
    .footer-logo path.logo-dark-orange-shape:nth-child(7), .footer-logo path.logo-light-orange-shape:nth-child(6) {
      dark-logo: 0, 0, $t, 0, 0;
    }
    path.logo-orange-shape:nth-child(3),
    .footer-logo path.logo-orange-shape:nth-child(2) {
      dark-logo: 0, 0, 0, $t, 0;
    }
    path.logo-orange-shape:nth-child(6),
    .footer-logo path.logo-orange-shape:nth-child(5) {
      dark-logo: 0, 0, 0, 0, $t;
    }
  }
}

// * Code for forum on GitLab.
