  input[type="text"]:focus, input[type="password"]:focus, input[type="datetime"]:focus, input[type="datetime-local"]:focus,
  input[type="date"]:focus, input[type="month"]:focus, input[type="time"]:focus, input[type="week"]:focus, input[type="number"]:focus,
  input[type="email"]:focus, input[type="url"]:focus, input[type="search"]:focus, input[type="tel"]:focus, input[type="color"]:focus {
    box-shadow: none !important;
    border-color: $accent !important;
  }
  // Advanced search
  .search-container .search-advanced-sidebar .search-advanced-title,
  .search-container .search-advanced-sidebar .search-advanced-filters {
    background: $darker !important;
  }
   div.ac-wrap, .select-kit.combo-box .select-kit-header, .select-kit.multi-select .multi-select-header {
    colors: 0, $shadow, $dark
    &.is-focused { // TODO vednoc, tagged focused isn't styled
      box-shadow: none !important;
      border-color: $accent !important;
    }
  }
  // > dropdowns
  .select-kit.combo-box .select-kit-filter,
  .select-kit.combo-box .select-kit-row {
    colors: 0, $shadow, $dark;
  }
  .select-kit .select-kit-row.is-highlighted {
    background-color: $darker !important;
  }
  .badge-wrapper.bar span.badge-category,
  .select-kit.combo-box.category-chooser .select-kit-row .topic-count {
    color: $lighter !important;
  }


  // Topic filter
  .list-controls .combo-box .combo-box-header,
  .nav-pills > li > a {
    colors: $lighter, $shadow, $darker;
    &:hover {
      transform: translateY(-2px);
    }
  }
  .select-kit.dropdown-select-box.period-chooser .period-chooser-header h2.selected-name {
    .top-date-string {
      color: $lighter !important;
    }
    .date-section {
      color: $accent !important;
    }
  }
  // Filter dropdown by date
  .select-kit .select-kit-collection {
    background-color: $darker !important;
  }
  .select-kit .select-kit-row.is-selected.is-highlighted, .select-kit .select-kit-row.is-selected,
  .select-kit .select-kit-row:hover /*li hover*/ {
    background-color: $dark !important;
  }
  .select-kit.dropdown-select-box {
    .select-kit-body {
      border-color: $shadow !important;
      box-shadow: $box !important;
    }
    // Date
    &.period-chooser .period-chooser-row .date-section {
      color: $accent !important;
    }
    // Info
    &.period-chooser .period-chooser-row .top-date-string {
      color: $lighter !important;
    }
  }

  // Topic list
  .topic-list th {
    color: $lighter !important;
  }
  .topic-list {
    .topic-excerpt {
      color: $light !important;
    }
    .main-link a.title {
      color: $accent !important;
      &:visited {
        color: $lighter !important;
      }
    }
    .sortable:hover {
      background-color: $darker !important;
    }
    tr, tbody {
      border-color: $darker !important;
    }
    tr:hover > td {
      background-color: $darker !important;
    }
  }
  // Topic numbers
  .topic-list .num {
    color: $light !important;
  }
  // Heatmap for topic number
  .heatmap-high, .heatmap-high a {
    color: $red !important;
  }
  .heatmap-med, .heatmap-med a {
    color: $orange !important;
  }
  .heatmap-low, .heatmap-low a {
    color: $yellow !important;
  }
  // Last visit
  .topic-list .topic-list-item-separator td {
    border-color: $accent !important;
    span {
      colors: $accent, $shadow, $darker;
    }
  }
  // Tags
  .discourse-tag.simple, .discourse-tag.simple:visited, .discourse-tag.simple:hover {
    color: $lighter !important;
  }

  // Topic bodies
  #topic-title h1 a, .topic-link {
    color: $accent !important;
  }
  .topic-body, .topic-avatar, .topic-status-info {
    border-color: $darker !important;
  }
  .post-links-container {
    .post-links {
      border-color: $darker !important;
    }
    ul li a[href] {
      color: $light !important;
    }
  }
  // Topic animation
  .topic-body {
    animation: 0s !important;
    &.highlighted {
      animation: 0s !important;
    }
  }
  // Pinned topics
  .names span a {
    color: $lighter !important;
  }
  .small-action {
    border-color: $darker !important;
    .small-action-desc {
      color: $light !important;
    }
  }
  // Mods
  .moderator .clearfix > .topic-meta-data > .names span.user-title, .moderator .nav-pills > .topic-meta-data > .names span.user-title,
  .moderator .container > .topic-meta-data > .names span.user-title, .moderator .wrap > .topic-meta-data > .names span.user-title,
  /*post*/ .moderator .regular > .cooked {
    colors: $accent, 0, $darker;
  }
  .alert.alert-info {
    colors: $lighter, 0, $darker;
  }
  // @ mentions
  a.mention, a.mention-group {
    colors: $accent, 0, $darker;
  }
  // code
  .topic-post {
    p > code, li > code, pre > code, blockquote {
      colors: $lighter, $darker, $darker;
    }
  }
  .quote-button {
    colors: $lighter, $shadow, 0;
    background-color: black !important;
    box-shadow: $box !important;
    &:hover, &:hover > .fa {
      color: $accent !important;
    }
  }
  aside.quote .title {
    colors: $light, $accent, $darker;
  }

  // Badges
  // in topics
  .badge-notification {
    colors: $accent, 0, $darker;
  }
  // in badges page and user pages
  .badge-card {
    colors: 0, $shadow, $darker;
    .badge-contents .badge-info {
      background: $t !important;
      border: none !important;
      color: $lighter !important;
      h3 {
        color: $accent !important;
      }
    }
  }
  .badge-card .badge-contents .badge-icon {
     &.badge-type-gold .fa {
       color: $yellow !important;
     }
     &.badge-type-silver .fa {
       color: $light !important;
     }
     &.badge-type-bronze .fa {
       color: $orange !important;
     }
  }

  // Like and share buttons
  nav.post-controls {
    *:hover {
      background-color: transparent !important;
      & > .fa {
        color: $accent !important;
      }
    }
    // TODO vednoc: is this code wrong? on hover it should make the count blue
    .like-button {
      .like-count {
        color: $light !important;
      }
      &:hover {
        .like-count {
          color: $accent !important;
          &.d-hover {
            color: $accent !important;
          }
        }
      }
    }
  }
  #share-link {
    background: $darker;
    div > a:hover > .fa {
      color: $accent !important;
    }
  }
  // Reply
  nav.post-controls button.create {
    color: $light !important;
    &:hover {
      color: $accent !important;
    }
  }

  // Topic map
  .topic-map {
    colors: 0, $shadow, $darker;
    section {
      border-color: $shadow !important;
    }
    .buttons .btn, .link-summary .btn {
      colors: 0, $shadow, $darker;
    }
    .map .number, .map i, span.domain {
      color: $light !important;
    }
    td {
      background-color: $t !important;
    }

  }
  // Timeline
  .timeline-container .topic-timeline {
    .timeline-handle {
      background-color: $lighter !important;
    }
    .timeline-scrollarea {
      border-color: $lighter !important;
    }
    .timeline-replies {
      color: $lighter !important;
    }
    .timeline-ago, .start-date, .now-date {
      color: $light !important;
    }
  }
  .timeline-container.timeline-fullscreen,
  #topic-progress, #topic-progress .bg {
    colors: $lighter, $shadow, $darker;
    box-shadow: $box;
    .topic-timeline .username, .topic-timeline .title a {
      color: $accent !important;
    }
  }

  // New topic modal / comment modal
  .open .grippie {
    background: $accent !important;
  }
  #reply-control {
    colors: $light, $shadow, $darker;
    box-shadow: $box !important;
    .reply-to .composer-action-title .action-title,
    #draft-status, #file-uploading {
      color: $light !important;
    }
    .save-or-cancel .cancel {
      color: $lighter !important;
      &:hover {
        color: $red !important;
      }
    }
  }
  .select-kit.dropdown-select-box.composer-actions .select-kit-header {
    &:hover, &:focus {
      colors: $icon, $t, $dark;
    }
    .d-icon {
      border-color: $t;
    }
  }
  .select-kit.dropdown-select-box {
    .select-kit-body {
      colors: $light, $shadow, $darker;
      box-shadow: $box;
    }
    &.toolbar-popup-menu-options .select-kit-row {
      colors: $lighter, 0, $dark;
      &:hover .name {
        color: $accent !important;
      }
     }
    &.toolbar-popup-menu-options .select-kit-row .name, &.toolbar-popup-menu-options .select-kit-row .d-icon {
      color: $light !important;
    }
    .select-kit-row .texts .name {
      color: $accent !important;
    }
    .select-kit-row .texts .desc {
      color: $light !important;
    }
  }
  .composer-popup {
    colors: $lighter, $shadow, $dark;
  }

  // User pages
  .user-content {
    background-color: $dark !important;
  }
  .user-main .about {
    .details, &.collapsed-info .details {
      colors: 0, $shadow, $darker;
    }
    &.collapsed-info .controls ul a, & .controls a {
      colors: $light, 0, $darker;
    }
    .secondary {
      colors: 0, $shadow, $darker;
      dt {
        color: $light !important;
      }
      dd {
        color: $lighter !important;
      }
    }
  }
  .stats-section .value {
    color: $light !important;
  }
  .user-main .d-icon-heart {
    color: $red !important;
  }
  // Top borders
  .top-sub-section li {
    border-color: $darker !important;
  }
  // Other user names
  .user-info .user-detail .username a {
    color: $lighter !important;
  }
  .user-info .user-detail .name {
    color: $light !important;
  }
  // Top links
  .links-section .domain {
    color: $light !important;
  }
  // date
  .top-sub-section .topic-info {
    color: $light !important;
  }
  // User activity
  .user-stream .item, .user-stream .user-stream-item {
    colors: 0, $shadow, $darker;
  }
  .user-stream .time, .user-stream .delete-info {
    color: $light !important;
  }
  .user-navigation .nav-stacked a, .form-vertical .controls > .btn[href] {
    color: $lighter !important;
  }
  // Logged user settings
  .user-preferences {
    .static {
       color: $lighter !important;
    }
    .instructions {
      color: $light !important;
    }
  }

  // About
  .user-preferences .bio-composer, .group-form-bio, .edit-category-tab-topic-template {
    border-color: $shadow !important;
    & .d-editor-preview {
      background-color: $dark !important;
    }
    & textarea {
      color: $light !important;
    }
  }
  .d-editor {
    &-spacer {
      background-color: $shadow !important;
    }
    &-button-bar {
      border-color: $shadow !important;
    }
    &-textarea-wrapper {
      colors: $light, $shadow, $darker;
    }
    // insert link
    & .d-editor-modal {
      colors: $light, $shadow, $dark;
    }
    &-textarea-wrapper textarea {
      colors: $light, $shadow, $dark;
    }
  }
  // emoji
  .emoji-picker {
    colors: $light, $shadow, $darker;
    box-shadow: $box;
    .categories-column {
      colors: 0, $shadow, $darker;
    }
    .filter {
      border-color: $shadow !important;
      .clear-filter .fa, .d-icon-search .fa {
        color: $icon !important;
      }
      .clear-filter:hover .fa, .d-icon-search:hover {
        color: $accent !important;
      }
    }
    .section-header .title {
      color: $lighter !important;
    }
    .section-group button.emoji:hover, .results button.emoji:hover {
      background-color: $dark !important;
    }
    .footer {
      border-color: $shadow !important;
    }
  }
  // upload image
  .uploaded-image-preview {
    background-color: $darker !important;
  }
  .upload-selector .radios .inputs input[type="file"] {
    color: $light !important;
  }
  // modals
  .modal {
    &-inner-container {
      colors: $light, $shadow, $dark;
      box-shadow: $box !important;
    }
    .modal-body label {
      color: $lighter !important;
    }
  }

  // Notifications
  .user-menu .notifications li {
    colors: $light, $shadow, $darker;
    $:hover, &:focus {
      background-color: $dark !important;
    }
    span {
      color: $accent !important;
    }
  }
  div.menu-links-header a {
    color: $lighter;
    &:hover, &:focus {
      colors: $accent, 0, $dark;
      .fa {
        color: $accent !important;
      }
    }
  }

  // Login
  // done
  #simple-container {
    background-color: $darker;
  }
  .login-modal #login-buttons:not(.hidden), .create-account #login-buttons:not(.hidden) {
    border-color: $shadow !important;
  }
  button#login-link, button#new-account-link {
    color: $lighter !important;
  }
  #login-form a {
    color: $light !important;
  }

  // History
  .modal.history-modal {
    #revision-details, tbody {
      border-color: $shadow !important;
    }
    .diff-ins {
      colors: $lighter, 0, $green;
    }
  }
}

// * Code for docs page on GitLab.
// ? Might be split into a separate userstyle if it gets quite large.
// ! @RaitaroH takes the full responsibility for this section. (=

@-moz-document regexp("https?://docs.gitlab.com(/.*)") {
  // Navbar
  .landing .scrolling-header {
    box-shadow: $box;
  }

  // * Landing page.
  .landing {
    // Navigation.
    .header-link {
      text-decoration: none !important;
      &:hover p { colors: $accent, 0, 0; }
    }
    // Search container.
    .landing-header {
      colors: 0, 0, $dark;
      box-shadow: 0 1px 0 0 $darker, 0 2px 8px 0 $shadow;
      .input-container img { filter: invert(var(--bg-invert)); }
    }
    // Most popular topics.
    .topic h4 { colors: 0, $darker, 0; }
  }

  // * Navigation.
  .header {
    colors: 0, $darker, $darker;
    // Search bar.
    #search-form {
      colors: $lighter, 0, 0;
      // Found on docs/.../archives page.
      .docsearch { colors: $lighter, 0, $dark; }
      input {
        padding: 2px 11px 2px 28px !important;
        line-height: 30px;
        radius: $radius;
        colors: 0, $dark, 0;
      }
    }
    // Docs versions.
    .btn-versions { colors: $lighter, $t, $t; }
    .versions-tooltip { colors: $light, 0, $dark; }
    // Main navigation.
    .nav-item a { colors: $light, 0, 0; }
    .nav-item:hover a { colors: $accent, 0, 0; }
  }

  // Cards
  .main-topics li .wrapper h2 {
    border-color: $darker !important;
  }

  // Footer
  .footer {
    background-color: $darker;
    box-shadow: 0 -1px 0 0 $darker, 0 -2px 8px 0 $shadow;
    // Colorize icons.
    a:hover .fa { colors: $accent, 0, 0; }
    // "Created with" section.
    .source-link {
      colors: $lighter, $darker, $dark;
      a { colors: $accent, 0, 0; }
    }
  }

  // Logo
  if dark_logo {
    .landing .landing-header-top .logo, .header img {
      height: 0 !important;
      width: 0 !important;
      padding: 40px 0 0 40px !important;
      background-image: url("https://gitlab.com/vednoc/dark-gitlab/raw/master/images/docs_logo.png") !important;
      background-size: 40px 40px;
      background-repeat: no-repeat;
    }

    .footer-logo path.logo-dark-orange-shape:nth-child(4), .footer-logo path.logo-light-orange-shape:nth-child(3) {
      dark-logo: $t, 0, 0, 0, 0;
    }
    .footer-logo path.logo-svg-shape:nth-child(1) {
      dark-logo: 0, $t, 0, 0, 0;
    }
    .footer-logo path.logo-dark-orange-shape:nth-child(7), .footer-logo path.logo-light-orange-shape:nth-child(6) {
      dark-logo: 0, 0, $t, 0, 0;
    }
    .footer-logo path.logo-orange-shape:nth-child(2) {
      dark-logo: 0, 0, 0, $t, 0;
    }
    .footer-logo path.logo-orange-shape:nth-child(5) {
      dark-logo: 0, 0, 0, 0, $t;
    }
  }

  // * Main content.
  .main {
    border: none;
    h1 { colors: 0, $darker, 0; }
    img { box-shadow: none !important; }
    * > a { colors: $accent, 0, 0; }
    // Help and feedback section.
    .comments {
      margin: 0 auto -45px auto;
      radius: $radius;
      colors: 0, 0, $darker;
      .nav-tabs {
        border-color: $dark;
        margin: 0;
        li a { colors: $lighter, $dark, $t; }
        li.active a {
          border-bottom: none !important;
          colors: $light, $accent, $t;
        }
      }
      // Buttons.
      .comments-filter a.comments-holder {
        colors: 0, $dark, $dark;
        box-shadow: none !important;
      }
      .tab-content .row { border-bottom-color: $dark; }
    }
  }
  .anchor {
    filter: invert(1);
  }
  .badge-small:hover::after {
    colors: $accent, $shadow, $darker;
    box-shadow: $box !important;
  }

  // * Left sidebar.
  .global-nav {
    colors: 0, 0, $dark;
    a.active { colors: $lighter, $accent, $darker; }
    &-block-top a { color: $light; text-shadow: none; }
    &-link {
      color: $lighter;
      box-shadow: none !important;
      transition: all 0.15s ease-in-out;
      &:visited { colors: $lighter, 0, 0; }
      &:hover, &:focus { colors: $light, 0, $darker; }
    }
  }

  // * Right sidebar.
  .doc-nav {
    right: 8px;
    height: calc(100vh + 20px);
    colors: $lighter, 0, $darker;
    // Wrapper.
    #markdown-toc {
      margin: 0px;
      h4, li { padding-left: 8px; }
      a {
        text-decoration: none;
        colors: $lighter, 0, 0;
        &:hover { colors: $accent, 0, 0; }
      }
    }
  }

  // * Code blocks.
  pre.highlight {
    radius: $radius !important;
    box-shadow: 0 2px 4px -1px $shadow;
    // Roundify corners.
    code { radius: inherit; }
    // Copy to clipboard.
    .clip-btn {
      top: 1px; right: 1px;
      radius: 0 $radius 0 0;
      colors: $lighter, 0, $dark;
      .fa-clipboard::before { color: $lighter; }
    }
    // Syntax highlighting.
    code, .na, .c { colors: $lighter, 0, 0; }
    .n, .p, .nb { colors: $light, 0, 0; }
    .pi { colors: $red, 0, 0; }
    .no { colors: $violet, 0, 0; }
    .na { colors: $lighter, 0, 0; }
    .s1, .s2 { colors: $green, 0, 0; }
    .kp, .k { colors: $violet, 0, 0; }
    .nt { colors: $blue, 0, 0; }
    .se, .err { colors: $orange, 0, 0; }
    .o { colors: $cyan, 0, 0; }
  }
}

@-moz-document domain("status.gitlab.com") {
  // * Header.
  #statusio_header {
    margin: 0;
    display: flex;
    align-items: center;
    justify-content: space-between;
    // ? Make the header image smaller.
    #statuspage_header_image {
      float: left;
      width: 100%;
      max-width: 400px;
      &::before, &::after { clear: both; }
    }
  }

  // * Status bar.
  [style*="background:#C44031"] { colors: 0, 0, $red; }
  [style*="background:#27AE60"] { colors: 0, 0, $green; }
  [style*="background:#00AAF0"] { colors: 0, 0, $blue; }
  [style*="background:#FFA837"] { colors: 0, 0, $yellow; }

  // * About section.
  .section_border_1 {
    colors: 0, $darker, $dark;
    a { colors: $accent, 0, 0; }
    // Icons and text elements nested within them.
    .fa {
      span { colors: $light, 0, 0; }
      &-check { colors: $green, 0, 0; }
    }
  }

  // * Table.
  .status_td { colors: $lighter, $darker, $dark; }

  // * History timeline.
  #statusio_history .history-filter-text, .back-text { colors: $lighter, 0, 0; }
  #statusio_history_timeline .timelineContainer {
    colors: 0, $darker, 0;
    .incident_time { colors: $light, 0, 0; }
    .timelineMajor h2 {
      background-image: none !important;
      &::after {
        content: "";
        position: absolute;
        margin-top: 20px;
        left: 16px;
        width: 24px;
        height: 2px;
        colors: 0, 0, $darker;
      }
    }
    .timelineMinor dt {
      background: none;
      .open, .closed { colors: $lighter, 0, 0; }
      &::before {
        content: "";
        position: absolute;
        margin-top: 20px;
        left: 0px;
        width: 12px;
        height: 2px;
        colors: 0, 0, $darker;
      }
    }
    hr { colors: 0, 0, $darker; }
  }

  // * Labels.
  .label-success { colors: $light, 0, $green; }
  .label-info { colors: $light, 0, $blue; }

  // * Rounded corners.
  .make_round {
    radius: $radius;
    &.make_round_bottom_only { radius: 0 0 $radius $radius; }
    &.panel-heading { radius: $radius $radius 0 0; }
  }
  .component {
    &_first { radius: $radius $radius 0 0; }
    &_last { radius: 0 0 $radius $radius; }
  }

  // * Footer.
  #bottom_menu {
    a { colors: $accent, 0, 0; }
    strong a { text-decoration: underline; }
  }
}

@-moz-document domain("next.gitlab.com") {
  .white-box {
    radius: $radius;
    border: 1px solid;
    colors: $light, $darker, $dark;
    a { colors: $accent, 0, 0; }
    .text-switch { colors: $lighter, 0, 0; }
    .switch .cmn-toggle-round {
      + label { colors: 0, 0, $darker; }
      + label::after { colors: 0, 0, $darker; }
      + label::before { colors: 0, 0, $dark; }
      &:checked + label::before { colors: 0, 0, $blue; }
    }
  }
