  .header #search-form .docsearch {
    color: var(--lighter) !important;
    background-color: var(--dark) !important;
  }
  .header #search-form input {
    padding: 2px 11px 2px 28px !important;
    line-height: 30px;
    -webkit-border-radius: var(--radius);
    -moz-border-radius: var(--radius);
    border-radius: var(--radius);
    border-color: var(--dark) !important;
  }
  .header .btn-versions {
    color: var(--lighter) !important;
    border-color: var(--t) !important;
    background-color: var(--t) !important;
  }
  .header .versions-tooltip {
    color: var(--light) !important;
    background-color: var(--dark) !important;
  }
  .header .nav-item a {
    color: var(--light) !important;
  }
  .header .nav-item:hover a {
    color: var(--accent) !important;
  }
  .main-topics li .wrapper h2 {
    border-color: var(--darker) !important;
  }
  .footer {
    background-color: var(--darker);
    box-shadow: 0 -1px 0 0 var(--darker), 0 -2px 8px 0 var(--shadow);
  }
  .footer a:hover .fa {
    color: var(--accent) !important;
  }
  .footer .source-link {
    color: var(--lighter) !important;
    border-color: var(--darker) !important;
    background-color: var(--dark) !important;
  }
  .footer .source-link a {
    color: var(--accent) !important;
  }
  .landing .landing-header-top .logo,
  .header img {
    height: 0 !important;
    width: 0 !important;
    padding: 40px 0 0 40px !important;
    background-image: url("https://gitlab.com/vednoc/dark-gitlab/raw/master/images/docs_logo.png") !important;
    background-size: 40px 40px;
    background-repeat: no-repeat;
  }
  .footer-logo path.logo-dark-orange-shape:nth-child(4),
  .footer-logo path.logo-light-orange-shape:nth-child(3) {
    dark-logo: var(--t), 0, 0, 0, 0;
  }
  .footer-logo path.logo-svg-shape:nth-child(1) {
