  .main {
    border: none;
  }
  .main h1 {
    border-color: var(--darker) !important;
  }
  .main img {
    box-shadow: none !important;
  }
  .main * > a {
    color: var(--accent) !important;
  }
  .main .comments {
    margin: 0 auto -45px auto;
    -webkit-border-radius: var(--radius);
    -moz-border-radius: var(--radius);
    border-radius: var(--radius);
    background-color: var(--darker) !important;
  }
  .main .comments .nav-tabs {
    border-color: var(--dark);
    margin: 0;
  }
  .main .comments .nav-tabs li a {
    color: var(--lighter) !important;
    border-color: var(--dark) !important;
    background-color: var(--t) !important;
  }
  .main .comments .nav-tabs li.active a {
    border-bottom: none !important;
    color: var(--light) !important;
    border-color: var(--accent) !important;
    background-color: var(--t) !important;
  }
  .main .comments .comments-filter a.comments-holder {
    border-color: var(--dark) !important;
    background-color: var(--dark) !important;
    box-shadow: none !important;
  }
  .main .comments .tab-content .row {
    border-bottom-color: var(--dark);
  }
  .anchor {
    filter: invert(1);
  }
  .badge-small:hover::after {
    color: var(--accent) !important;
    border-color: var(--shadow) !important;
    background-color: var(--darker) !important;
    box-shadow: var(--box-shadow) !important;
  }
  .global-nav {
    background-color: var(--dark) !important;
  }
  .global-nav a.active {
    color: var(--lighter) !important;
    border-color: var(--accent) !important;
    background-color: var(--darker) !important;
  }
  .global-nav-block-top a {
    color: var(--light);
    text-shadow: none;
  }
  .global-nav-link {
    color: var(--lighter);
    box-shadow: none !important;
    transition: all 0.15s ease-in-out;
  }
  .global-nav-link:visited {
    color: var(--lighter) !important;
  }
  .global-nav-link:hover,
  .global-nav-link:focus {
    color: var(--light) !important;
    background-color: var(--darker) !important;
  }
  .doc-nav {
    right: 8px;
    height: calc(100vh + 20px);
    color: var(--lighter) !important;
    background-color: var(--darker) !important;
  }
  .doc-nav #markdown-toc {
    margin: 0px;
  }
  .doc-nav #markdown-toc h4,
  .doc-nav #markdown-toc li {
    padding-left: 8px;
  }
  .doc-nav #markdown-toc a {
    text-decoration: none;
    color: var(--lighter) !important;
  }
  .doc-nav #markdown-toc a:hover {
    color: var(--accent) !important;
  }
  pre.highlight {
    -webkit-border-radius: var(--radius) !important;
    -moz-border-radius: var(--radius) !important;
    border-radius: var(--radius) !important;
    box-shadow: 0 2px 4px -1px var(--shadow);
  }
  pre.highlight code {
    -webkit-border-radius: inherit;
    -moz-border-radius: inherit;
    border-radius: inherit;
  }
  pre.highlight .clip-btn {
    top: 1px;
    right: 1px;
    -webkit-border-radius: 0 var(--radius) 0 0;
    -moz-border-radius: 0 var(--radius) 0 0;
    border-radius: 0 var(--radius) 0 0;
    color: var(--lighter) !important;
    background-color: var(--dark) !important;
  }
  pre.highlight .clip-btn .fa-clipboard::before {
    color: var(--lighter);
  }
  pre.highlight code,
  pre.highlight .na,
  pre.highlight .c {
    color: var(--lighter) !important;
  }
  pre.highlight .n,
  pre.highlight .p,
  pre.highlight .nb {
    color: var(--light) !important;
  }
  pre.highlight .pi {
    color: var(--red) !important;
  }
  pre.highlight .no {
    color: var(--violet) !important;
  }
  pre.highlight .na {
    color: var(--lighter) !important;
  }
  pre.highlight .s1,
  pre.highlight .s2 {
    color: var(--green) !important;
  }
  pre.highlight .kp,
  pre.highlight .k {
    color: var(--violet) !important;
  }
  pre.highlight .nt {
    color: var(--blue) !important;
  }
  pre.highlight .se,
  pre.highlight .err {
    color: var(--orange) !important;
  }
  pre.highlight .o {
    color: var(--cyan) !important;
  }

#statusio_header {
    margin: 0;
    display: flex;
    align-items: center;
    justify-content: space-between;
  }
  #statusio_header #statuspage_header_image {
    float: left;
    width: 100%;
    max-width: 400px;
  }
  #statusio_header #statuspage_header_image::before,
  #statusio_header #statuspage_header_image::after {
    clear: both;
  }
  [style*="background:#C44031"] {
    background-color: var(--red) !important;
  }
  [style*="background:#27AE60"] {
    background-color: var(--green) !important;
  }
  [style*="background:#00AAF0"] {
    background-color: var(--blue) !important;
  }
  [style*="background:#FFA837"] {
    background-color: var(--yellow) !important;
  }
  .section_border_1 {
    border-color: var(--darker) !important;
    background-color: var(--dark) !important;
  }
  .section_border_1 a {
    color: var(--accent) !important;
  }
  .section_border_1 .fa span {
    color: var(--light) !important;
  }
  .section_border_1 .fa-check {
    color: var(--green) !important;
  }
  .status_td {
    color: var(--lighter) !important;
    border-color: var(--darker) !important;
    background-color: var(--dark) !important;
  }
  #statusio_history .history-filter-text,
  .back-text {
    color: var(--lighter) !important;
  }
  #statusio_history_timeline .timelineContainer {
    border-color: var(--darker) !important;
  }
  #statusio_history_timeline .timelineContainer .incident_time {
    color: var(--light) !important;
  }
  #statusio_history_timeline .timelineContainer .timelineMajor h2 {
    background-image: none !important;
  }
  #statusio_history_timeline .timelineContainer .timelineMajor h2::after {
    content: "";
    position: absolute;
    margin-top: 20px;
    left: 16px;
    width: 24px;
    height: 2px;
    background-color: var(--darker) !important;
  }
  #statusio_history_timeline .timelineContainer .timelineMinor dt {
    background: none;
  }
  #statusio_history_timeline .timelineContainer .timelineMinor dt .open,
  #statusio_history_timeline .timelineContainer .timelineMinor dt .closed {
    color: var(--lighter) !important;
  }
  #statusio_history_timeline .timelineContainer .timelineMinor dt::before {
    content: "";
    position: absolute;
    margin-top: 20px;
    left: 0px;
    width: 12px;
    height: 2px;
    background-color: var(--darker) !important;
  }
  #statusio_history_timeline .timelineContainer hr {
    background-color: var(--darker) !important;
  }
  .label-success {
    color: var(--light) !important;
    background-color: var(--green) !important;
  }
  .label-info {
    color: var(--light) !important;
    background-color: var(--blue) !important;
  }
  .make_round {
    -webkit-border-radius: var(--radius);
    -moz-border-radius: var(--radius);
    border-radius: var(--radius);
  }
  .make_round.make_round_bottom_only {
    -webkit-border-radius: 0 0 var(--radius) var(--radius);
    -moz-border-radius: 0 0 var(--radius) var(--radius);
    border-radius: 0 0 var(--radius) var(--radius);
  }
  .make_round.panel-heading {
    -webkit-border-radius: var(--radius) var(--radius) 0 0;
    -moz-border-radius: var(--radius) var(--radius) 0 0;
    border-radius: var(--radius) var(--radius) 0 0;
  }
  .component_first {
    -webkit-border-radius: var(--radius) var(--radius) 0 0;
    -moz-border-radius: var(--radius) var(--radius) 0 0;
    border-radius: var(--radius) var(--radius) 0 0;
  }
  .component_last {
    -webkit-border-radius: 0 0 var(--radius) var(--radius);
    -moz-border-radius: 0 0 var(--radius) var(--radius);
    border-radius: 0 0 var(--radius) var(--radius);
  }
  #bottom_menu a {
    color: var(--accent) !important;
  }
  #bottom_menu strong a {
    text-decoration: underline;
  }

.white-box {
    -webkit-border-radius: var(--radius);
    -moz-border-radius: var(--radius);
    border-radius: var(--radius);
    border: 1px solid;
    color: var(--light) !important;
    border-color: var(--darker) !important;
    background-color: var(--dark) !important;
  }
  .white-box a {
    color: var(--accent) !important;
  }
  .white-box .text-switch {
    color: var(--lighter) !important;
  }
  .white-box .switch .cmn-toggle-round + label {
    background-color: var(--darker) !important;
  }
  .white-box .switch .cmn-toggle-round + label::after {
    background-color: var(--darker) !important;
  }
  .white-box .switch .cmn-toggle-round + label::before {
    background-color: var(--dark) !important;
  }
  .white-box .switch .cmn-toggle-round:checked + label::before {
    background-color: var(--blue) !important;
  }
  .footer a {
    color: var(--light) !important;
  }

