/* ==UserStyle==
@name         Spotify Gruvbox
@namespace    gruvbox.spotify.com
@version      1.0.0
@description  Gruvbox theme for Spotify player.
@author       Luiyi
@homepageURL  http://gruvbox.spotify.com
@supportURL   http://gruvbox.spotify.com
@updateURL    http://gruvbox.spotify.com
@preprocessor stylus

@var text     blur  "Custom blur" 8px
@var text     opacity  "Custom opacity" 1
@var text     font "Custom font" "CaviarDreams"
@var checkbox p_header  "Center project header elements" 0
@var checkbox dark_logo "Switch to our darker logo" 0
@var checkbox navbar_c  "Theme navbar color" 1
@var checkbox checkbox  "Checkbox inversion" 1
@var text     bg_image  "Background (url/base64)" "url(https://bing.biturl.top/?format=image&mkt=en-US)"
@var text     bg_position "Background position" center
@var text     bg_repeat "Background repeat" no-repeat
@var text     bg_attachment    "Background attachment" fixed

@var color dark    "Dark color."    #3c3836
@var color darker  "Darker color."  #32302f
@var color light   "Light color."   #ebdbb2
@var color lighter "Lighter color." #fbf1c7
@var color accent  "Accent color."  #d65d0e
@var color yellow  "Yellow color."  #fabd2f
@var color orange  "Orange color."  #fe8019
@var color red     "Red color."     #fb4934
@var color purple  "Purple color."  #d3869b
@var color violet  "Violet color."  #6c71c4
@var color blue    "Blue color."    #83a598
@var color cyan    "Cyan color."    #8ec07c
@var color green   "Green color."   #b8bb26
@var color shadow  "Shadow"         rgba(0, 0, 0, .25)

@var color base00 "Default Background" #32302f 
@var color base01 "Lighter Background (Used for status bars)" #3c3836 
@var color base02 "Selection Background color." #504945 
@var color base03 "Comments, Invisibles, Line Highlighting" #665c54 
@var color base04 "Dark Foreground (Used for status bars)" #bdae93 
@var color base05 "Default Foreground, Caret, Delimiters, Operators" #d5c4a1 
@var color base06 "Light Foreground (Not often used)" #ebdbb2 
@var color base07 "Light Background (Not often used)" #fbf1c7 
@var color base08 "Variables, XML Tags, Markup Link Text, Markup Lists, Diff Deleted" #fb4934 
@var color base09 "Integers, Boolean, Constants, XML Attributes, Markup Link Url" #fe8019 
@var color base0A "Classes, Markup Bold, Search Text Background" #fabd2f 
@var color base0B "Strings, Inherited Class, Markup Code, Diff Inserted" #b8bb26 
@var color base0C "Support, Regular Expressions, Escape Characters, Markup Quotes" #8ec07c 
@var color base0D "Functions, Methods, Attribute IDs, Headings" #83a598 
@var color base0E "Keywords, Storage, Selector, Markup Italic, Diff Changed" #d3869b 
@var color base0F "Deprecated, Opening/Closing Embedded Language Tags, e.g. <?php ?>" #d65d0e 
==/UserStyle== */
@-moz-document domain("open.spotify.com") {
	:root {
		--font: font;
		--bg-image: bg_image;
		--bg-position: bg_position;
		--bg-repeat: bg_repeat;
		--bg-attachment: bg_attachment;

		--blur: blur;
		--opacity: opacity;
		--dark: dark;
		--darker: darker;
		--light: light;
		--lighter: lighter;
		--accent: accent;
		--icon: icon;
		--shadow: shadow;
		--box-shadow: 0 2px 3px 0 var(--shadow);
		--width: max_width;
		--radius: b_radius;
		--avatar: a_radius;
		--code-font: code_font;
		--base00: base00;
		--base01: base01;
		--base02: base02;
		--base03: base03;
		--base04: base04;
		--base05: base05;
		--base06: base06;
		--base07: base07;
		--base08: base08;
		--base09: base09;
		--base0A: base0A;
		--base0B: base0B;
		--base0C: base0C;
		--base0D: base0D;
		--base0E: base0E;
		--base0F: base0F;
	}
	@font-face {
		font-family: CaviarDreams;
		src: url(https://cyberlapse.github.io/SpotifyStylishExtension/CaviarDreams.woff);
	}
	body {
		font-family: var(--font);
		color: var(--base05);
	}
	.Root__top-container {
		background: var(--bg-image) var(--bg-position) var(--bg-repeat) var(--bg-attachment);
	}
	.Root__nav-bar:before {
		content: "";
		background: inherit;
		box-shadow: inset 0 0 0 10em rgba(0, 0, 0, .5);
		filter: blur(var(--blur)) grayscale(50%);
		position: absolute;
		top: -1em;
		bottom: -1em;
		left: -1em;
		right: -1em;
	}
	.Root__nav-bar {
		background: inherit;
		overflow: hidden;
		box-shadow: 0 0 1em 0 #000;
	}
	.RecentlyPlayedWidget__type,
	.track-name-wrapper .tracklist-row__album-name-link, .track-name-wrapper .tracklist-row__artist-name-link {
		color: var(--base04);
	}
	.Root__now-playing-bar {
		background: linear-gradient(to bottom, var(--base01) 0, var(--base00) 100%);
		box-shadow: 0 0 1em 0 #000;
		z-index: 1;
	}
	header.artist-header:before {
		background: none;
	}
	header.artist-header {
	}
	header.artist-header > span {
		text-shadow: -2px -2px 2px rgba(0, 0, 0, .75), 0 -2px 2px rgba(0, 0, 0, .75), 2px -2px 2px rgba(0, 0, 0, .75), -2px 2px 2px rgba(0, 0, 0, .75), 0 2px 2px rgba(0, 0, 0, .75), 2px 2px 2px rgba(0, 0, 0, .75);
	}
	header.artist-header > h1 {
		text-shadow: 0 0.5px 0 #bababa, 0 1px 0 #b4b4b4, 0 1.5px 0 #aeaeae, 0 2px 0 #a8a8a8, 0 2.5px 0 #a2a2a2, 0 3px 0 #9c9c9c, 0 3.5px 0 #969696, 0 4px 0 #909090, 0 1px 3px rgba(0, 0, 0, 0.333), 0 3px 5px rgba(0, 0, 0, 0.333), 0 5px 10px rgba(0, 0, 0, 0.333), 0 10px 10px rgba(0, 0, 0, 0.333), 0 20px 20px rgba(0, 0, 0, 0.333);
	}
	.artist-appears-on, .artist-music, .artist-singles, .now-playing-bar-container {
		background-color: transparent;
		border: none;
		/*box-shadow: 0 0 1em 0 #000;*/
	}
	._3v-H5s52KoSlx1rvzcnTvM,
	.media-object,
	.TrackListHeader__body,
	.tracklist-container,
	.Search__content--empty {
		backdrop-filter: blur(var(--blur)) grayscale(50%);
		background: rgba(0, 0, 0, .5);
		box-shadow: 0 0 .5em 0 #000;
	}
	.TrackListHeader__body {
		margin: 0 1em;
		padding: 1em;
	}
	.cover-art.shadow {
		box-shadow: none;
	}
	.media-object {
		margin-bottom: 2.5em;
		padding-bottom: 0;
	}
	.mo-info,
	.mo-meta {
		padding-bottom: .25em;
	}
	a,
	.btn,
	.link-subtle,
	html:not([lang=ar]) .navBar-group-header,
	.tracklist-row .position,
	.tracklist-name,
	.control-button {
		color: var(--base05);
	}
	.control-button--circled:after {
		border: 1px solid var(--base05);
	}
	.progress-bar__fg {
		background-color: var(--base05);
	}
	.navBar-link--active:after,
	.EYVkZXmTImKH4iH48N7Uj._1-aXXBO8bQU1WTFY-7B0m-:before,
	.EYVkZXmTImKH4iH48N7Uj._1-aXXBO8bQU1WTFY-7B0m-:hover:before,
	.RecentlyPlayedWidget__link--active:after,
	.progress-bar--is-active .progress-bar__fg,
	.ConnectBar,
	.btn.btn-green {
		background-color: var(--base0F);
	}
	.ConnectBar:after {
		border-bottom: 10px solid var(--base0F);
	}
	.btn.btn-green:hover {
		background-color: var(--base05);
		color: var(--base0F);
	}
	.link-subtle:focus, .link-subtle:hover,
	.tracklist-row--active .position,
	.tracklist-row--active .tracklist-duration,
	.tracklist-row--active .tracklist-name,
	.tracklist-row--active .tracklist-play-pause {
		color: var(--base0F) !important;
	}
	.SearchInputBox__input {
		caret-color: var(--base0F);
	}
}

